INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(1, 2, 'Call of duty: World At War', 'call-of-duty:-world-at-war', '<h3>Sau th&agrave;nh c&ocirc;ng qu&aacute; lớn của phi&ecirc;n bản Modern Warfare đầu ti&ecirc;n cũng như sự thắng thế của bối cảnh chiến tranh hiện đại, kh&ocirc;ng ai nghĩ chiến tranh thế giới thứ 2 sẽ l&agrave; yếu tố tạo n&ecirc;n th&agrave;nh c&ocirc;ng cho Call Of Duty: World At War nữa.</h3>', '<p><span style=\"font-size:14px\">Sau th&agrave;nh c&ocirc;ng qu&aacute; lớn của phi&ecirc;n bản Modern Warfare đầu ti&ecirc;n cũng như sự thắng thế của bối cảnh chiến tranh hiện đại, kh&ocirc;ng ai nghĩ chiến tranh thế giới thứ 2 sẽ l&agrave; yếu tố tạo n&ecirc;n th&agrave;nh c&ocirc;ng cho Call Of Duty: World At War nữa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy nhi&ecirc;n Treyarch đ&atilde; chứng minh rằng m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể l&agrave;m c&oacute; thể l&agrave;m được những việc tưởng chứng như kh&ocirc;ng thể. D&ugrave; kh&ocirc;ng sở hữu một cốt truyện hấp dẫn như Modern Warfare nhưng World At War cũng trang bị cho m&igrave;nh một d&agrave;n diễn vi&ecirc;n lồng tiếng gồm to&agrave;n những t&ecirc;n tuổi lớn như Kiefer Sutherland v&agrave; Gary Oldman, bối cảnh chiến tranh được chuyển dịch về khu vực Th&aacute;i B&igrave;nh Dương đầy lạ lẫm v&agrave; nhiều bất ngờ cộng với việc đẩy mạnh yếu tố h&agrave;nh động bạo lực l&ecirc;n cao chưa từng thấy trong Call Of Duty.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Call of Duty World At War vẫn tạo dựng được một chỗ đứng nhất định trong l&ograve;ng người h&acirc;m mộ, đồng thời khẳng định cho khả năng của h&atilde;ng Treyarch. Nhưng đ&oacute; chưa phải l&agrave; tất cả v&igrave; chế độ chơi mạng mới l&agrave; linh hồn thực sự của tựa game n&agrave;y. Game lần đầu ti&ecirc;n giới thiệu đến người chơi chế độ chơi Zombie v&ocirc; c&ugrave;ng hấp dẫn m&agrave; sau n&agrave;y đ&atilde; được Treyarch &aacute;p dụng th&agrave;nh c&ocirc;ng v&agrave;o c&aacute;c phi&ecirc;n bản Call Of Duty tiếp theo của m&igrave;nh.</span></p>', '<p>CPU: Intel Core i3 3225 3.3 GHz or AMD Ryzen 5 1400</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 8 GB</p>\r\n\r\n<p>OS: Windows 7 64-Bit or later</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 660 @ 2 GB / GTX 1050 or ATI Radeon HD 7850 @ 2GB / AMD RX 550</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 90 GB</p>', '<p>CPU: Intel Core i3 3225 3.3 GHz or AMD Ryzen 5 1400</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 8 GB</p>\r\n\r\n<p>OS: Windows 7 64-Bit or later</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 660 @ 2 GB / GTX 1050 or ATI Radeon HD 7850 @ 2GB / AMD RX 550</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 90 GB</p>', 'Cài đặt và chơi', '1520190408Wallpaper_call_of_duty_5_world_at_war.jpg', 'https://www.youtube.com/embed/Y_Ip_SaJqpg', 2008, '2018-03-08'),
(2, 6, 'Grand Theft Auto V', 'grand-theft-auto-v', '<h3 style=\"text-align:justify\">Grand Theft Auto V l&agrave; tr&ograve; chơi video được ph&aacute;t triển bởi Rockstar North thuộc h&atilde;ng Rockstar Games. L&agrave; phi&ecirc;n bản thứ mười lăm trong D&ograve;ng tr&ograve; chơi Grand Theft Auto v&agrave; l&agrave; phần kế tiếp của Grand Theft Auto IV.</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Game ra từ 17 th&aacute;ng 9 năm 2013 vậy m&agrave; tới b&acirc;y giờ mới rớ v&ocirc; được. L&uacute;c đ&oacute; cũng muốn chơi lắm m&agrave; nh&igrave;n c&aacute;i dung lượng l&agrave; ng&aacute;n tới cổ lu&ocirc;n &ndash; 60Gb th&ocirc;i nghỉ khỏe, với lại m&aacute;y cũng kh&ocirc;ng k&eacute;o nổi, hồi đ&oacute; chơi GTA IV th&ocirc;i m&agrave; n&oacute; như l&ograve; nướng b&aacute;nh m&igrave; rồi. Sau mấy năm r&ograve;ng x&agrave;i k&eacute; được c&aacute;i m&aacute;y, mua một con Asus GTX 950 về gắn phải n&oacute;i l&agrave; mừng hết lớn, c&aacute;i cảm gi&aacute;c cầm c&aacute;i card m&agrave;n h&igrave;nh mới c&oacute;n c&ograve;n thơm &ldquo;m&ugrave;i linh kiện m&aacute;y t&iacute;nh&rdquo;&nbsp;n&oacute; ph&ecirc; như con t&ecirc; t&ecirc;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trước giờ cũng ghiền series GTA lắm, chơi từ bản San&nbsp;Andreas &ndash;&nbsp;Vice City &ndash; IV rồi giờ qua tới V. Bản San Andreas phải n&oacute;i l&agrave; rất hay, tại n&oacute; mi&ecirc;u tả đ&uacute;ng được bối cảnh của một khu ổ chuột, trong đ&oacute; tệ nạn cũng nhiều, m&agrave; phần hay nhất l&agrave; c&aacute;c chỉ số nh&acirc;n vật từ sức mạnh &ndash; thể lực. Qua tới Vice City cũng hay nhưng n&oacute;i về mafia bắn giết c&aacute;c thứ chứ kh&ocirc;ng g&oacute;i gọn như c&aacute;c băng đảng b&ecirc;n San Andreas. Qua phần IV th&igrave; đẹp hơn nhiều, cốt truyện cũng hay nữa, nh&acirc;n vật thiết kế c&oacute; chiều s&acirc;u v&agrave; t&iacute;nh c&aacute;ch. Tới phần V th&igrave; m&aacute; ơi đẹp kinh dị chưa chơi được 1/5 game nữa nhưng m&agrave; thấy m&ecirc; với c&aacute;i đồ họa rồi.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Mở m&agrave;n như Hollywood</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621175947_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621175947_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&aacute;c với mấy phần trước thay v&igrave; nhiệm vụ đầu ti&ecirc;n l&agrave; l&aacute;i xe đi đ&oacute;n ai đ&oacute; hoặc chạy tới nơi n&agrave;o đ&oacute;, GTA V vừa v&ocirc; l&agrave; đ&atilde; cho bạn cướp ng&acirc;n h&agrave;ng liền. Mở m&agrave;n bằng việc la h&eacute;t nh&acirc;n vi&ecirc;n ng&acirc;n h&agrave;ng, xong rồi chỉa s&uacute;ng v&ocirc; mặt để l&ugrave;a hết đ&aacute;m nh&acirc;n vi&ecirc;n v&ocirc; ph&ograve;ng kh&aacute;c, kế đến l&agrave; k&iacute;ch nổ cửa hầm chứa tiền. Vơ v&eacute;t xong hết ra đụng cảnh s&aacute;t đứng chờ, l&uacute;c n&agrave;y cầm s&uacute;ng l&ecirc;n bắn m&agrave; phải headshot kh&ocirc;ng mới chịu nha, chơi mấy game FPS kia quen rồi giờ v&ocirc; GTA bắn thấy như l&ecirc;n đồng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180339_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180339_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau m&agrave;n bắn s&uacute;ng dạo đầu l&agrave; m&agrave;n chạy cong đu&ocirc;i để tho&aacute;t cảnh s&aacute;t. L&uacute;c n&agrave;y bối cảnh đang c&oacute; tuyết, sương m&ugrave; n&agrave;y nọ nhưng vẫn c&oacute; một ch&uacute;t nắng, nh&igrave;n kh&ocirc;ng c&oacute; g&igrave; gọi l&agrave; qu&aacute; rực rỡ&nbsp;nhưng c&aacute;i mấy c&aacute;i chi tiết th&igrave; rất đ&atilde;:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chẳng hạn như l&uacute;c chạy trốn cảnh s&aacute;t từ ng&acirc;n h&agrave;ng, mới v&ocirc; l&aacute;i xe tr&ecirc;n GTA t&igrave;m lại cảm gi&aacute;c n&ecirc;n hơi lạng l&aacute;ch ch&uacute;t, v&ocirc; t&igrave;nh trật khỏi đường ch&iacute;nh, b&aacute;nh xe sau l&uacute;c lấn ra ngo&agrave;i chỗ &iacute;t tuyết th&igrave; bắn đất c&aacute;t ra ph&iacute;a sau. Đang chạy trốn cảnh s&aacute;t m&agrave; to&agrave;n nh&igrave;n cảnh vật, chỗ n&agrave;y phải n&oacute;i l&agrave; rất đẹp m&agrave; tại c&oacute; tuyết che phủ n&ecirc;n kh&ocirc;ng thấy được g&igrave; nhiều, chứ &iacute;t c&acirc;y cối nh&agrave; cửa, nh&igrave;n được cả một v&ugrave;ng ch&acirc;n trời c&oacute; mấy đ&aacute;m m&acirc;y nhỏ, nắng kh&ocirc;ng nhiều lắm nhưng đủ chiếu s&aacute;ng khung cảnh&nbsp;nh&igrave;n rất lung thị linh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180610_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180610_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuyết lạnh trắng x&oacute;a đồ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Bởi lo nh&igrave;n trời m&acirc;y m&agrave; tới chỗ cua qu&ecirc;n quẹo, vậy l&agrave; t&ocirc;ng thẳng v&ocirc; d&agrave;n xe chặn đường của cảnh s&aacute;t ở ph&iacute;a trước, đ&agrave;nh phải chạy lại kh&uacute;c n&agrave;y. Tới cảnh gặp đường c&ugrave;ng th&igrave; sau khi xuy&ecirc;n t&aacute;o được hết cảnh s&aacute;t, nh&acirc;n vật chạy thẳng v&agrave;o cơn b&atilde;o tuyết đang nổi l&ecirc;n, kh&uacute;c n&agrave;y y chang cảnh trong phim m&agrave; theo kiểu kết th&uacute;c mở.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau cảnh đ&oacute; l&agrave; đ&aacute;m tang giả của Michael Townley, l&uacute;c n&agrave;y d&ograve;ng chữ Rockstar Games presents hiện l&ecirc;n l&agrave; thấy rần rần trong người, cứ như đang coi phim vậy. M&agrave;n h&igrave;nh tối lại rồi chữ Grand Theft Auto&nbsp;V hiện l&ecirc;n, trong khi m&igrave;nh vẫn c&ograve;n gật g&ugrave; khen thầm c&aacute;i mở m&agrave;n của game qu&aacute; b&aacute; ch&aacute;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180813_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180813_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tưởng l&agrave; giới thiệu xong rồi n&ecirc;n mới tới cảnh Michael ngồi trị liệu t&acirc;m l&yacute; với &ocirc;ng b&aacute;c sĩ trong một căn hộ kh&aacute; sang cảnh ven bờ biển, đ&uacute;ng l&agrave; game đ&uacute;ng chất Rockstar, c&aacute;c c&acirc;u thoại tuy hơi th&ocirc; nhưng m&agrave; rất thật v&igrave; n&oacute; gắn đ&uacute;ng với t&iacute;nh c&aacute;ch nh&acirc;n vật. C&aacute;c c&acirc;u chửi thề như l&uacute;c Michael n&oacute;i về thằng con trai tối ng&agrave;y quay tay &ndash; h&uacute;t cần &ndash; chơi game kh&ocirc;ng nghề ngỗng sẽ l&agrave;m bạn ph&igrave; cười v&igrave; li&ecirc;n tục đệm &ldquo;f*cking&rdquo; xối xả v&agrave;o mặt &ocirc;ng b&aacute;c sĩ t&acirc;m l&yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kết th&uacute;c buổi trị liệu t&acirc;m l&yacute; Michael bước ra khỏi căn nh&agrave;, nhạc nổi l&ecirc;n v&agrave; GTA V bắt đầu hớp hồn người chơi bằng những g&oacute;c camera độc đ&aacute;o, ph&ocirc; diễn th&agrave;nh phố sống động &nbsp;v&agrave; đẹp v&ocirc; c&ugrave;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180948_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180948_1-1024x576.jpg\" style=\"height:540px; width:960px\" />&nbsp;<img alt=\"20160621181002_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621181002_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621181005_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621181005_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">L&uacute;c n&agrave;y&nbsp;vừa chiếu khung cảnh trong game vừa hiện chữ giới thiệu nh&agrave; sản xuất v&agrave; nhiều người nữa, nh&igrave;n cứ như một bộ phim lấy chủ đề b&atilde;i biển Miami vậy, đ&uacute;ng l&agrave; c&oacute; phần mở m&agrave;n th&ocirc;i m&agrave; game hớp hồn người chơi qu&aacute; chừng,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau phần thể hiện cảnh đẹp của game th&igrave; gặp được Franklin v&agrave; Lamar đang t&aacute;m chuyện dọc đường, tới một ng&ocirc;i nh&agrave; gần chỗ b&aacute;c sĩ t&acirc;m l&yacute; của Michael. Tới đ&acirc;y từ cuộc n&oacute;i chuyện, game chuyển cảnh theo một c&aacute;ch v&ocirc; c&ugrave;ng mượt đến nỗi phải mất tới 5 gi&acirc;y khi thấy Franklin đứng y&ecirc;n th&igrave; mới biết đ&atilde; v&ocirc; game rồi, l&uacute;c n&agrave;y như thường lệ &ndash; một nh&acirc;n vật da đen ch&ocirc;m xe như GTA San Andreas, bạn sẽ chọn một trong 2 chiếc xe để c&ugrave;ng Lamar l&aacute;i tới tiệm b&aacute;n xe nơi m&agrave; cả 2 đang l&agrave;m việc.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Vẻ đẹp kh&oacute; cưỡng</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616172629_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616172629_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trong th&agrave;nh phố, nh&agrave; cửa, xe cộ rất sống động. Th&iacute;ch nhất l&agrave; c&aacute;i nắng trong GTA V, rạng ngời m&agrave; kh&ocirc;ng ch&oacute;i l&oacute;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616170930_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616170930_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">C&aacute;i nắng sớm th&igrave; dịu nhẹ, c&aacute;i nắng giữa trưa th&igrave; gắt hơn, chiều trở đi th&igrave; bắt đầu dịu dần, tới gần ho&agrave;ng h&ocirc;n l&agrave; đẹp nhất. Nhiều l&uacute;c chạy giữa đường phải đứng lại lựa những g&oacute;c đẹp để m&agrave; ngắm mặc d&ugrave; trong th&agrave;nh phố th&ocirc;i m&agrave; nh&igrave;n rất thật tiếc l&agrave; m&aacute;y kh&ocirc;ng k&eacute;o nổi l&ecirc;n Very High. Đ&oacute; giờ hiếm c&oacute; game n&agrave;o m&agrave; ngắm cảnh nhiều như vậy, v&igrave; th&agrave;nh phố trong phần n&agrave;y nh&igrave;n vui tươi hơn hẳn (cũng do chỉnh cường độ d&acirc;n số cao), xe cộ đa dạng v&agrave; tấp nập đ&uacute;ng nơi, như trung t&acirc;m th&agrave;nh phố th&igrave; l&uacute;c n&agrave;o cũng đ&ocirc;ng xe nhất l&agrave; s&aacute;ng tới trưa, chiều tối th&igrave; vắng hơn một ch&uacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616161949_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616161949_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chơi tới l&uacute;c Trevor nghe tin Michael c&ograve;n sống, vậy l&agrave; sau khi xử băng đảng biker l&agrave; lật đật&nbsp;tức tốc chạy l&ecirc;n th&agrave;nh phố liền. Đường rất d&agrave;i m&agrave; l&uacute;c khởi h&agrave;nh th&igrave; đang mưa nữa, ngay cả mặt đường l&uacute;c l&uacute;c mưa cũng giống thật v&igrave; đang ở ngoại &ocirc; n&ecirc;n đường xấu hơn nhiều, chỗ bằng chỗ l&otilde;m n&ecirc;n l&uacute;c mưa th&igrave; chỗ đọng nước chỗ th&igrave; kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616165621_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616165621_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">L&aacute;i xe từ l&uacute;c mưa cho tới tạnh, đang l&aacute;i giữa chừng th&igrave; nhiệm vụ k&ecirc;u dừng lại giữa đường, tại đi ban đ&ecirc;m n&ecirc;n l&uacute;c đ&oacute; gần s&aacute;ng, nhiệm vụ k&ecirc;u Trevor lại chỗ sườn n&uacute;i đứng ng&oacute; xuống th&agrave;nh phố rồi lầm bẩm chửi Michael, c&ocirc;ng nhận Rockstar rất hay&nbsp;trong chuyện&nbsp;tạo những cảnh &ldquo;đi s&acirc;u v&agrave;o l&ograve;ng người&rdquo; như vầy. Chỗ sườn n&uacute;i đ&oacute; nh&igrave;n xuống th&agrave;nh phố ban đ&ecirc;m, mặt trời sắp l&ecirc;n nhưng đ&egrave;n đường v&agrave; đ&egrave;n nh&agrave; c&ograve;n mở, vẫn thấy xe chạy tr&ecirc;n đường mặc d&ugrave; rất xa, đứng đ&oacute; phải mất khoảng v&agrave;i ph&uacute;t để ngắm cảnh m&agrave; ước l&agrave; m&igrave;nh nhảy v&ocirc; m&agrave;n h&igrave;nh để ngắm tiếp.&nbsp;L&ecirc;n xe l&aacute;i l&ecirc;n th&agrave;nh phố tiếp, trời cũng bắt đầu rạng s&aacute;ng, l&uacute;c l&aacute;i tới gần th&agrave;nh phố nh&agrave; c&agrave;ng l&uacute;c c&agrave;ng nhiều l&agrave; l&uacute;c b&igrave;nh minh l&ecirc;n, nắng xuy&ecirc;n qua mấy khe hở của những ng&ocirc;i nh&agrave; ổ chuột rồi những t&ograve;a cao tầng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Phải n&oacute;i l&agrave; giống như đi phượt vậy đ&oacute;,&nbsp;b&igrave;nh thường mấy phần trước to&agrave;n l&aacute;i xe chạy như đi&ecirc;n tới chỗ nhận nhiệm vụ m&agrave; tới phần n&agrave;y phải chạy từ từ để ngắm cảnh, nhiều l&uacute;c c&ograve;n chạy giống nhu NPC vậy &ndash; c&agrave; rề rề để chụp h&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sắp tới c&ograve;n c&oacute; vụ lặn xuống nước để l&agrave;m nhiệm vụ nữa, m&agrave; tại chưa chơi tới, l&uacute;c trước cũng c&oacute; lặn một lần m&agrave; c&ograve;n giới hạn vụ oxy đồ n&ecirc;n kh&ocirc;ng ở dưới l&acirc;u để kh&aacute;m ph&aacute; được, để từ từ l&agrave;m nhiệm vụ ch&ocirc;m t&agrave;u ngầm rồi kh&aacute;m ph&aacute; tiếp hehe.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Gameplay ấn tượng</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&ocirc;ng những mang quy m&ocirc; lớn như cướp ng&acirc;n h&agrave;ng chẳng hạn m&agrave; nhiệm vụ trong phần V c&ograve;n th&uacute; vị hơn nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.gtanet.com/gta-5/images/characters/chop.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&ocirc;ng quay đi quay lại mấy nhiệm vụ cỏn con chạy xe tới lui m&agrave; n&oacute; c&ograve;n cho người chơi một cảm gi&aacute;c chưa từng c&oacute; khi chơi c&aacute;c phần trước, chẳng hạn như l&aacute;i xe t&ocirc;ng thẳng v&agrave;o chỗ b&aacute;n xe của Franklin rồi l&ocirc;i &ocirc;ng chủ ra nện cho một trận, đạp xe tr&ecirc;n b&atilde;i biển v&agrave;o một ng&agrave;y nắng đẹp hay thậm ch&iacute; c&ograve;n cho bạn v&agrave;o g&oacute;c nh&igrave;n của một ch&uacute; ch&oacute; khi l&agrave;m nhiệm vụ &ldquo;Chop&rdquo;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616163017_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616163017_1-1024x576.jpg\" style=\"height:540px; width:960px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đ&atilde; nhất l&agrave; nhiệm vụ Trevor trả th&ugrave; một băng nh&oacute;m d&aacute;m gi&agrave;nh mối b&aacute;n ma t&uacute;y đ&aacute; sau đ&oacute; đốt cả căn nh&agrave;, l&uacute;c ng&ocirc;i nh&agrave; nổ Trevor quay lưng đi một c&aacute;ch lạnh l&ugrave;ng đ&uacute;ng chất phim Mỹ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"https://i.ytimg.com/vi/Z25GKAlgNEQ/maxresdefault.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Cốt truyện trong GTA V rất hợp l&yacute; m&agrave; lại sống động vui nhộn, một n&eacute;t nữa c&aacute;c t&igrave;nh tiết đều đến li&ecirc;n tiếp một c&aacute;ch kh&ocirc;ng ngờ nhưng bạn vẫn thấy hợp l&yacute; v&agrave; trơn tru, thậm ch&iacute; c&ograve;n cảm thấy phấn kh&iacute;ch với những g&igrave; m&igrave;nh sắp l&agrave;m. Chẳng hạn như Michael bị vợ cắm sừng bởi huấn luyện vi&ecirc;n tennis, về nh&agrave; thấy cảnh tượng kh&oacute; ưa Michael rượt theo đ&aacute;nh ghen rồi c&ograve;n rủ cả Franklin, tới nơi thằng cha huấn luyện vi&ecirc;n đang n&uacute;p ở mộc căn nh&agrave; sang chảnh tr&ecirc;n đồi, Michael nảy ra &yacute; định k&eacute;o sập cả căn nh&agrave;. Kh&ocirc;ng ngờ nh&agrave; n&agrave;y l&agrave; của &ocirc;ng tr&ugrave;m mafia nổi tiếng, vậy l&agrave; Michael sau khi nghỉ hưu lại phải đi cướp ng&acirc;n h&agrave;ng để trả nợ cho &ocirc;ng tr&ugrave;m, rồi từ đ&oacute; nhiều t&igrave;nh tiết tiếp diễn khi m&agrave; Trevor t&igrave;m được Michael, cả bộ ba Franklin &ndash; Michael &ndash; Trevor phải rửa kiếm đem ra x&agrave;i lại chứ kh&ocirc;ng g&aacute;c nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ngo&agrave;i những nhiệm vụ ch&iacute;nh ra (ch&iacute;nh x&aacute;c l&agrave; mới chơi hơn những c&aacute;i đang n&oacute;i c&oacute; một ch&uacute;t), nh&acirc;n vật c&ograve;n c&oacute; thể tương t&aacute;c được nhiều hơn với thế giới trong game, v&iacute; dụ như bạn c&oacute; thể n&oacute;i chuyện với người đi đường nhưng hầu hết l&agrave; đều th&ugrave; hằng, đứng nh&igrave;n NPC n&agrave;o đ&oacute; th&igrave; một l&agrave; NPC sẽ bỏ đi v&agrave; chửi bạn kh&ugrave;ng &ndash; hai l&agrave; g&acirc;y sự theo kiểu &ldquo;nh&igrave;n g&igrave; m&agrave;y ?!&rdquo; v&agrave; khi r&uacute;t s&uacute;ng ra chỉa v&agrave;o mặt th&igrave; NPC sẽ xin lỗi bạn r&iacute;u r&iacute;t. Bạn c&oacute; thể afk khoảng v&agrave;i ph&uacute;t, nh&acirc;n vật sẽ bắt đầu l&agrave;m v&agrave;i h&agrave;nh động tự do khi kh&ocirc;ng ai điều khiển, th&uacute; vị&nbsp;hơn l&agrave; m&agrave;n h&igrave;nh bắt đầu zoom lại gần v&agrave; lia theo&nbsp;m&ocirc;ng phụ nữ đi ngang qua &ndash; đang rảnh m&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Th&uacute; vị hơn l&agrave; vẻ ngo&agrave;i của nh&acirc;n vật sẽ bị ảnh hưởng bởi m&ocirc;i trường b&ecirc;n ngo&agrave;i, v&iacute; dụ khi chạy nhiều nh&acirc;n vật sẽ đổ mồ h&ocirc;i v&agrave; bạn sẽ thấy &aacute;o ướt phần giữa lưng hoặc khi nhảy xuống nước cả bộ đồ sẽ bị ướt nhẹp, c&aacute;c vết đạn m&aacute;u me của nh&acirc;n vật thật hơn</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">GTA V nằm trong những d&ograve;ng game tui th&iacute;ch nhất từ trước tới giờ, ngay từ thời trẻ tr&acirc;u chơi kh&ocirc;ng thấy hay lắm tại to&agrave;n cướp xe lạng l&aacute;ch nhập cheatcode rồi bắn loạn xạ xong nghĩ, c&agrave;ng biết chơi th&igrave; c&agrave;ng thấy cốt truyện n&oacute; hay v&agrave; rất thực tế. Mới chơi được c&oacute; kh&uacute;c đầu th&ocirc;i m&agrave; đ&atilde; ghiền rồi, sau n&agrave;y chắc chắn sẽ c&ograve;n gặp nhiều nhiệm vụ hay hơn vậy nữa, kh&ocirc;ng hổ danh 10 điểm tr&ecirc;n IGN.</span></p>', '<p>OS: Windows 8.1 64 Bit, Windows 8 64 Bit, Windows 7 64 Bit Service Pack 1, Windows Vista 64 Bit Service Pack 2</p>\r\n\r\n<p>Processor: Intel Core 2 Quad CPU Q6600 @ 2.40GHz (4 CPUs) / AMD Phenom 9850 Quad-Core Processor (4 CPUs) @ 2.5GHz</p>\r\n\r\n<p>Memory: 4GB</p>\r\n\r\n<p>Video Card: NVIDIA 9800 GT 1GB / AMD HD 4870 1GB (DX 10, 10.1, 11)</p>\r\n\r\n<p>Sound Card: 100% DirectX 10 compatible</p>\r\n\r\n<p>HDD Space: 65GB DVD Drive</p>', '<p>OS: Windows 8.1 64 Bit, Windows 8 64 Bit, Windows 7 64 Bit Service Pack 1, Windows Vista 64 Bit Service Pack 2</p>\r\n\r\n<p>Processor: Intel Core 2 Quad CPU Q6600 @ 2.40GHz (4 CPUs) / AMD Phenom 9850 Quad-Core Processor (4 CPUs) @ 2.5GHz</p>\r\n\r\n<p>Memory: 4GB</p>\r\n\r\n<p>Video Card: NVIDIA 9800 GT 1GB / AMD HD 4870 1GB (DX 10, 10.1, 11)</p>\r\n\r\n<p>Sound Card: 100% DirectX 10 compatible</p>\r\n\r\n<p>HDD Space: 65GB DVD Drive</p>', 'Cài đặt và chơi', '15201654701280.jpg', 'https://www.youtube.com/embed/QkkoHAzjnUs', 2013, '2018-03-08'),
(3, 5, 'Total War Rome II', 'total-war-rome-ii', '<h3 style=\"text-align:justify\">Total War ROME II&nbsp;l&agrave;&nbsp;game chiến thuật thời gian thực v&agrave; chiến lược theo lượt,&nbsp;do h&atilde;ng The Creative Assembly ph&aacute;t triển v&agrave; Sega ph&aacute;t h&agrave;nh. Game được x&acirc;y dựng dựa theo&nbsp;thời kỳ cổ đại, v&agrave; tập trung v&agrave;o mi&ecirc;u tả mỗi nền văn h&oacute;a của thời đại một ch&iacute;nh x&aacute;c v&agrave; s&acirc;u sắc nhất c&oacute; thể, tr&aacute;i ngược với người tiền nhiệm của n&oacute; tập trung gần như ho&agrave;n to&agrave;n v&agrave;o Cộng h&ograve;a La M&atilde; v&agrave; ch&iacute;nh trị của n&oacute;. C&aacute;c chiến dịch lớn bắt đầu trong 272BC (trước c&ocirc;ng nguy&ecirc;n), v&agrave; k&eacute;o d&agrave;i trong 300 năm.</h3>', '<p><span style=\"font-size:14px\"><strong>Cũng đ&atilde; ra mắt kh&aacute; l&acirc;u (th&aacute;ng 9 năm 2013) với tận 3 lần cập nhật DLC (phi&ecirc;n bản mở rộng), thế nhưng Rome II Total war vẫn mang trong m&igrave;nh một sức h&uacute;t m&atilde;nh liệt kh&oacute; cưỡng lại đối với d&ograve;ng game RTS lẫn TBS tr&ecirc;n to&agrave;n thế giới. Nếu bạn thật sự l&agrave; game thủ của d&ograve;ng game d&agrave;n trận chiến thuật thời gian thật v&agrave; theo lượt, c&oacute; lẽ kh&ocirc;ng thể kh&ocirc;ng biết tới d&ograve;ng game Total war của nh&agrave; ph&aacute;t triển&nbsp;The Creative Assembly đồng kết hợp với tận 3 h&atilde;ng ph&aacute;t h&agrave;nh lớn (theo từng phi&ecirc;n bản) l&agrave; SEGA, Electronics Art v&agrave; Activision ph&aacute;t h&agrave;nh trong suốt hơn 14 năm qua. H&atilde;y c&ugrave;ng Guide Game xem sơ l&yacute; do m&agrave; d&ograve;ng game n&agrave;y được nhiều người ưa chuộng đến vậy với phần tr&igrave;nh diễn của phi&ecirc;n bản mới nhất: ROME II Total war</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần I: Giới thiệu game</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Rome II total war l&agrave; phần hai của phi&ecirc;n bản Rome Total war được ph&aacute;t h&agrave;nh v&agrave;o tận năm 2004 tức 10 năm trước. L&yacute; do được&nbsp;The Creative Assembly l&agrave;m lại kh&aacute; ch&iacute;nh đ&aacute;ng: sau tận 10 năm trời vẫn c&oacute; hơn 2 triệu game thủ tr&ecirc;n to&agrave;n thế giới ch&igrave;m đắm v&agrave;o c&aacute;c trận đấu LAN trong phi&ecirc;n bản Rome đầu ti&ecirc;n mặc d&ugrave; chất lượng đồ họa cũng như chế độ chơi đ&atilde; qu&aacute; lỗi thời. Với phi&ecirc;n bản Rome II n&agrave;y,&nbsp;The Creative Assembly đ&atilde; thật sự mang lại một game bom tấn cho d&ograve;ng game d&agrave;n trận với c&ocirc;ng nghệ đồ họa độc quyền l&agrave;&nbsp;Warscape engine. Với khả năng t&aacute;i tạo một trận đ&aacute;nh với qu&acirc;n số hơn 10.000 người với những hiệu ứng ch&aacute;y nổ ho&agrave;nh tr&aacute;ng. Chưa kể mọi hoạt cảnh, h&agrave;nh động của từng nh&acirc;n vật đều được đảm bảo kh&ocirc;ng tr&ugrave;ng lặp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/battle-1.jpg\"><img alt=\"Rome\" src=\"https://guidegame.vn/wp-content/uploads/battle-1.jpg\" style=\"height:341px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Một trận đ&aacute;nh thường với hơn 10.000 l&iacute;nh&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần II: Tổng quan</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lấy bối canh ch&acirc;u &Acirc;u thời Hy Lạp cổ đại (năm 200 TCN đến năm 100 SCN) với hơn 40 quốc gia (Factions) với những quốc gia nổi tiếng như Rome (&Yacute; ng&agrave;y nay), Sparta,&nbsp;Macedonia (2 v&ugrave;ng đất thuộc Hy Lạp ng&agrave;y nay),&nbsp;Carthago, Ai Cập, đến những quốc gia chẳng c&oacute; t&ecirc;n tuổi g&igrave; như&nbsp;Suebi (Đức ng&agrave;y nay),&nbsp;Iceni (một bộ tộc nhỏ v&ugrave;ng Đ&ocirc;ng nước Anh). Người chơi c&oacute; thể thỏa th&iacute;ch lựa chọn quốc gia cho ri&ecirc;ng m&igrave;nh để chơi trong suốt qu&aacute; tr&igrave;nh chinh chiến. Về lối chơi Rome II c&oacute; 2 chế độ l&agrave; theo lượt v&agrave; d&agrave;n trận thời gian thật:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Với giao diện ch&iacute;nh l&agrave; theo lượt. Tại giao diện n&agrave;y bạn c&oacute; thể ra lệnh cho quốc gia m&igrave;nh về mọi mặt, từ quan hệ ngoại giao (g&acirc;y chiến, h&ograve;a b&igrave;nh, l&agrave;m&nbsp;<a href=\"https://guidegame.vn/esports/lien-minh-huyen-thoai\" title=\"liên minh\">li&ecirc;n minh</a>, c&ugrave;ng giao thương hoặc rủ nhau hội đồng một nước kh&aacute;c) đến&nbsp;<a href=\"https://guidegame.vn/champions/vi\" title=\"vi\">vi</a>ệc n&acirc;ng cấp th&agrave;nh phố (x&acirc;y nh&agrave; l&iacute;nh, x&acirc;y cảng, x&acirc;y khu chăn nu&ocirc;i, x&acirc;y nh&agrave; văn h&oacute;a như rạp h&aacute;t, hoặc x&acirc;y đền đ&agrave;i thờ thần th&aacute;nh). Việc bạn thỏa th&iacute;ch l&agrave;m mọi thứ theo &yacute; bạn được hoan ngh&ecirc;nh từ nh&agrave; ph&aacute;t triển&nbsp;The Creative Assembly! Sau mỗi lượt đi bạn c&oacute; thể tiếp tục n&acirc;ng cấp nh&agrave; hoặc mua th&ecirc;m tiểu đội (1 lượt l&agrave; 1 m&ugrave;a trong năm, tối đa 4 m&ugrave;a/năm) v&agrave; việc n&acirc;ng cấp nh&agrave; hoặc mua đồ đều được t&iacute;nh bằng tiền v&agrave; lượt đi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/28039TWRII_E3_2013_Pontus.png\"><img alt=\"Rome 2 \" src=\"https://guidegame.vn/wp-content/uploads/28039TWRII_E3_2013_Pontus.png\" style=\"height:337px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Giao diện theo lượt, với mỗi đạo qu&acirc;n được tượng trưng bằng h&igrave;nh ảnh vị tướng/thuyền (với thủy qu&acirc;n) cầm l&aacute; cờ với số gạch ngang tượng trung cho số tiểu đội đang sỡ hữu</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; C&ograve;n giao diện d&agrave;n trận thời gian thực sẽ diễn ra chỉ khi n&agrave;o bạn mở một trận đ&aacute;nh. Với qu&acirc;n số trung b&igrave;nh 1 tiểu đội l&iacute;nh l&agrave; từ 90 l&iacute;nh với kỵ binh tới tận 180 l&iacute;nh với bộ binh. Với một đại đội ho&agrave;n chỉnh bao gồm 19 tiểu đội cộng với 1 tiểu đội với vị Tướng đứng đầu th&igrave; với qu&acirc;n số trung b&igrave;nh l&agrave; hơn 4000 l&iacute;nh cho một đại đội. Ngo&agrave;i ra việc bạn c&oacute; thể mở giao tranh với chiến thuật lấy thịt đ&egrave; người với tối đa 10 đại đội gồm nh&igrave;u quốc gia đồng minh chung th&igrave; việc một trận đ&aacute;nh c&oacute; hơn 80.000 l&iacute;nh cho tổng cả 2 phe l&agrave; quả thật rất lớn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/maxresdefault4.jpg\"><img alt=\"Rome 2\" src=\"https://guidegame.vn/wp-content/uploads/maxresdefault4.jpg\" style=\"height:337px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>1 trận đ&aacute;nh ti&ecirc;u biểu với chiến thuật lấy thịt đ&egrave; người v&agrave; ph&ograve;ng thủ theo từng lớp l&iacute;nh với tướng l&agrave; trung t&acirc;m</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần III: Lối chơi &ndash; chiến thuật</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&uacute;ng ta phải n&oacute;i qua về kinh tế &ndash; ch&iacute;nh trị trước trong game n&agrave;y. Với việc kinh tế ch&iacute;nh l&agrave; giao thương v&agrave; thuế d&acirc;n, bạn kh&ocirc;ng thể n&agrave;o tăng thuế v&ocirc; l&yacute; v&igrave; l&ograve;ng d&acirc;n c&oacute; hạn v&agrave; nếu d&acirc;n ch&uacute;ng qu&aacute; ngh&egrave;o khổ do bạn l&agrave; một &ocirc;ng vua tham lam th&igrave; họ sẽ lật đổ bạn bằng c&aacute;ch khởi nghĩa với nhiều đạo qu&acirc;n Rebels (qu&acirc;n c&aacute;ch mạng). C&ograve;n giao thương bạn phải c&oacute; được chữ k&yacute; của người đứng đầu quốc gia đ&oacute; để c&oacute; quyền giao thương, n&oacute;i l&agrave; chữ k&yacute; nhưng n&oacute; như một bản cam kết v&agrave; việc c&oacute; được chữ k&yacute; đ&oacute; mu&ocirc;n v&agrave;n kh&oacute; khăn: kh&aacute;c biệt chủng tộc, kh&aacute;c biệt t&ocirc;n gi&aacute;o, kh&aacute;c biệt v&ugrave;ng, c&oacute; quan hệ th&acirc;n mật với một quốc gia thứ 3 l&agrave; kẻ th&ugrave; của quốc gia muốn giao thương&hellip; hoặc l&yacute; do đơn giản nhất l&agrave; &ldquo;tao gh&eacute;t tao kh&ocirc;ng th&iacute;ch k&yacute; th&igrave; m&agrave;y l&agrave;m g&igrave; được tao?&rdquo;. Việc đ&uacute;t l&oacute;t, t&acirc;ng bốc hoặc gi&uacute;p đỡ về qu&acirc;n sự, trao đổi kỹ thuật l&agrave; điều bắt buộc để c&oacute; được sự giao thương hoặc rộng hơn l&agrave; quan hệ đồng minh. Về mặt ch&iacute;nh trị trong nước lẫn quốc tế, bạn ho&agrave;n to&agrave;n nắm quyền, l&agrave; vua. Thế nhưng đối với một số nước c&oacute; hội đồng tối cao (V&iacute; dụ như Đế chế La M&atilde;, Đế Chế Carthago&nbsp;) th&igrave; bạn vẫn phải l&agrave;m theo &yacute; hội đồng nếu kh&ocirc;ng muốn bị Hội đồng chia năm xẻ bảy v&agrave; dẫn đến nội chiến (Civil War) Khi đ&oacute; nếu bạn đ&aacute;nh được to&agrave;n bộ c&aacute;c đạo qu&acirc;n từ c&aacute;c nh&agrave; kh&aacute;c (kiểu như qu&acirc;n từ c&aacute;c Ho&agrave;ng Tử kh&aacute;c) v&agrave; kết th&uacute;c nội chiến, bạn sẽ l&agrave;m Vua v&agrave; dẹp lu&ocirc;n cả c&aacute;i hội đồng ồn &agrave;o n&agrave;y. Ngo&agrave;i ra về ch&iacute;nh trị đối với c&aacute;c nước kh&aacute;c, bạn phải thật kh&ocirc;n kh&eacute;o với nhiều ch&iacute;nh s&aacute;ch ngoại giao kh&aacute;c nhau. Việc kết bạn với c&aacute;c quốc gia mạnh xung quanh hoặc g&acirc;y chiến với c&aacute;c quốc gia nhỏ yếu thế hơn để khẳng định sức mạnh qu&acirc;n sự đ&ocirc;i khi l&agrave; điều bắt buộc để quốc gia tồn tại. Đ&ocirc;i khi bạn phải nhờ vả c&aacute;c nước c&oacute; sức mạnh qu&acirc;n sự khủng khiếp hoặc k&ecirc;u gọi c&aacute;c nước yếu xung quanh để c&oacute; thể đ&aacute;nh được một quốc gia kh&aacute;c. Lấy v&iacute; dụ như nếu bạn chơi Ai Cập th&igrave; chỉ cần cỡ 20 lượt đi đầu ti&ecirc;n bạn sẽ vấp phải một chướng ngại cực lớn đến từ ph&iacute;a Đ&ocirc;ng l&agrave; li&ecirc;n minh c&aacute;c quốc gia của đế chế Selucid (gồm c&aacute;c nước thuộc địa cũ của Ba Tư) với hơn 10 nước thuộc địa v&agrave; Selucid sẽ tuy&ecirc;n chiến với bạn (theo đ&uacute;ng lịch sử)</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/1EYCARR.jpg\"><img alt=\"Rome 2\" src=\"https://guidegame.vn/wp-content/uploads/1EYCARR.jpg\" style=\"height:337px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đ&ocirc;i khi phải đ&uacute;t l&oacute;t như trong h&igrave;nh để k&yacute; được hiệp ước giao thương</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">L&agrave; một game chiến thuật, bạn kh&ocirc;ng thể m&atilde;i d&ugrave;ng chiến thuật lấy thịt đ&egrave; người như những g&igrave; c&aacute;c game d&agrave;n trận kh&aacute;c đ&atilde; l&agrave;m. Với mỗi quốc gia đều c&oacute; thế mạnh của mỗi quốc gia đ&oacute;. V&iacute; dụ như Đế chế La M&atilde; (Rome) th&igrave; mạnh về qu&acirc;n cầm khi&ecirc;ng với đội h&igrave;nh con r&ugrave;a nổi tiếng (đội h&igrave;nh Testudo) tốt cho việc đỡ c&aacute;c vũ kh&iacute; s&aacute;t thương từ xa. C&aacute;c nước thuộc khối Hy Lạp như Sparta, Athens hoặc Macedonia th&igrave; mạnh về qu&acirc;n cầm gi&aacute;o ngắn với đội h&igrave;nh Phalanx huyền thoại c&oacute; khả năng trụ vững trước những pha x&ocirc;ng v&agrave;o gi&aacute;p l&aacute; c&agrave;. Ngo&agrave;i ra cũng n&ecirc;n kể về đội h&igrave;nh Phalanx Pikeman cầm gi&aacute;o d&agrave;i của qu&acirc;n Ai Cập v&agrave; c&aacute;c nước v&ugrave;ng Trung Đ&ocirc;ng c&oacute; khả năng giết chết ngay những kỵ binh liều lĩnh x&ocirc;ng v&agrave;o. Ngo&agrave;i ra cũng n&ecirc;n n&oacute;i về c&aacute;c trận thủy chiến với t&agrave;u gỗ m&aacute;i ch&egrave;o khi xưa với những pha Roam (t&ocirc;ng) thẳng v&agrave;o t&agrave;u đối phương hoặc Sige (cập v&agrave;o mạng t&agrave;u đối phương để cho qu&acirc;n sang). Thậm ch&iacute; phi&ecirc;n bản Rome II đ&atilde; c&oacute; cải tiến đ&aacute;ng kể m&agrave; những phi&ecirc;n bản trước chưa c&oacute; ch&iacute;nh l&agrave; việc thủy binh nay đ&atilde; c&oacute; thể hỗ trợ bộ binh để c&ocirc;ng th&agrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/Testudo.jpg\"><img alt=\"Testudo\" src=\"https://guidegame.vn/wp-content/uploads/Testudo.jpg\" style=\"height:337px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&nbsp;Đội h&igrave;nh con r&ugrave;a Testudo nổi tiếng của người La M&atilde; khi c&ocirc;ng th&agrave;nh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/Phalanx.jpg\"><img alt=\"Phalanx\" src=\"https://guidegame.vn/wp-content/uploads/Phalanx.jpg\" style=\"height:375px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&nbsp;Đội h&igrave;nh Phalanx của Sparta với khi&ecirc;n giơ cao ngang vai v&agrave; gi&aacute;o g&aacute;c tr&ecirc;n khi&ecirc;ng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/Pike-Man.jpg\"><img alt=\"Pike Man\" src=\"https://guidegame.vn/wp-content/uploads/Pike-Man.jpg\" style=\"height:375px; width:600px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đội h&igrave;nh Phalanx Pikeman của Sparta với gi&aacute;o d&agrave;i chống kỵ binh cực tốt</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần IV: Kết</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Vẫn c&ograve;n rất nhiều thứ m&agrave; Guide Game muốn chia sẽ cho c&aacute;c bạn về Rome II Total war, thế nhưng nếu n&oacute;i hết th&igrave; c&ograve;n rất rất d&agrave;i n&ecirc;n Guide Game sẽ chỉ tổng hợp nhưng th&ocirc;ng tin n&oacute;ng của tựa game n&agrave;y để mọi người c&ograve;n hy vọng, t&igrave;m hiểu v&agrave; chơi thử. Guide Game mong rằng với những th&ocirc;ng tin tr&ecirc;n người chơi sẽ c&oacute; được c&aacute;i nh&igrave;n sơ lượt về d&ograve;ng game n&agrave;y v&agrave; sắp tới Guide Game sẽ c&ograve;n mang rất nhiều d&ograve;ng game kh&aacute;c cho mọi người, h&atilde;y c&ugrave;ng đ&oacute;n đọc nh&eacute;.</span></p>', '<p>OS: XP/ Vista / Windows 7 / Windows 8</p>\r\n\r\n<p>Processor:2 GHz Intel Dual Core processor / 2.6 GHz Intel Single Core processor</p>\r\n\r\n<p>Memory: 2GB RAM</p>\r\n\r\n<p>Graphics:512 MB DirectX 9.0c compatible card (shader model 3, vertex texture fetch support).</p>\r\n\r\n<p>DirectX&reg;:9.0c</p>\r\n\r\n<p>Hard Drive: 35 GB HD space</p>', '<p>OS: XP/ Vista / Windows 7 / Windows 8</p>\r\n\r\n<p>Processor:2 GHz Intel Dual Core processor / 2.6 GHz Intel Single Core processor</p>\r\n\r\n<p>Memory: 2GB RAM</p>\r\n\r\n<p>Graphics:512 MB DirectX 9.0c compatible card (shader model 3, vertex texture fetch support).</p>\r\n\r\n<p>DirectX&reg;:9.0c</p>\r\n\r\n<p>Hard Drive: 35 GB HD space</p>', 'Cài đặt và chơi', '15201676045069.jpg', 'https://www.youtube.com/embed/JReGDeaxaDI', 2013, '2018-03-08'),
(4, 6, 'Assassin’s Creed: Origins', 'assassin’s-creed:-origins', '<h3 style=\"text-align:justify\">Tiếp nối truyền thống tốt đẹp của d&ograve;ng game&nbsp;Assassin&rsquo;s Creed,&nbsp;Assassin&rsquo;s Creed Origins&nbsp;tiếp tục đưa game thủ đi ngược d&ograve;ng thời gian để t&igrave;m đến với những nền văn minh xưa cũ ẩn chứa v&ocirc; v&agrave;n điều b&iacute; ẩn chưa thể t&igrave;m ra lời giải đ&aacute;p. Tuy nhi&ecirc;n, kh&aacute;c với c&aacute;c phi&ecirc;n bản đang c&oacute; xu hướng &ldquo;hiện đại h&oacute;a&rdquo; trong thời gian gần đ&acirc;y,&nbsp;Assassin&rsquo;s Creed Origins&nbsp;lại đưa game thủ đến một mốc thời gian kh&aacute; xa x&ocirc;i &ndash; Ai Cập cổ đại, mốc thời gian khoảng năm 49 TCN. Đến đ&acirc;y sẽ c&oacute; kh&ocirc;ng &iacute;t người tự hỏi: &ldquo;Thời cổ đại đ&atilde; c&oacute; Templar v&agrave; Assassins Brotherhood hay sao?&rdquo;</h3>', '<p><span style=\"font-size:14px\"><strong>Assassin&rsquo;s Creed: Origins l&agrave;</strong>&nbsp;<strong>game phi&ecirc;u lưu&nbsp;h&agrave;nh động<strong>&nbsp;</strong>tiếp theo của series&nbsp;game Assassin&rsquo;s Creedđ&igrave;nh đ&aacute;m nh&agrave;&nbsp;Ubisoft. Game Assassin&rsquo;s Creed: Origins vượt qua những người đ&agrave;n anh trước đ&oacute; để mang 1 thế giới mở lớn nhất từ trước đến nay cho c&aacute;c game thủ thỏa sức kh&aacute;m ph&aacute;.</strong>&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-danh.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&oacute; thể n&oacute;i th&ocirc;ng qua Assassin&rsquo;s Creed: Origins nh&agrave; ph&aacute;t h&agrave;nh game Ubisoft đang nỗ lực lấy lại h&igrave;nh ảnh của m&igrave;nh sau những thất vọng kh&ocirc;ng đ&aacute;ng c&oacute; với nhiều c&aacute;i t&ecirc;n đ&atilde; l&agrave;m c&aacute;c game thủ kỳ vọng v&agrave; sau đ&oacute; l&agrave; chuỗi ng&agrave;y thất vọng:&nbsp;Assassin&#39;s Creed 3,&nbsp;Watch Dogs 2, vv. Chắc chắn bạn sẽ kh&ocirc;ng gặp lại những điều đ&oacute; khi bước ch&acirc;n v&agrave;o thế giới của game Assassin&rsquo;s Creed: Origins.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-hanh-dong.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&acirc;y l&agrave; phi&ecirc;n bản ch&iacute;nh thứ 10, Assassins&rsquo;s Creed Origins nhận được kh&aacute; nhiều k&igrave; vọng nhằm vực dậy lại d&ograve;ng Assassins&rsquo;s Creed. Assassins&rsquo;s Creed Origins cho thấy tham vọng của h&atilde;ng n&agrave;y v&agrave;o phi&ecirc;n bản lấy bối cảnh tại Ai Cập với rất nhiều tiểu tiết bao gồm quang cảnh nh&acirc;n vật, văn h&oacute;a của thời kỳ n&agrave;y, nếu kh&ocirc;ng l&agrave;m tốt th&igrave; Assassins&rsquo;s Creed Origins ch&iacute;nh l&agrave; sự kết th&uacute;c của Assassins&rsquo;s Creed.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-nhap-vai.jpg\" style=\"height:337px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Assassin&#39;s Creed: Origins đưa người chơi đến với Ai Cập cổ đại với cuộc phi&ecirc;u lưu của g&atilde; s&aacute;t thủ Ba Yek. Trong game Assassins&rsquo;s Creed Origins thay v&igrave; s&uacute;ng đạn của c&aacute;c phi&ecirc;n bản mới gần đ&acirc;y ch&uacute;ng ta sẽ sử dụng cung t&ecirc;n l&agrave;m vũ kh&iacute; tầm xa cực kỳ tin cậy của nh&acirc;n vật ch&iacute;nh. Chủ đề của ch&uacute;ng ta ch&iacute;nh l&agrave; Ai Cập cổ đại n&oacute;i về hội s&aacute;t thủ đầu ti&ecirc;n tr&ecirc;n thế giới đ&atilde; được th&agrave;nh lập.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-nu-hoang.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Người chơi sẽ điều khiển hai nh&acirc;n vật bao gồm cả nam v&agrave; nữ với hệ thống kỹ năng ri&ecirc;ng biệt, c&aacute;c kỹ năng n&agrave;y c&oacute; thể n&acirc;ng cấp th&ocirc;ng qua hệ thống kỹ năng giống như c&aacute;c game nhập vai kh&aacute;c. Tuy nhi&ecirc;n thay v&igrave; nh&acirc;n vật sẽ sử dụng loại vũ kh&iacute; đặc trưng th&igrave; c&aacute;c nh&acirc;n vật trong game Assassins&rsquo;s Creed Origins sẽ d&ugrave;ng l&aacute; chắn cũng như c&aacute;c loại vũ kh&iacute; mang hơi hướng chiến binh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-phieu-luu.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thay v&igrave; sử dụng c&aacute;c loại vũ kh&iacute; đặc trưng của s&aacute;t thủ, nh&acirc;n vật trong Origins sẽ sử dụng cả l&aacute; chắn, thứ vũ kh&iacute; mang hơi hướng của c&aacute;c chiến binh. Theo những đồn đo&aacute;n trước đ&oacute;, Origins sẽ c&oacute; nhiều yếu tố về biển (giống như người tiền nhiệm Black Flag). Tuy nhi&ecirc;n những yếu tố đ&oacute; lại kh&ocirc;ng thấy xuất hiện trong bức h&igrave;nh quảng c&aacute;o n&agrave;y. Do vậy, ch&uacute;ng ta vẫn chưa thể khẳng định được Assassin&rsquo;s Creed: Origins sẽ c&oacute; những m&agrave;n chơi tr&ecirc;n biển hay kh&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-sat-thu.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Để c&oacute; thể di chuyển thuận tiện trong thế giới mở rộng lớn của Ai Cập, ch&uacute;ng ta c&oacute; thể lựa chọn cưỡi th&uacute; như: ngựa v&agrave; lạc đ&agrave;, di chuyển đường thủ bằng thuyền tr&ecirc;n s&ocirc;ng Nile thơ mộng v&agrave; lặn s&ocirc;ng cũng l&agrave; 1 trải nghiệm v&ocirc; c&ugrave;ng th&uacute; vị. Tuy nhi&ecirc;n để c&oacute; được những lựa chọn tr&ecirc;n bạn phải t&igrave;m kiếm v&agrave; thu thập trong suốt h&agrave;nh tr&igrave;nh để c&oacute; được thứ tốt nhất cho chuyến đi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-tan-cong.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chuyến h&agrave;nh tr&igrave;nh v&ocirc; số những thử nghiệm sẽ để lại nhiều ấn tượng cho c&aacute;c game thủ giống như 1 chuyến đi bụi xuy&ecirc;n qua Ai Cập của d&acirc;n du lịch. Bạn c&oacute; cơ hội bắt c&aacute; bằng mũi t&ecirc;n đ&acirc;m xuy&ecirc;n m&aacute;u, nhưng l&uacute;c n&agrave;y m&aacute;u loang ra s&ocirc;ng c&oacute; thể l&ocirc;i k&eacute;o c&aacute; sấu đến g&acirc;y hại cho bạn. C&ugrave;ng đ&oacute; l&agrave; rất nhiều h&agrave;nh động bộc ph&aacute;t v&agrave; thuận theo tự nhi&ecirc;n cứ thế diễn ra khiến cho cuộc phi&ecirc;u lưu của Assassin&rsquo;s Creed: Origins trở n&ecirc;n hấp dẫn hơn bao giờ hết. Tải game Assassin&rsquo;s Creed: Origins để bắt đầu tham gia cuộc phi&ecirc;u lưu kỳ th&uacute; n&agrave;y c&ugrave;ng nh&oacute;m s&aacute;t thủ chuy&ecirc;n nghiệp.</span></p>', '<p>Hệ Điều H&agrave;nh: Windows 7, Windows 8, Windows 8.1 v&agrave; Windows 10 (64bit)</p>\r\n\r\n<p>Bộ vi xử l&yacute;: Intel Core i5-2400s @ 2.5 GHz or AMD FX-6350 @ 3.9 GHz hoặc tương đương</p>\r\n\r\n<p>Bộ nhớ RAM: 6 GB</p>\r\n\r\n<p>Ổ cứng c&agrave;i đặt game trống: 42 GB</p>\r\n\r\n<p>DirectX: phi&ecirc;n bản 11</p>\r\n\r\n<p>Đồ họa: NVIDIA GeForce GTX 660 or AMD R9 270 (2048 MB VRAM with Shader Model 5.0 hoặc tốt hơn)</p>', '<p>Hệ Điều H&agrave;nh: Windows 7, Windows 8, Windows 8.1 v&agrave; Windows 10 (64bit)</p>\r\n\r\n<p>Bộ vi xử l&yacute;: Intel Core i5-2400s @ 2.5 GHz or AMD FX-6350 @ 3.9 GHz hoặc tương đương</p>\r\n\r\n<p>Bộ nhớ RAM: 6 GB</p>\r\n\r\n<p>Ổ cứng c&agrave;i đặt game trống: 42 GB</p>\r\n\r\n<p>DirectX: phi&ecirc;n bản 11</p>\r\n\r\n<p>Đồ họa: NVIDIA GeForce GTX 660 or AMD R9 270 (2048 MB VRAM with Shader Model 5.0 hoặc tốt hơn)</p>', 'Cài đặt và chơi', '1520168328ss_1a0ab42e93e51c48ad9a6bda7f408958e09a43b0.1920x1080.jpg', 'https://www.youtube.com/embed/8QCVi7kfgrw', 2017, '2018-03-08');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(5, 8, 'Pro Evolution Soccer 2018', 'pro-evolution-soccer-2018', '<h3>Tựa game b&oacute;ng đ&aacute; được mong chờ nhất tại Việt Nam sắp ra l&ograve; với những bổ sung th&uacute; vị. PES 2018 hứa hẹn c&oacute; đồ hoạ đẹp mắt, lối chơi ch&acirc;n thật v&agrave; rực lửa hơn so phi&ecirc;n bản 2017.</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ng&agrave;y đầu ti&ecirc;n tại E3 2017, PES 2018 đ&atilde; đem đến trailer mới toanh c&ugrave;ng chi tiết c&aacute;c t&iacute;nh năng mới khiến c&aacute;c fan ngất ng&acirc;y.</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trailer E3 của PES 2018 nổi bật nhất trong show diễn,&nbsp;ch&uacute;ng ta h&atilde;y c&ugrave;ng chi&ecirc;m ngưỡng&nbsp;v&agrave; ph&acirc;n t&iacute;ch&nbsp;c&aacute;c t&iacute;nh năng v&agrave; th&ocirc;ng tin của phi&ecirc;n bản Pro Evolution Soccer tiếp theo&nbsp;được Konami đem đến:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>1. Gameplay MasterClass:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>a. R&ecirc; b&oacute;ng chiến thuật:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện việc điều khiển: Cảm nhận việc điều khiển to&agrave;n bộ khi đang kiểm so&aacute;t b&oacute;ng, khả năng tạo được những bước di chuyển nhỏ v&agrave; quyết định để duy tr&igrave; tr&aacute;i b&oacute;ng hay tạo khoảng trống.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_01_dribbling_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thể hiện vật l&yacute;: Việc điều khiển b&oacute;ng th&ocirc;ng minh cho ph&eacute;p người chơi giữ b&oacute;ng tr&aacute;nh xa khỏi c&aacute;c cầu thủ khỏe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">R&ecirc; b&oacute;ng chiến thu&acirc;t: Điều khiển việc chạm b&oacute;ng theo toan t&iacute;nh thời gian chỉ qua việc điều khiển&nbsp;tr&ecirc;n tay cầm hoặc b&agrave;n ph&iacute;m. Dễ d&agrave;ng r&ecirc; b&oacute;ng qua người khi đưa b&oacute;ng v&agrave;o chận kh&ocirc;ng thuận của hậu vệ hoặc d&ugrave;ng giả s&uacute;t&nbsp;&quot;vu&ocirc;ng X&quot;.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>b. Real Touch+ (chạm thực tế):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chạm&nbsp;to&agrave;n bộ cơ thể: Kiểm so&aacute;t mượt m&agrave; hơn th&ocirc;ng qua chuyển động thực tế từ c&aacute;c bộ phận tr&ecirc;n cơ thể như ngực, bụng v&agrave; đ&ugrave;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhận thức xung quanh: Cầu thủ sẽ ch&uacute; &yacute; xung quanh khi nhận b&oacute;ng, thường th&igrave; sẽ đẩy cơ thể v&agrave;o giữa đối phương v&agrave; tr&aacute;i b&oacute;ng. Ri&ecirc;ng với những cầu thủ khỏe sẽ c&oacute; nhiều giải ph&aacute;p hơn trong việc giữ b&oacute;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_02_opposition_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vật l&yacute; b&oacute;ng thực tế: Cải thiện độ thực tế như việc nẩy b&oacute;ng,&nbsp;với những cầu thủ c&oacute; kỹ thuật tốt sẽ điều khiển mượt m&agrave; hơn theo từng t&igrave;nh huống b&oacute;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>c. T&igrave;nh huống b&oacute;ng chết mới:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phạt g&oacute;c/Đ&aacute; phạt: Cải thiện việc điều khiển khi loại bỏ phần hướng dẫn.&nbsp;Với lựa chọn g&oacute;c&nbsp;camera mới sẽ đem lại cho game thủ c&aacute;i nh&igrave;n tốt nhất để đ&aacute; phạt. Người chơi cũng sẽ nh&igrave;n thấy nhiều h&agrave;nh động hơn khi bước l&ecirc;n đ&aacute; phạt g&oacute;c, trong khi việc điều khiển chiến thuật c&oacute; thể được thực hiện v&agrave;o thời điểm thực hiện c&uacute; đ&aacute; đem đến sự kịch t&iacute;nh hơn cho trận đấu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_03_corner-kick_01_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_04_corner-kick_02_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>2. Cải thiện t&iacute;nh thực tế của h&igrave;nh ảnh:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>a. Khu&ocirc;n mẫu cầu thủ ch&iacute;nh x&aacute;c:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Qu&eacute;t h&igrave;nh ảnh to&agrave;n bộ cơ thể cầu thủ: Sử dụng dữ liệu scan thật để t&aacute;i tạo lại 1 c&aacute;ch ho&agrave;n hảo cầu thủ bao gồm cả cỡ &aacute;o hay độ vừa vặn khi mặc &aacute;o đấu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_01_playermodels_pes.jpg\" style=\"height:394px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>b. Thực hiện lại chuyển động:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&acirc;n t&iacute;ch mở rộng: C&aacute;c chuyển động được l&agrave;m lại từ đầu, bắt đầu từ những chuyển động nhỏ như đi bộ, xoay v&agrave; tư thế cầu thủ</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chuyển động cơ thể: Với h&agrave;ng trăm chuyển động được đưa v&agrave;o game, sự tự nhi&ecirc;n sẽ được thể hiện chưa từng c&oacute; trong d&ograve;ng game PES.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Biểu cảm khu&ocirc;n mặt: Chuyển động khu&ocirc;n mặt được n&acirc;ng cấp đ&aacute;ng kể, cho ph&eacute;p người chơi xem được những biến chuyển phức tạp trong cảm x&uacute;c với bất kỳ t&igrave;nh huống n&agrave;o.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thủ m&ocirc;n: Người g&aacute;c đền được đ&aacute;nh gi&aacute; cao sẽ c&oacute; bước cải tiến lớn, Ngo&agrave;i ra c&oacute; th&ecirc;m nhiều chuyển động mới cho thủ m&ocirc;n, GK sẽ giữ b&oacute;ng tự nhi&ecirc;n hơn sau mỗi pha bắt b&oacute;ng v&agrave; n&eacute;m b&oacute;ng th&ocirc;ng minh hơn khi chuyền cho đồng đội.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_02_goalkeepers_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Yếu tố hiển thị mới: Bộ HUD mới (bảng tỉ số, radar,...) sẽ bao gồm chỉ số trong trận đấu của từng cầu thủ v&agrave; đội b&oacute;ng. C&aacute;c cầu thủ cũng sẽ được sử dụng ảnh thật trong PES 2018. C&aacute;c cảnh cắt như thay người hay l&uacute;c trọng t&agrave;i phạt thẻ sẽ được th&ecirc;m mới. Ngo&agrave;i ra, l&uacute;c cầu thủ lập hat-trick cũng sẽ c&oacute; ph&acirc;n cảnh kh&aacute;c ho&agrave;n to&agrave;n PES 2017.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_03_matchexperience_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>c. H&igrave;nh ảnh đời thật&nbsp;(chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&Aacute;nh s&aacute;ng thực: Với việc sử dụng nghi&ecirc;n cứu dữ liệu từ c&aacute;c s&acirc;n vận động đối t&aacute;c như Nou Camp hay Signal Iduna-Park, PES 2018 sẽ đem đến &aacute;nh s&aacute;ng thật của mặt cỏ.&nbsp;H&igrave;nh ảnh cầu thủ trong đường hầm cả l&uacute;c ban ng&agrave;y lẫn ban đ&ecirc;m cũng được dựa tr&ecirc;n dữ liệu thực tế thu thập từ c&aacute;c trận b&oacute;ng ngo&agrave;i đời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_04_lighting_pes.jpg\" style=\"height:274px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&ograve;ng thay đồ: H&igrave;nh ảnh ph&ograve;ng thay đồ sẽ mang lại cảm gi&aacute;c thực tế hơn cho PES 2018</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&aacute;n giả: H&igrave;nh ảnh kh&aacute;n giả v&agrave; cảm x&uacute;c của họ với từng pha b&oacute;ng tr&ecirc;n s&acirc;n được cải thiện theo c&aacute;ch tự nhi&ecirc;n nhưng vẫn đầy kịch t&iacute;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>3. Chế độ Online Co-op (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thi đấu với bạn b&egrave;: Chế độ mới hỗ trợ chơi 2v2 v&agrave;&nbsp;3v3. cho ph&eacute;p người chơi thi đấu c&ugrave;ng bạn b&egrave; hoặc người kh&aacute;c với h&agrave;ng triệu đối thủ kh&aacute;c tr&ecirc;n thế giới</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chơi với phong c&aacute;ch của bạn: Bạn c&oacute; thể tạo nh&oacute;m với việc gh&eacute;p đ&ocirc;i tự động hoặc tạo 1 clan c&ugrave;ng bạn b&egrave; của m&igrave;nh để đua rank.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Huấn luyện vi&ecirc;n Co-op: Hỗ trợ 2 kh&aacute;ch chơi tr&ecirc;n c&ugrave;ng 1 m&aacute;y console v&agrave; chơi 3 người c&ugrave;ng 1 đội khi đ&aacute; online</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_3_01_online-coop_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_3_01_online-coop_result_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_3_01_online-coop_profile_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hợp nhất c&aacute;c chế độ: Chơi 3v3 qua c&aacute;c chế độ bao gồm myClub v&agrave; PES League</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>4. C&aacute;c chế độ v&agrave; t&iacute;nh năng mới (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>a. myClub:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hợp nhất PES League: Thi đấu c&aacute;c giải đấu eSports với myClub</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện việc đấu gi&aacute; cầu thủ: C&aacute;c chức năng như t&igrave;m kiếm dữ liệu những loại cầu thủ ch&iacute;nh x&aacute;c m&agrave; người chơi muốn. lịch sử c&aacute;c cuộc đấu gi&aacute; trong qu&aacute; khứ v&agrave; tự động đặt gi&aacute; đấu thầu được đưa v&agrave;o game.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">myClub Online Co-op: X&acirc;y dựng đội Co-op của bạn c&ugrave;ng với bạn b&egrave;,&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>b. Master League:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thử th&aacute;ch v&agrave; mục ti&ecirc;u: Ở đầu m&ugrave;a giải mới, bạn sẽ được quyền đặt mục ti&ecirc;u dựa v&agrave;o t&igrave;nh h&igrave;nh của đội b&oacute;ng. Đ&acirc;y l&agrave; điều quan trọng gi&uacute;p bạn giữ được c&ocirc;ng việc HLV.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_4_02_challenges_pes.jpg\" style=\"height:500px; width:888px\" />&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Giải đấu trước m&ugrave;a giải: Thi đấu với c&aacute;c đội b&oacute;ng lớn trước khi m&ugrave;a giải ch&iacute;nh thức bắt đầu</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện hệ thống chuyển nhương: Th&ecirc;m những yếu tố mới như giải ph&oacute;ng ph&iacute; chuyển nhượng trong hợp đồng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c yếu tố mới: Menu mới, phỏng vấn trước trận đấu v&agrave; cảnh ở ph&ograve;ng thay đồ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>c. Random Selection Match:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chế độ y&ecirc;u th&iacute;ch trở lại: Đặt ti&ecirc;u ch&iacute; của người chơi với cầu thủ được chọn sẽ trở th&agrave;nh đội b&oacute;ng của bạn</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hệ thống mua b&aacute;n: Khi bạn đ&atilde; c&oacute; những cầu thủ của m&igrave;nh, h&atilde;y thử v&agrave; mặc cả việc trao đổi cầu thủ m&agrave; bạn muốn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_4_03_trade_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>5. Tu sửa lại giao diện (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menu: Menu được thiết kế lại theo phong c&aacute;ch hiện đại hơn. HUD in-game (bảng tỉ số, radar,...) đem đến cảm gi&aacute;c giống như xem truyền h&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/main_new_feature_5_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ảnh thật cầu thủ: H&igrave;nh ảnh thật của cầu thủ được đưa v&agrave;o Game Plan v&agrave; m&agrave;n h&igrave;nh hiển thị chỉ số trong trận</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_5_01_life_photos_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>6. Hợp nhất PES League (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):&nbsp;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_6_01_pesleague_pes.jpg\" style=\"height:500px; width:888px\" /></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">PES League mở rộng th&ecirc;m c&aacute;c chế độ như myClub, Co-op v&agrave; Random Selection Match</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&acirc;n bằng thống k&ecirc; người chơi: Cho ph&eacute;p tất cả c&aacute;c đội c&oacute; chỉ số trung b&igrave;nh giống nhau.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>7. Cải thiện nền tảng PC:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đồ họa v&agrave; nội dung của PC đảm bảo chất lượng như PS4 v&agrave; Xbox One</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_7_01_pc_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>8. PES 2018 Online Beta:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/main_open_beta_test_pes.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">PES 2018 Online Beta sẽ được Konami ph&aacute;t h&agrave;nh tr&ecirc;n PS4 v&agrave; Xbox One từ th&aacute;ng 7 năm nay. Phi&ecirc;n bản n&agrave;y sẽ bao gồm chế độ Quickmatch v&agrave; Online Co-op.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>9. Usain Bolt v&agrave; Maradona&nbsp;trở th&agrave;nh đại sứ mới của PES:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/bolt_img_01.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/bolt_img_02.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_maradona_face.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_maradona_dribble.jpg\" style=\"height:500px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể n&oacute;i, với những thay đổi đ&aacute;ng kể như tr&ecirc;n, PES 2018 với&nbsp;kỳ vọng l&agrave; &quot;phi&ecirc;n bản PES hay nhất 10 năm qua&quot; đang dần dần lấy lại niềm tin nơi người h&acirc;m mộ v&agrave; chắc chắn sẽ khiến kh&ocirc;ng &iacute;t c&aacute;c fan phải nao l&ograve;ng.</span></span></p>', '<p>OS: Windows 7 64 bit (32 bit NOT supported)</p>\r\n\r\n<p>Processor: Intel Core i5-3230M @ 2.60GHz or equivalent AMD processor and above</p>\r\n\r\n<p>Memory: 4 GB RAM</p>\r\n\r\n<p>Graphics: 256 MB DX 9 Compliant videocard with pixel shader 3,0</p>\r\n\r\n<p>DirectX: Version 9.0c</p>\r\n\r\n<p>Storage: 2 GB available space</p>\r\n\r\n<p>Sound Card: DirectX 9 Compatible Audio Additional</p>\r\n\r\n<p>Notes: Minimum Resolution: 1024 x 768</p>', '<p>OS: Windows 7 64 bit (32 bit NOT supported)</p>\r\n\r\n<p>Processor: Intel Core i5-3230M @ 2.60GHz or equivalent AMD processor and above</p>\r\n\r\n<p>Memory: 4 GB RAM</p>\r\n\r\n<p>Graphics: 256 MB DX 9 Compliant videocard with pixel shader 3,0</p>\r\n\r\n<p>DirectX: Version 9.0c</p>\r\n\r\n<p>Storage: 2 GB available space</p>\r\n\r\n<p>Sound Card: DirectX 9 Compatible Audio Additional</p>\r\n\r\n<p>Notes: Minimum Resolution: 1024 x 768</p>', 'Cài đặt và chơi', '1520169876U5dtzfc4c23qQ9fzoRH5mLqtWDSQchQ_1680x8400.jpg', 'https://www.youtube.com/embed/jGgNxN0rQYw', 2017, '2018-03-08'),
(6, 7, 'Little Nightmares', 'little-nightmares', '<h3>Một tựa game phi&ecirc;u lưu giải đố c&oacute; phong c&aacute;ch kh&aacute; giống với Limbo hay Inside. Một sản phẩm của BANDAI NAMCO dưới sự ph&aacute;t triển của Tarsier Studios, những người đ&atilde; l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng cho series LittleBigPlanet.&nbsp;</h3>', '<h1><span style=\"font-size:14px\"><strong>Những cơn &aacute;c mộng của tuổi thơ sẽ quay lại dưới dạng một tựa game h&agrave;nh động m&agrave;n h&igrave;nh ngang với t&ecirc;n gọi Little Nightmares.</strong></span></h1>\r\n\r\n<p><span style=\"font-size:14px\">Trong tuổi thơ của từng người, lu&ocirc;n c&oacute; những khoảnh khắc sợ h&atilde;i vu vơ như bị &ocirc;ng kẹ bắt, hay qu&aacute;i vật đang trốn dưới gầm giường của m&igrave;nh mỗi đ&ecirc;m. Khi bạn c&agrave;ng lớn l&ecirc;n, những cơn &aacute;c mộng vu vơ&nbsp;n&agrave;y&nbsp;dần biến mất v&agrave; bạn hầu như chỉ xem ch&uacute;ng như một c&acirc;u chuyện cười về sự ng&acirc;y thơ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thế nhưng mới đ&acirc;y, những cơn &aacute;c mộng sẽ quay lại dưới dạng một tựa game h&agrave;nh động m&agrave;n h&igrave;nh ngang với t&ecirc;n gọi Little Nightmares. Đảm bảo sẽ gi&uacute;p bạn bạn một lần nữa trải nghiệm những nỗi &aacute;m ảnh kinh ho&agrave;ng nhất ẩn giấu trong tuổi thơ&nbsp;của&nbsp;một đứa thơ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-7.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-7.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">V&agrave;o vai một đứa trẻ b&iacute; ẩn t&ecirc;n l&agrave; Six mang tr&ecirc;n m&igrave;nh một tấm &aacute;o kho&aacute;c m&agrave;u v&agrave;ng ch&oacute;i mắt cực k&igrave; tương phản với thế giới k&igrave; quặc v&agrave; u &aacute;m trong game. Trong thế giới m&agrave;n h&igrave;nh ngang 3D của Little Nightmares, Six phải vượt qua một loạt những căn ph&ograve;ng&nbsp;kh&aacute;c nhau được dựng n&ecirc;n bởi những linh hồn lạc lối để đi đến c&aacute;i đ&iacute;ch cuối c&ugrave;ng một c&aacute;ch an to&agrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-8.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-8.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Little Nightmares mang mang phong c&aacute;ch nghệ thuật&nbsp;ri&ecirc;ng trong thiết kế của m&igrave;nh nhưng cũng đồng thời cực k&igrave; nghệ thuật. Một ch&uacute;t phong c&aacute;ch của Tim Burton pha trộn với những h&igrave;nh ảnh mập mờ trong bối cảnh tranh tối tranh s&aacute;ng mang đến một ch&uacute;t cảm gi&aacute;c kh&oacute; thở v&agrave; bất an cho game thủ trong qu&aacute; tr&igrave;nh chơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Little Nightmares ho&agrave;n to&agrave;n kh&ocirc;ng&nbsp;lạm dụng jump scare như nhiều tựa game kinh dị kh&aacute;c hiện nay m&agrave; dựa nhiều v&agrave;o c&aacute;ch tạo ra bầu kh&ocirc;ng kh&iacute; căng thẳng, hồi hộp với những thứ m&agrave; m&igrave;nh chưa biết, như liệu trong khoảng trống trước mặt c&oacute; c&aacute;nh tay ma n&agrave;o đ&oacute; đang chực chờ t&oacute;m lấy m&igrave;nh hay kh&ocirc;ng? Bạn chỉ muốn trốn tho&aacute;t khỏi thế giới trong game c&agrave;ng nhanh c&agrave;ng tốt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-6.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-6.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhưng kẻ th&ugrave; của bạn trong game l&agrave; những sinh vật h&igrave;nh người nhưng to lớn hơn bạn đến chục lần, v&agrave; bạn kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o tr&aacute;nh được ch&uacute;ng n&ecirc;n tốt hết l&agrave; cứ tr&aacute;nh đi th&ocirc;i. Đ&oacute; l&agrave; tất cả những nổi sợ được hiện thực ho&aacute; để trừng phạt bất cứ khi n&agrave;o ch&uacute;ng ph&aacute;t hiện được bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-5.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-5.jpg\" /></a><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-3.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-3.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần &acirc;m thanh trong game cũng l&agrave; một bước tiến đồng h&agrave;nh với đồ hoạ, với những sinh vật k&ecirc;u g&agrave;o đi&ecirc;n cuồng khiến bạn cảm thấy ph&aacute;t bệnh. &Acirc;m nhạc trở n&ecirc;n nhẹ nh&agrave;ng một c&aacute;ch ma qu&aacute;i trong những trường đoạn l&eacute;n l&uacute;t rồi đột nhi&ecirc;n trở n&ecirc;n căng thẳng khi bạn bị ph&aacute;t hiện như k&iacute;ch th&iacute;ch từng d&acirc;y thần kinh của nỗi sợ trong người của bạn vậy.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-1.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-1.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Little Nightmares c&oacute; thể khiến người ta gợi nhớ nhiề đến c&aacute;c tựa game tương tự l&agrave;&nbsp;Limbo&nbsp;v&agrave; Inside. Cũng lối chơi đi cảnh giải đố, cũng phong c&aacute;ch đồ hoạ độc nhất, sự vận dụng b&oacute;ng tối v&agrave; cắt bỏ ho&agrave;n to&agrave;n phần lời thoại để tập trung v&agrave;o gameplay. N&ecirc;n đ&acirc;y sẽ l&agrave; lựa chọn kh&ocirc;ng thể bỏ qua nếu bạn đ&atilde; từng m&ecirc; mẩn 2 tựa game ở tr&ecirc;n.</span></p>', '<p>OS: Windows 7 64-bit</p>\r\n\r\n<p>CPU: Intel Core i3-2115C 2.0 GHz or AMD Athlon II X3 460 3.4 GHz</p>\r\n\r\n<p>RAM: 6 GB System Memory</p>\r\n\r\n<p>GPU VRAM: 1.5 GB Video Memory</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 480 or AMD Radeon R7 265</p>\r\n\r\n<p>DX: DirectX 11</p>', '<p>OS: Windows 7 64-bit</p>\r\n\r\n<p>CPU: Intel Core i3-2115C 2.0 GHz or AMD Athlon II X3 460 3.4 GHz</p>\r\n\r\n<p>RAM: 6 GB System Memory</p>\r\n\r\n<p>GPU VRAM: 1.5 GB Video Memory</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 480 or AMD Radeon R7 265</p>\r\n\r\n<p>DX: DirectX 11</p>', 'Cài đặt và chơi', '1520172192maxresdefault (1).jpg', 'https://www.youtube.com/embed/aOadxZBsPiA', 2017, '2018-03-08'),
(7, 4, 'The Sims 4', 'the-sims-4', '<h3 style=\"text-align:justify\">D&ograve;ng game The Sim của EA từ l&uacute;c ra mắt phi&ecirc;n bản đầu ti&ecirc;n đến nay đ&atilde; trở th&agrave;nh một hiện tượng v&agrave; tượng đ&agrave;i vững chắc của thể loại&nbsp; game m&ocirc; phỏng. Kh&ocirc;ng c&oacute; tựa game n&agrave;o trong gần một thập kỉ c&oacute; thể thay thế vị tr&iacute; của seri The Sim trong mắt của game thủ. Sau gần 5 năm thống trị v&agrave; l&agrave;m mửa l&agrave;m gi&oacute; với phi&ecirc;n bản The Sim 3 k&egrave;m h&agrave;ng chục DLC đi k&egrave;m, EA đ&atilde; quyết định khởi động lại d&ograve;ng game n&agrave;y với phi&ecirc;n bản The Sim 4.&nbsp;</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau thời gian thống trị thị trường game m&ocirc; phỏng m&agrave; gần như kh&ocirc;ng một h&atilde;ng game hay tựa game n&agrave;o c&oacute; thể hất đổ được vị tr&iacute; ho&agrave;ng kim của The Sims 3, EA cảm thấy đ&atilde; đến l&uacute;c tạo n&ecirc;n một vị &ldquo;vua&rdquo; mới đễ thay thế tựa game 5 năm tuổi của m&igrave;nh, với sự hứa hẹn sẽ l&agrave; cuộc đột ph&aacute; về lối chơi v&agrave; những cải tiến đ&aacute;ng gi&aacute; hấp dẫn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trong The Sims 4, vẫn như mọi khi, người chơi sẽ được điều khiển một nh&acirc;n vật Sim v&agrave; ho&agrave;n to&agrave;n x&acirc;y dựng kiểm so&aacute;t cuộc sống của anh ta hoặc c&ocirc; ta. Khả năng c&aacute; nh&acirc;n h&oacute;a nh&acirc;n vật của game cực k&igrave; đa dạng khi người chơi c&oacute; rất nhiều những lựa chọn để tạo ra một nh&acirc;n vật Sim m&agrave; họ muốn, từ v&oacute;c d&aacute;ng, kiểu c&aacute;ch, c&acirc;n nặng, c&aacute; t&iacute;nh, t&oacute;c tai, gi&agrave;y d&eacute;p, quần &aacute;o v&agrave; đặc biệt l&agrave; to&agrave;n bộ khu&ocirc;n mặt của nh&acirc;n vật. Ở The Sims 4, hệ thống tạo dựng khu&ocirc;n mặt của game được n&acirc;ng cấp th&ecirc;m rất nhiều, người chơi ho&agrave;n to&agrave;n c&oacute; thể tạo ra những khu&ocirc;n mặt c&oacute; thật nếu kh&eacute;o tay v&agrave; tinh &yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"The-Sims-4 1\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/15\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Khả năng tương t&aacute;c của game cũng được tinh chỉnh, bạn c&oacute; thể chia sẻ nh&acirc;n vật v&agrave; cuộc sống của nh&acirc;n vật với những người chơi kh&aacute;c, tạo th&agrave;nh một x&atilde; hội ảo thu nhỏ cực k&igrave; th&uacute; vị. Bạn c&oacute; thể t&ugrave;y chỉnh t&iacute;nh c&aacute;ch, phong c&aacute;ch của Sim qua những loại quần &aacute;o c&oacute; sẵn hoặc tải th&ecirc;m từ trang chủ bởi v&igrave; ở phi&ecirc;n bản gốc, s&ocirc; lượng quần &aacute;o đồ đạc c&oacute; thể n&oacute;i l&agrave; c&oacute; phần hạn chế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chức năng tự x&acirc;y dựng nh&agrave; cửa cũng được giữ lại trong The Sims 4. Người chơi c&oacute; thể tự x&acirc;y dựng ng&ocirc;i nh&agrave; th&acirc;n y&ecirc;u của m&igrave;nh từ gốc đến rễ theo sở th&iacute;ch hoặc mua lại những căn hộ c&oacute; sẵn v&agrave; tự cải tiến dần th&ecirc;m. Khả năng chỉnh sửa n&acirc;ng cấp trong phi&ecirc;n bản gốc c&oacute; thể n&oacute;i chỉ dừng ở mức tạm chấp nhận được nếu như so với những g&igrave; người chơi c&oacute; thể l&agrave;m ở The Sims 3. Tuy nhi&ecirc;n, điều n&agrave;y cũng kh&ocirc;ng hẳn l&agrave; điểm yếu của game bởi v&igrave; như bạn đ&atilde; biết đấy, EA nổi tiếng với&nbsp;<a href=\"https://guidegame.vn/champions/vi\" title=\"vi\">vi</a>ệc &ldquo;h&uacute;t m&aacute;u&rdquo; người h&acirc;m mộ qua những bản DLC bổ sung cơ m&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Sims4-057\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/13\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Một trong những thay đổi dễ nhận ra nhất, đ&oacute; ch&iacute;nh l&agrave; nh&agrave; sản xuất đ&atilde; ch&uacute; &yacute; hơn v&agrave;o những xu hướng x&atilde; hội mới mẻ của cuộc sống thật v&agrave; mang v&agrave;o game một c&aacute;ch rất th&uacute; vị. Những h&agrave;nh động như chụp ảnh tự sướng, cập nhật trạng th&aacute;i cuộc sống tr&ecirc;n những trang mạng x&atilde; hội Sim Social, d&ugrave;ng phần mềm chỉnh sửa h&igrave;nh ảnh để bức h&igrave;nh lung linh hay thậm ch&iacute; l&agrave; tự khởi nghiệp th&ocirc;ng qua những m&ocirc; h&igrave;nh hoạt động nh&oacute;m. Những yếu tố n&agrave;y khiến game trở n&ecirc;n sống động v&agrave; ch&acirc;n thật hơn những phiển bản tiền nhiệm rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Một cải tiến đ&aacute;ng khen ngợi nhất, đ&oacute; l&agrave; nh&acirc;n vật Sim của người chơi trong The Sims 4 c&oacute; thể thực hiện nhiều h&agrave;nh động c&ugrave;ng một l&uacute;c chứ kh&ocirc;ng cần phải dừng h&agrave;nh động n&agrave;y để l&agrave;m việc kh&aacute;c hay chờ đợi nữa. C&ograve;n g&igrave; tuyệt vời hơn việc vừa đọc s&aacute;ch vừa ăn s&aacute;ng hay vừa nghe nhạc vừa trồng c&acirc;y. Đặc biệt nhất l&agrave; những cuộc tr&ograve; chuyện giờ đ&acirc;y đ&atilde; kh&ocirc;ng c&ograve;n bị giới hạn chỉ 2 người với nhau m&agrave; c&oacute; thể từ 3 đến 5 người c&ugrave;ng một l&uacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Sims4-4\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/10\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Nhưng chức năng m&agrave; EA tự h&agrave;o nhất về The Sims 4 kh&ocirc;ng phải l&agrave; khả năng tương t&aacute;c của nh&acirc;n vật đối với thế giới xung xanh, m&agrave; l&agrave; sự thay đổi nội t&acirc;m cảm x&uacute;c nh&acirc;n vật của Sim, thứ m&agrave; The Sims 3 kh&ocirc;ng hề c&oacute;. Mỗi một Sim, t&ugrave;y v&agrave;o những t&iacute;nh c&aacute;ch m&agrave; người chơi đ&atilde; chọn l&uacute;c bắt đầu game. Kh&ocirc;ng những thế, những yếu tố b&ecirc;n ngo&agrave;i cũng sẽ t&aacute;c động rất nhiều l&ecirc;n cảm x&uacute;c của nh&acirc;n vật. Nếu như nh&acirc;n vật Sim vừa mới bị mất việc, anh ta hoặc c&ocirc; ta sẽ buồn ch&aacute;n v&agrave; l&agrave;m hỏng những h&agrave;nh động sau đ&oacute;, hoặc l&uacute;c Sim vừa mới &ldquo;cưa&rdquo; được người bạn t&igrave;nh y&ecirc;u dấu l&acirc;u năm của m&igrave;nh, Sim đ&oacute; sẽ rất hưng phấn v&agrave; vui vẻ, khiến những việc li&ecirc;n quan đến cảm x&uacute;c n&agrave;y cũng được hưởng l&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuy c&oacute; nhiều cải tiến l&agrave; v&acirc;y nhưng kh&ocirc;ng phải l&agrave; game kh&ocirc;ng c&oacute; khuyết điểm. Điều đầu ti&ecirc;n dễ nhận ra nhất, đ&oacute; l&agrave; kh&ocirc;ng gian hoạt động của tr&ograve; chơi bị b&oacute;p lại một c&aacute;ch rất khủng khiếp. Kh&ocirc;ng c&ograve;n những th&agrave;nh phố rộng m&ecirc;nh m&ocirc;ng với đủ thứ để kh&aacute;m ph&aacute;, giờ đ&acirc;y trong The Sims 4, tất cả những g&igrave; bạn c&ograve;n lại khi bước ra khỏi nh&agrave; chỉ l&agrave; một khu phố nhỏ với những ng&ocirc;i nh&agrave; v&agrave; khu vực tr&ocirc;ng na n&aacute; nhau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"The-Sims-4 2\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/12\" style=\"height:339px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Thậm ch&iacute; với bản đồ nhỏ l&agrave; vậy, mỗi khi đi đến một khu vực n&agrave;o đ&oacute;, người chơi sẽ phải nh&igrave;n m&agrave;n h&igrave;nh loading li&ecirc;n tục đến ph&aacute;t mệt m&otilde;i, cho d&ugrave; chỉ l&agrave; đi đến nh&agrave; của anh h&agrave;ng x&oacute;m n&agrave;o đ&oacute; ngay cạnh nh&agrave; của m&igrave;nh. Nếu như so s&aacute;nh với những g&igrave; m&agrave; The Sims 3 đ&atilde; l&agrave;m được với thế giới mở của n&oacute;, thật sự v&agrave;o thời điểm n&agrave;y, The Sims 4 đ&atilde; kh&ocirc;ng thể s&aacute;nh được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Khi th&agrave;nh phố bị r&uacute;t ngắn như vậy, một v&agrave;i t&iacute;nh năng nho nhỏ cũng bị cắt x&eacute;n bớt đi, một trong số đ&oacute; l&agrave; việc sử dụng phương tiện di chuyển, hay n&oacute;i c&aacute;ch kh&aacute;c, đ&oacute; l&agrave; bạn kh&ocirc;ng c&oacute; cơ hội để tự sở hữu một chiếc xế y&ecirc;u cho ri&ecirc;ng m&igrave;nh nữa. C&ograve;n đ&acirc;u cảm gi&aacute;c l&acirc;ng l&acirc;ng kh&oacute; tả khi tiết kiệm một đống tiền để chạm tay v&agrave;o chiếc xe y&ecirc;u th&iacute;ch, c&ugrave;ng n&oacute; dạo quanh những h&agrave;ng c&acirc;y dọc bờ biển v&agrave; đi &ldquo;l&ograve;e&rdquo; với bạn g&aacute;i của m&igrave;nh. Đ&acirc;y l&agrave; một điểm trừ lớn khi n&oacute; khiến người chơi mất đi một niềm vui nhỏ b&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Sims4-3\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/11\" style=\"height:315px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Hệ thống điều khiển v&agrave; giao diện h&igrave;nh ảnh của game cũng khiến đa số game thủ, đặc biệt l&agrave; những fan trung th&agrave;nh l&acirc;u năm của d&ograve;ng game The Sims, bất b&igrave;nh v&agrave; phản ứng gay gắt. Giờ đ&acirc;y, người chơi sẽ phải l&agrave;m quen với một giao diện mi&ecirc;u tả trạng th&aacute;i ho&agrave;n to&agrave;n mới v&agrave; rất phức tạp. Nếu bạn muốn biết th&ocirc;ng tin về điều g&igrave; đ&oacute;, người chơi sẽ phải chọn v&agrave;o nhiều mục v&agrave; thanh trạng th&aacute;i kh&aacute;c nhau để đến nơi m&igrave;nh cần t&igrave;m, trong khi ở The Sims 3, tất cả đều nằm r&otilde; r&agrave;ng dưới m&agrave;n h&igrave;nh của game.</span></p>\r\n\r\n<h4 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&hellip; HỨA HẸN TRONG TƯƠNG LAI.</strong></span></h4>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"the-sims-4-early-access-download\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/16\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đồ họa v&agrave; &acirc;m thanh của game c&oacute; một bước thay đổi lớn so với The Sims 3. Vẫn l&agrave; phong c&aacute;ch đồ họa rực rỡ đầy m&aacute;u sắc tươi vui nhưng The Sims 4 c&ograve;n thể hiện th&ecirc;m những khung h&igrave;nh v&agrave; h&agrave;nh động h&agrave;i h&ograve;a hơn, đầy m&agrave;u sắc v&agrave; dịu d&agrave;ng. &Acirc;m nhạc của game kh&ocirc;ng c&oacute; g&igrave; thay đổi r&otilde; n&eacute;t, khi vẫn l&agrave; những ng&ocirc;n ngữ Sims đặc trưng, &acirc;m thanh reo rắt nh&iacute; nhảnh v&agrave; nhạc nền dịu d&agrave;ng, nh&igrave;n chung cả 2 đều đ&atilde; ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuy kh&ocirc;ng c&oacute; qu&aacute; nhiều điểm nổi bật cũng như xuất hiện nhiều lỗi v&agrave; chỉnh sửa kh&ocirc;ng hợp l&yacute;, nhưng The Sims 4 vẫn c&ograve;n rất nhiều điều để mong đợi bởi v&igrave; EA vẫn chưa tung ra những DLC, thứ thậm ch&iacute; c&ograve;n cốt l&otilde;i v&agrave; quan trọng hơn. Nếu bạn tr&ocirc;ng chờ một sự đột ph&aacute; về lối chơi, GuideGame sẽ đề nghị bạn tạm thời chọn The Sims 3 v&agrave;o l&uacute;c n&agrave;y, c&ograve;n nếu bạn muốn cảm nhận những t&iacute;nh năng mới mẻ, h&atilde;y thử qua The Sims 4 nh&eacute;.</span></p>', '<p>CPU: Intel Core 2 Duo E4300 or AMD Athlon 64 X2 4000+ (2.0 GHz Dual Core required if using integrated graphics)</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 2 GB</p>\r\n\r\n<p>OS: Windows XP</p>\r\n\r\n<p>VIDEO CARD: NVIDIA GeForce 6600 or ATI Radeon X1300 or Intel GMA X4500</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 10 GB</p>', '<p>CPU: Intel Core 2 Duo E4300 or AMD Athlon 64 X2 4000+ (2.0 GHz Dual Core required if using integrated graphics)</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 2 GB</p>\r\n\r\n<p>OS: Windows XP</p>\r\n\r\n<p>VIDEO CARD: NVIDIA GeForce 6600 or ATI Radeon X1300 or Intel GMA X4500</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 10 GB</p>', 'Cài đặt và chơi', '1520188708DVjGLWzVwAA5dlp.jpg', 'https://www.youtube.com/embed/z00mK3Pxc8w', 2014, '2018-03-08');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(8, 3, 'Need For Speed: Payback', 'need-for-speed:-payback', '<h3 style=\"text-align:justify\">Need for Speed đ&atilde; trở lại sau 1 năm vắng b&oacute;ng kể từ phi&ecirc;n bản 2015. Tiếp tục được ph&aacute;t triển bởi Ghost Games, phi&ecirc;n bản mang t&ecirc;n&nbsp;Need for Speed Payback&nbsp;lần n&agrave;y quyết định đi theo phong c&aacute;ch thi&ecirc;n về h&agrave;nh động với những pha takedown v&agrave; những trường đoạn đậm chất Fast &amp; Furious.</h3>', '<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Một cốt truyện mới khi c&oacute; tới 3 nh&acirc;n vật ch&iacute;nh, thế giới mở rộng lớn v&agrave; hơn 80 mẫu xe đua đủ thể loại sẽ xuất hiện trong Need for Speed Payback.</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Need for Speed Payback</strong> sẽ l&agrave; tựa game đua xe đ&aacute;ng ch&uacute; &yacute; nhất trong th&aacute;ng 11 n&agrave;y, với một cốt truyện mới v&agrave; c&aacute;c t&iacute;nh năng độc đ&aacute;o lần đầu ti&ecirc;n được đưa v&agrave;o seri Need for Speed. C&aacute;c fan h&acirc;m mộ gạo cội lẫn l&iacute;nh mới chưa từng chơi thử Need for Speed cũng sẽ c&oacute; c&aacute;c trải nghiệm th&uacute; vị như nhau, đ&oacute; l&agrave; lời hứa của nh&agrave; ph&aacute;t h&agrave;nh Need for Speed Payback, vậy h&atilde;y c&ugrave;ng Mọt game điểm qua c&aacute;c điểm độc đ&aacute;o ch&iacute;nh của game trước khi n&oacute; ra mắt nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-payback-2-e1509441902712.jpg\" style=\"height:338px; width:600px\" /></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Cốt truyện mới </strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đ&acirc;y l&agrave; thay đổi khiến nhiều fan h&acirc;m mộ mong chờ nhất, khi Need for Speed Payback th&ocirc;ng b&aacute;o sẽ cải tiến cốt truyện của m&igrave;nh theo hướng phim ảnh với 3 nh&acirc;n vật ch&iacute;nh kh&aacute;c nhau. Xoay quanh 3 nh&acirc;n vật ch&iacute;nh l&agrave; Tyler Morgan, Jessica Miller v&agrave; Sean McAlister trong một phi vụ trả th&ugrave; băng nh&oacute;m thế giới ngầm. Phần chơi đơn của Need for Speed Payback sẽ xoay quanh li&ecirc;n tục giữa 3 người n&agrave;y, với những t&igrave;nh tiết đan xen lẫn nhau với thủ lĩnh l&agrave; Tyler Morgan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Mỗi nh&acirc;n vật trong Need for Speed Payback sẽ đ&oacute;ng c&aacute;c vai tr&ograve; ri&ecirc;ng trong cốt truyện, như Tyler l&agrave; một tay đua đường phố kh&eacute;t tiếng, Jessica nhận nhiệm vụ như một &ldquo;người giao h&agrave;ng&rdquo; bất hợp ph&aacute;p v&agrave; Sean th&igrave; lại y&ecirc;u th&iacute;ch c&aacute;c cuộc rượt đuổi c&ugrave;ng cảnh s&aacute;t. Cốt truyện của Need for Speed Payback n&oacute;i về sự xung đột băng đảng v&agrave; phản bội giữa c&aacute;c th&agrave;nh vi&ecirc;n với nhau, thế n&ecirc;n n&oacute; mang d&aacute;ng dấp giống một bộ phim nhiều hơn c&aacute;c phi&ecirc;n bản trước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-e1509441005116.jpg\" style=\"height:337px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><em>Tyler Morgan, một trong ba nh&acirc;n vật ch&iacute;nh</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">C&aacute;c fan sẽ rất h&agrave;i l&ograve;ng khi biết rằng giờ đ&acirc;y Need for Speed Payback sẽ thay đổi theo hệ thống ng&agrave;y đ&ecirc;m, với c&aacute;c nhiệm vụ v&agrave; t&igrave;nh tiết mới dựa theo thời gian trong game. Hơn nữa với việc c&oacute; tới 3 nh&acirc;n vật ch&iacute;nh, phần chơi đơn c&oacute; thể được ho&agrave;n th&agrave;nh c&ugrave;ng những đồng đội kh&aacute;c khi chơi multiplayer.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Thế giới mở rộng lớn</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-payback-1-e1509441939168.jpg\" style=\"height:338px; width:600px\" /></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Bối cảnh của Need for Speed Payback diễn ra tại một khu d&acirc;n cư c&oacute; t&ecirc;n l&agrave; Fortune Valley, ngo&agrave;i c&aacute;c khu phố đ&ocirc;ng đ&uacute;c l&agrave; nơi đ&oacute;ng qu&acirc;n của băng đảng The House, th&igrave; bản đồ c&ograve;n chia ra l&agrave;m 4 khu vực ri&ecirc;ng biệt l&agrave; v&ugrave;ng cao nguy&ecirc;n Providence, sa mạc Liberty, d&atilde;y n&uacute;i Silver v&agrave; thung lũng Silver. Mỗi khu vực của Fortune Valley sẽ c&oacute; từ 5 tới 6 g&oacute;c nhỏ ri&ecirc;ng biệt, với c&aacute;c nhiệm vụ phụ, trophy v&agrave; tuyến nh&acirc;n vật phụ để người chơi kh&aacute;m ph&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><a href=\"https://motgame.vn/need-speed-payback-se-co-cot-truyen-giong-nhu-fast-furious.game\" target=\"_blank\"><img alt=\"\" src=\"https://media.motgame.vn/2017/10/vuhoang/needforspeedpayback-2-1-368x207.jpg\" style=\"height:207px; width:368px\" /></a></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&ocirc;ng chỉ l&agrave; những m&agrave;n đua xe v&agrave; độ xế quen thuộc, Need for Speed Payback sẽ c&oacute; một cốt truyện đậm m&ugrave;i Fast &amp; Furious để người chơi trải nghiệm.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Cả v&ugrave;ng đất Fortune Valley sẽ l&agrave; một trường đua khổng lồ, giờ đ&acirc;y người chơi sẽ c&oacute; thể l&aacute;i xe khắp nơi v&agrave; thu thập c&aacute;c điểm reputation để tăng độ nổi tiếng cho m&igrave;nh, hoặc thử th&aacute;ch những nhiệm vụ &ldquo;SpeedList&rdquo; độc đ&aacute;o. Tại Fortune Valley th&igrave; ngo&agrave;i The House l&agrave; băng đảng nắm quyền ch&iacute;nh ra th&igrave; vẫn c&ograve;n rất nhiều nh&oacute;m nhỏ hoạt động độc lập, bọn ch&uacute;ng sẽ th&aacute;ch thức người chơi th&ocirc;ng qua c&aacute;c &ldquo;SpeedList&rdquo; ri&ecirc;ng biệt, c&aacute;c nhiệm vụ n&agrave;y kh&oacute; khăn hơn v&agrave; chia theo từng cấp từ dễ đến kh&oacute;, đủ để cho người chơi bận rộn với game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/nfsp-world-game-map-1.jpg\" style=\"height:497px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><em>Bản đồ Fortune Valley</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ngo&agrave;i việc đua xe v&agrave; thử th&aacute;ch bản th&acirc;n với c&aacute;c &ldquo;SpeedList&rdquo;, người chơi cũng sẽ đi thu thập nguy&ecirc;n liệu để độ xe rải r&aacute;c khắp nơi tr&ecirc;n bản đồ Fortune Valley. C&oacute; thể n&oacute;i Need for Speed Payback đ&atilde; x&acirc;y dựng bối cảnh của m&igrave;nh ho&agrave;n to&agrave;n theo kiểu thế giới mở, tự do hơn v&agrave; c&oacute; phần kh&aacute; giống với GTA. Một kh&aacute;i niệm mới đ&atilde; được đưa v&agrave;o gọi l&agrave; Heat Level, khi bạn để chỉ số n&agrave;y l&ecirc;n qu&aacute; cao v&igrave; đi sinh sự, hoặc đua xe bạt mạng giữa đường phố th&igrave; cảnh s&aacute;t sẽ v&agrave;o cuộc để thăm hỏi nh&acirc;n vật. Nh&igrave;n chung khi Need for Speed Payback đ&atilde; thay đổi về cốt truyện, th&igrave; việc đổi mới cả về bối cảnh cũng l&agrave; một bước đi hợp l&yacute;.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>5 loại xe với t&iacute;nh năng kh&aacute;c nhau</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-4-e1509441014849.jpg\" style=\"height:337px; width:600px\" /></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Với một game đua xe th&igrave; sự đa dạng trong c&aacute;c mẫu m&atilde; &ldquo;xế độ&rdquo; lu&ocirc;n l&agrave; thứ thu h&uacute;t người chơi, số lượng xe trong Need for Speed Payback hiện tại đang l&agrave; hơn 80 mẫu. Một điểm đổi mới của game l&agrave; giờ đ&acirc;y những mẫu xe n&agrave;y sẽ được ra l&agrave;m những &ldquo;class&rdquo; ri&ecirc;ng biệt, phục vụ theo từng loại nhiệm vụ theo cốt truyện, tổng cộng c&oacute; 5 loại xe kh&aacute;c nhau:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Race: </strong>Mẫu xe thường d&ugrave;ng v&agrave; chiếm số lượng lớn nhất trong Need for Speed Payback, Race l&agrave; c&aacute;c loại xe đua th&ocirc;ng thường m&agrave; ch&uacute;ng ta c&oacute; thể gặp thường xuy&ecirc;n tr&ecirc;n đường. C&acirc;n bằng, đẹp đẽ v&agrave; phổ biến l&agrave; những g&igrave; để m&ocirc; tả về lớp n&agrave;y, tất nhi&ecirc;n l&agrave; ở c&aacute;c đoạn đường bằng phẳng Race sẽ l&agrave; vua, nhưng trong Need for Speed Payback đ&acirc;u chỉ c&oacute; mỗi đường nhựa th&ocirc;ng thường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Off-Road: </strong>D&ugrave;ng cho những chặng đường gồ ghề, đất đ&aacute; m&agrave; xe đua b&igrave;nh thường kh&ocirc;ng thể đi nổi. C&aacute;c loại xe thuộc lớp Off-road c&oacute; thể giữ vững tốc độ kể cả khi đi trong c&aacute;c đoạn đường sa mạc, cũng như khả năng giữ tay l&aacute;i cho người chơi tr&ecirc;n c&aacute;c đoạn đường kh&oacute; nhằn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-2-e1509440996536.jpg\" style=\"height:337px; width:600px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><em>Off-Road</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Drift: </strong>Bạn y&ecirc;u th&iacute;ch những c&uacute; bo cua nảy lửa, hoặc quay đầu đến ch&aacute;y cả lốp tr&ecirc;n những đoạn đường hẹp&hellip; vậy th&igrave; Drift chắc chắn sẽ l&agrave; d&ograve;ng xe d&agrave;nh cho bạn. Ở những thử th&aacute;ch đường n&uacute;i với những kh&uacute;c cua li&ecirc;n tục th&igrave; bạn sẽ cần tới Drift, v&igrave; sức mạnh v&ocirc; địch của n&oacute; trong những lần &ldquo;lết b&aacute;nh&rdquo; với tốc độ đ&aacute;ng kinh ngạc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Drag: </strong>Kh&ocirc;ng cần phanh, kh&ocirc;ng cần thắt d&acirc;y an to&agrave;n v&agrave; kh&ocirc;ng cần bất cứ phụ t&ugrave;ng g&igrave; đi k&egrave;m. Tốc độ ch&iacute;nh l&agrave; thứ m&agrave; những tay đua Drag hướng tới, loại xe n&agrave;y được thiết kế với những ống đẩy phản lực như rocket ph&iacute;a sau gi&uacute;p xe đạt tốc độ tối đa tr&ecirc;n c&aacute;c đoạn đường thẳng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Runner: </strong>Nếu như Race l&agrave; mẫu xe đua để bạn chạy tr&ecirc;n đường nhựa, th&igrave; Runner l&agrave; phi&ecirc;n bản n&acirc;ng cấp của n&oacute; với gầm xe thấp c&ugrave;ng kich thước v&ocirc; c&ugrave;ng khi&ecirc;m tốn, cực kỳ th&iacute;ch hợp để cắt đu&ocirc;i khi bị cảnh s&aacute;t truy đuổi. Runner d&agrave;nh cho những ai th&iacute;ch ph&aacute; luật, khi n&oacute; c&oacute; thể luồn l&aacute;ch khắp c&aacute;c con ng&otilde; nhỏ hoặc chạy trối chết khi bị số đ&ocirc;ng cảnh s&aacute;t truy n&atilde;.</span></p>', '<p>OS: 64-bit Windows 7 or later</p>\r\n\r\n<p>CPU: Intel i3 6300 @ 3.8GHz or AMD FX 8150 @ 3.6GHz with 4 hardware threads</p>\r\n\r\n<p>RAM: 6GB</p>\r\n\r\n<p>Disc Drive: DVD ROM drive required for installation only Hard Drive: 30GB</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 750 Ti, AMD Radeon HD 7850, or equivalent DX11 compatible GPU with 2GB of memory</p>\r\n\r\n<p>DirectX: 11-compatible video card or equivalent</p>', '<p>OS: 64-bit Windows 7 or later</p>\r\n\r\n<p>CPU: Intel i3 6300 @ 3.8GHz or AMD FX 8150 @ 3.6GHz with 4 hardware threads</p>\r\n\r\n<p>RAM: 6GB</p>\r\n\r\n<p>Disc Drive: DVD ROM drive required for installation only Hard Drive: 30GB</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 750 Ti, AMD Radeon HD 7850, or equivalent DX11 compatible GPU with 2GB of memory</p>\r\n\r\n<p>DirectX: 11-compatible video card or equivalent</p>', 'Cài đặt và chơi', '15201894440027_Gamescom_stills_sc01_sh060_blockbuster_v001.jpg', 'https://www.youtube.com/embed/K-5EdHZ0hBs', 2017, '2018-03-08'),
(9, 1, 'Far Cry Primal', 'far-cry-primal', '<h3 style=\"text-align:justify\">Far Cry thường đặt bạn ở r&igrave;a của thế giới được biết đến, trong một bi&ecirc;n giới xinh đẹp, v&ocirc; luật v&agrave; man rợ. The Stone Age, trong một c&aacute;ch, bi&ecirc;n giới đầu ti&ecirc;n cho lo&agrave;i người; đ&oacute; l&agrave; thời gian khi con người đặt một c&acirc;y gậy xuống đất v&agrave; tuy&ecirc;n bố mặt đất cho ri&ecirc;ng m&igrave;nh, thời gian khi ch&uacute;ng ta bắt đầu leo l&ecirc;n c&aacute;c chuỗi thức ăn. Điều đ&oacute; đến với c&aacute;c cuộc xung đột, kh&ocirc;ng chỉ chống lại những con người kh&aacute;c của lo&agrave;i người, m&agrave; c&ograve;n chống lại thi&ecirc;n nhi&ecirc;n.</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Far Cry Primal đ&atilde; l&agrave;m rất tốt đ&aacute;c đặc điểm vốn c&oacute; trước đ&acirc;y của d&ograve;ng game, song lại kh&ocirc;ng mang đến nhiều cải tiến đ&aacute;ng gi&aacute;.</strong><br />\r\n<br />\r\nUbisoft đ&atilde; thật sự dung cảm khi đưa d&ograve;ng game Far Cry về một thời đại m&agrave; một số gi&aacute; trị cốt l&otilde;i của d&ograve;ng game n&agrave;y sẽ kh&ocirc;ng thể xuất hiện được. Từ trước đến nay, Far Cry lu&ocirc;n l&agrave; tựa game li&ecirc;n quan tới giải cứu những c&aacute; nh&acirc;n, những cộng đồng bị ch&agrave; đạp khỏi những t&ecirc;n bạo ch&uacute;a bạo ngược- bằng c&aacute;ch d&ugrave;ng đủ mọi loại s&uacute;ng v&agrave; thuốc nổ để thổi bay bất cứ thứ g&igrave; tr&ecirc;n đường đi.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-1(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Ấy thế m&agrave; bạn sẽ kh&ocirc;ng thể t&igrave;m thấy những điều đ&oacute; trong phi&ecirc;n bản mới nhất n&agrave;y. Far Cry Primal sẽ đưa bạn trở lại thời kỳ 10000 năm trước c&ocirc;ng nguy&ecirc;n, v&agrave;o thời đại đồ đ&aacute; giữa, sau kỷ băng h&agrave; cuối c&ugrave;ng, thời điểm m&agrave; thuốc s&uacute;ng, trồng trọt v&agrave; khai th&aacute;c chưa hề được ph&aacute;t minh ra. Bạn sẽ v&agrave;o vai Takkar, một th&agrave;nh vi&ecirc;n của bộ tộc Wenja. Trong một lần săn voi mamut, Takkar v&agrave; c&aacute;c chiến hữu (đ&atilde; đ&oacute;i lả sau nhiều ng&agrave;y) bất ngờ bị tấn c&ocirc;ng bỏi một con hổ răng kiếm hung dữ. Cuộc đi săn biến th&agrave;nh một cuộc thảm s&aacute;t đẫm m&aacute;u, nh&oacute;m thợ săn bị giết gần hết, chỉ c&ograve;n mỗi Takkar. Sau khi nghe lời trăn trối cuối c&ugrave;ng của người anh em, Takkar l&ecirc;n đường để ho&agrave;nh th&agrave;nh ước muốn của người đồng đội đ&oacute;: t&igrave;m ra v&ugrave;ng đất hứa Oros, v&agrave; những người c&ograve;n lại của bộ tộc Wenja đ&atilde; thất lạc từ l&acirc;u.<br />\r\n<br />\r\n<strong>Thế giới Oros thực sự tuyệt vời</strong><br />\r\n&nbsp;<br />\r\nThế nhưng, khi đặt ch&acirc;n tới Oros, Takkar mới ph&aacute;t hiện ra đ&oacute; chẳng phải l&agrave; thi&ecirc;n đường mật ngọt n&agrave;o cả. X&aacute;c chết người Wenja nằm rải r&aacute;c khắp nơi, số c&ograve;n lại bị bắt l&agrave;m n&ocirc; lệ cho 2 bộ tộc mạnh hơn kh&aacute;c: Udam &ndash; một bộ tộc man rợ chuy&ecirc;n ăn thịt người, sống ở v&ugrave;ng đất băng gi&aacute; ph&iacute;a Bắc, v&agrave; Izila &ndash; một bộ tộc cuồng t&iacute;n chuy&ecirc;n thi&ecirc;u sống t&ugrave; nh&acirc;n để c&uacute;ng tế cho c&aacute;c thần linh.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-2(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Nhiệm vụ của Takkar rất r&otilde; rang: tập hợp những người Wenja c&ograve;n sống s&oacute;t lại, g&acirc;y dựng lại bộ tộc v&agrave; chiến đấu với 2 kẻ th&ugrave; đ&aacute;ng sợ kia. Kh&ocirc;ng c&oacute; bất cứ n&uacute;t thắt hay chi tiết đặc biệt n&agrave;o trong c&acirc;u chuyện cả, kh&ocirc;ng c&oacute; bất cứ một t&ecirc;n độc t&agrave;i hay phản diện n&agrave;o cần phải loại bỏ.&nbsp; Nội dung của Far Cry Prinal cũng giống như c&aacute;ch m&agrave; ch&uacute;ng ta tưởng tượng về thời kỳ văn minh sơ khai của lo&agrave;i người, đ&oacute; ho&agrave;n to&agrave;n l&agrave; chiến đấu, giết ch&oacute;c để chiếm l&atilde;nh thổ.<br />\r\n<br />\r\nĐơn giản như vậy n&ecirc;n th&agrave;nh ra c&acirc;u chuyện cũng tương đối nh&agrave;m ch&aacute;n. C&oacute; lẽ nh&agrave; sản xuất đ&atilde; cố gắng cải thiện một ch&uacute;t bằng c&aacute;ch loại bỏ hệ thống nhiệm vụ tuyến t&iacute;nh, v&agrave; cho ph&eacute;p bạn thoải m&aacute;i đi theo những c&acirc;u chuyện của những người d&acirc;n thời tiền sử theo một thứ tự do ch&iacute;nh bạn đặt ra. Tuy nhi&ecirc;n, dấu ấn đạt được kh&aacute; mờ nhạt: kh&ocirc;ng c&oacute; khoảnh khắc đặc biệt đ&aacute;ng nhớ n&agrave;o, kh&ocirc;ng c&oacute; những miếng gh&eacute;p ho&agrave;n hảo để tạo n&ecirc;n một t&aacute;c phẩm liền mạch, thống nhất v&agrave; đủ hấp dẫn khiến bạn n&ocirc;n n&oacute;ng muốn xem kết th&uacute;c sẽ ra sao. Thực tế, chả ai trong Far Cry Primal ho&agrave;n to&agrave;n l&agrave; người tốt cả, đơn giản chỉ l&agrave; bạn c&oacute; 3 bộ tộc man rợ thời tiền sử đang tranh đấu một v&ugrave;ng đất m&agrave; kh&ocirc;ng thể n&agrave;o sống ho&agrave;n b&igrave;nh cạnh nhau được, chỉ c&oacute; vậy m&agrave; th&ocirc;i. C&oacute; thể 10000 năm trước đ&acirc;y người ta thực sự sống như vậy, nhưng t&ocirc;i vẫn ước rằng gi&aacute; m&agrave; 15 giờ tr&ocirc;i qua đ&oacute; c&oacute; thứ g&igrave; đ&aacute;ng nhớ v&agrave; suy ngẫm hơn th&igrave; tốt biết mấy.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-3(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">&nbsp;<br />\r\n<strong>C&agrave;ng thay đổi, c&agrave;ng như cũ</strong><br />\r\n<br />\r\nGameplay kh&ocirc;ng c&oacute; nhiều thay đổi cải tiến d&aacute;ng gi&aacute;. Đ&acirc;y vẫn ch&iacute;nh x&aacute;c l&agrave; một tựa game Far Cry, chỉ l&agrave; mọi thứ (ngoại trừ c&aacute;c đ&agrave;i ph&aacute;t thanh radio) &nbsp;m&agrave; bạn biết v&agrave; th&iacute;ch ở Far Cry c&aacute;c bản trước đ&atilde; thay đổi để th&iacute;ch ứng với một thế giới hoang d&atilde;.<br />\r\n<br />\r\nThay v&igrave; ống nh&ograve;m, giờ đ&acirc;y bạn sẽ ph&oacute;ng tầm mắt v&agrave; theo d&otilde;i thế giới bằng một con c&uacute;, mắt n&oacute; c&oacute; thể đ&aacute;nh dấu v&agrave; theo đu&ocirc;i c&aacute;c mục ti&ecirc;u cho bạn. Thay v&igrave; c&aacute;c chốt canh, nơi m&agrave; bạn cần phải hạ gục l&iacute;nh g&aacute;c để chiếm như c&aacute;c bản trước, th&igrave; giờ bạn c&oacute; những lều trại v&agrave; lửa trại người tiền sử phục vụ chức năng tương tự. Tương đương với c&aacute;c ph&aacute;o đ&agrave;i (Fortress) trong Far Cry 4, trong Far Cry Primal cũng xuất hiện những doanh trại quy m&ocirc; lớn hơn b&igrave;nh thường, nơi bạn sẽ phải d&ugrave;ng đến rất nhiều sự kh&ocirc;n ngoan, chuẩn x&aacute;c v&agrave; sức mạnh để chinh phục.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-4(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Tương tự như c&aacute;c bản trước, bạn cũng sẽ phải d&agrave;nh nhiều thời gian để săn động vật, lột da ch&uacute;ng, d&ugrave;ng da v&agrave; nội tạng để n&acirc;ng cấp vũ kh&iacute; v&agrave; ng&ocirc;i l&agrave;ng của m&igrave;nh. Tr&ecirc;n v&ugrave;ng dất Oros n&agrave;y, khi giải cứu những người d&acirc;n Wenja bị lạc v&agrave; đưa về l&agrave;ng của m&igrave;nh, bạn sẽ c&oacute; cơ hội mở kh&oacute;a th&ecirc;m những khả năng v&agrave; phần thưởng kh&aacute;c nữa. Như dự đo&aacute;n, bản đồ của game cũng d&agrave;y đặc với đủ mọi loại hoạt động v&agrave; nhiệm vụ, từ săn bắn, sưu tập, đến đ&agrave;o kho b&aacute;u. C&oacute; c&aacute;i th&igrave; cực kỳ th&uacute; vị v&agrave; cũng c&oacute; c&aacute;i nh&agrave;m ch&aacute;n đến ph&aacute;t nản.<br />\r\n<br />\r\n<strong>Trước d&ugrave;ng s&uacute;ng, nay d&ugrave;ng dao</strong><br />\r\n<br />\r\nThay đổi lớn nhất đương nhi&ecirc;n l&agrave; vũ kh&iacute;. Giờ đ&acirc;y bạn sẽ phải l&agrave;m quen với những vũ kh&iacute; cận chiến như ch&ugrave;y hay gi&aacute;o m&aacute;c hay thậm ch&iacute; cả n&aacute; nữa. Cung t&ecirc;n sẽ l&agrave; vũ kh&iacute; tầm xa chủ đạo, v&agrave; to&agrave;n bộ vũ kh&iacute; bạn c&oacute; đều c&oacute; thể n&acirc;ng cấp được. Tr&ecirc;n bản đồ bạn thi thoảng sẽ t&igrave;m thấy những người d&acirc;n c&oacute; kỹ năng đặc biệt (specialist), đưa họ về sẽ gi&uacute;p mở ra nh&aacute;nh kỹ năng hay n&acirc;ng cấp. Gi&aacute;o c&ugrave;n ban đầu sẽ được thay thế bằng những ngọn gi&aacute;o nhọn v&agrave; cứng hơn, cung t&ecirc;n bắn xa hơn, v&agrave; dần dần, Far Cry Primal c&oacute; cảm gi&aacute;c như một game sinh tồn thực thụ vậy.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-5(2).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Bạn cũng sẽ thu thập item để chế vũ kh&iacute; đạn được, chế tạo mũi t&ecirc;n tr&ecirc;n đường đi, hay đ&ugrave;ng đ&aacute; đẻ trang tr&iacute; cho ch&ugrave;y v&agrave; gi&aacute;o. Nếu bạn nhớ đến s&uacute;ng trong những bản trước th&igrave; cũng đừng c&oacute; lo, v&igrave; nay bạn đ&atilde; c&oacute; một thứ vũ kh&iacute; mới khủng bố hơn nhiều: những con th&uacute; tiền sử m&agrave; nay đ&atilde; tuyệt chủng. Hổ răng kiếm, sư tử n&uacute;i, gấm x&aacute;m, s&oacute;i v&agrave; nhiều con th&uacute; kh&aacute;c sẽ phục vụ dưới ch&acirc;n bạn khi bạn đ&atilde; l&agrave;m chủ kỹ năng thuần h&oacute;a ch&uacute;ng.<br />\r\n<br />\r\nHướng ch&uacute;ng tới kẻ địch, nhấn n&uacute;t l&agrave; bạn c&oacute; thể thoải m&aacute;i ngồi xem m&agrave;n những &ldquo;con đệ&rdquo; tiền sử của m&igrave;nh x&eacute; x&aacute;c kẻ th&ugrave; th&agrave;nh từng mảnh. Hầu hết những con th&uacute; n&agrave;y bạn đều cưỡi được: hổ răng kiếm l&agrave; phương tiện di chuyển tốt hơn nhiều so với xe cộ trong Far Cry 4. Bạn cũng c&oacute; thể trang bị vũ kh&iacute; cho đ&aacute;m voi ma m&uacute;t, v&agrave; cưỡi n&oacute; để đi gi&agrave;y x&eacute;o v&agrave; ph&aacute; tan hết đồn bốt n&agrave;y tới ph&aacute;o đ&agrave;i kh&aacute;c của địch. Ph&ecirc; cực kỳ.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-7(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">C&aacute;c nhiệm vụ săn Beast Master trong game cũng cực kỳ th&uacute; vị. Đ&acirc;y l&agrave; những nhiệm vụ cho bạn săn t&igrave;m, đặt bẫy v&agrave; ti&ecirc;u diệt những con th&uacute; huyền thoại, những con th&uacute; thậm ch&iacute; c&ograve;n to lớn v&agrave; hung dữ hơn những loại th&ocirc;ng thường. T&ocirc;i đặc biệt th&iacute;ch nhiệm vụ săn t&igrave;m con hổ răng kiếm đ&atilde; hạ s&aacute;t đồng đội của Takkar l&uacute;c đầu game. Sau một thời gian đi săn, t&ocirc;i đ&atilde; th&agrave;nh c&ocirc;ng trong việc ph&aacute;t hiện v&agrave; đả thương n&oacute; trước khi n&oacute; kịp hiểu chuyện g&igrave; xảy ra v&agrave; th&aacute;o chạy. T&ocirc;i v&agrave; người bạn s&oacute;i đồng h&agrave;nh đuổi theo con th&uacute; bị thương tới tận hang ổ của n&oacute;, v&agrave; đ&oacute; cũng l&agrave; nơi trận đấu boss cực kh&oacute; nhằn diễn ra. D&ugrave; sao th&igrave; cuối c&ugrave;ng th&igrave; t&ocirc;i cũng hạ gục được n&oacute;. C&oacute; thể thuần h&oacute;a con qu&aacute;i vật n&agrave;y, song &yacute; ch&iacute; b&aacute;o th&ugrave; của t&ocirc;i đ&atilde; trỗi dậy mạnh hơn...</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-8(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Tuy nhi&ecirc;n, Far Cry Primal c&oacute; một điểm trừ kh&aacute; lớn, đ&oacute; l&agrave; kh&ocirc;ng c&oacute; g&igrave; li&ecirc;n quan tới phần chơi multiplayer cả. Đ&oacute; l&agrave; một sự l&atilde;ng ph&iacute;, bởi v&igrave; săn những con th&uacute; lớn c&ugrave;ng với bạn b&egrave; l&uacute;c n&agrave;o cũng ẩn chứa cực kỳ nhiều niềm vui, v&agrave; chắc chắn việc đ&oacute; sẽ y&ecirc;u cầu sự phối hợp chặt chẽ của cả nh&oacute;m. C&ugrave;ng với bạn b&egrave; g&acirc;y loạn khắp nơi đ&atilde; trở th&agrave;nh một th&uacute; vui trong Far Cry 4, ch&iacute;nh v&igrave; thế việc thiếu hụt t&iacute;nh năng chơi mạng trong bạn n&agrave;y khiến t&ocirc;i c&oacute; một ch&uacute;t thất vọng. Săn qu&aacute;i th&uacute; huyền thoại, chuẩn bị vũ kh&iacute; v&agrave; thảo ra chiến thuật chắc chắn sẽ l&agrave; những trải nghiệm tuyệt vời, chỉ tiếc l&agrave; bạn sẽ kh&ocirc;ng t&igrave;m thấy n&oacute; ở đ&acirc;y.<br />\r\n<br />\r\nĐấu boss trong game cũng l&agrave; một vấn đề. Hai con boss ch&iacute;nh của game đều c&oacute; đặc điểm giống nhau, đ&oacute; l&agrave; đều rất tr&acirc;u b&ograve;, v&agrave; l&uacute;c n&agrave;o bạn cũng c&oacute; cảm gi&aacute;c chỉ c&oacute; thể g&atilde;i ngứa cho ch&uacute;ng trong khi phải chống chọi với cả một bầy l&acirc;u nh&acirc;u v&acirc;y quanh vậy. Chả c&oacute; g&igrave; kh&aacute;c biệt hay s&aacute;ng tạo g&igrave; cả, ch&uacute;ng chỉ đứng đ&oacute; để chứng minh rằng bạn đang chơi game m&agrave; th&ocirc;i, v&agrave; h&igrave;nh như c&aacute;c nh&agrave; l&agrave;m game cũng n&agrave;o cũng cho rằng tr&ugrave;m cuối cần phải m&aacute;u thật tr&acirc;u nhưng lại thật ngu hay sao ấy.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-9(1).jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">T&oacute;m lại, Far Cry Primal l&agrave; một game kh&aacute;, nhưng chưa phải l&agrave; tuyệt vời. Cốt truyện của Far Cry 4 đ&atilde; kh&ocirc;ng phải hay cho lắm, phần n&agrave;y thậm ch&iacute; c&ograve;n k&eacute;m hơn. D&ugrave; sao n&oacute; vẫn mang đầy đủ d&aacute;ng dấp của một game Far Cry, một tựa game thế giới mở với h&agrave;ng loạt nhiệm vụ b&ecirc;n lề để kh&aacute;m ph&aacute;, săn t&igrave;m v&agrave; sưu tập. Sự hấp dẫn của game c&oacute; lẽ sẽ đến từ những lựa chọn của bạn m&agrave; th&ocirc;i.<br />\r\n<br />\r\nFar Cry Primal kh&ocirc;ng c&oacute; nhiều sự đổi mới so với những g&igrave; m&agrave; c&aacute;c fan của series đ&atilde; qu&aacute; quen thuộc trước đ&acirc;y, thậm ch&iacute; c&oacute; l&uacute;c c&ograve;n thể hiện k&eacute;m hơn trong một bối cảnh m&agrave; lẽ ra cần phải tốt hơn. Nếu bạn muốn một game mang đ&uacute;ng chất để quẩy nhiệt t&igrave;nh v&agrave; dữ dội ở thời điểm qu&aacute; khứ, Far Cry Primal l&agrave; lựa chọn kh&ocirc;ng tồi. Tuy nhi&ecirc;n nếu bạn ưa th&iacute;ch moojt tựa game c&oacute; nội dung ho&agrave;n chỉnh, s&acirc;u sắc m&agrave; hấp dẫn, bạn sẽ phải thất vọng đấy.</span></p>', '<p>CPU: Core i3-550 / Phenom II X4 955 CPU SPEED: Info</p>\r\n\r\n<p>RAM: 4 GB</p>\r\n\r\n<p>OS: Windows 7, Windows 8.1, Windows 10 (64-bit versions only)</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 460 / Radeon HD 5770 1 GB</p>\r\n\r\n<p>FREE DISK SPACE: 20 GB</p>', '<p>CPU: Core i3-550 / Phenom II X4 955 CPU SPEED: Info</p>\r\n\r\n<p>RAM: 4 GB</p>\r\n\r\n<p>OS: Windows 7, Windows 8.1, Windows 10 (64-bit versions only)</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 460 / Radeon HD 5770 1 GB</p>\r\n\r\n<p>FREE DISK SPACE: 20 GB</p>', 'Cài đặt và chơi', '1520189896ngay-ra-mat-far-cry-primal-2.jpg', 'https://www.youtube.com/embed/LJ2iH57Fs3M', 2016, '2018-03-08');

