INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(1, 2, 'Call of duty: World At War', 'call-of-duty:-world-at-war', '<h3>Sau th&agrave;nh c&ocirc;ng qu&aacute; lớn của phi&ecirc;n bản Modern Warfare đầu ti&ecirc;n cũng như sự thắng thế của bối cảnh chiến tranh hiện đại, kh&ocirc;ng ai nghĩ chiến tranh thế giới thứ 2 sẽ l&agrave; yếu tố tạo n&ecirc;n th&agrave;nh c&ocirc;ng cho Call Of Duty: World At War nữa.</h3>', '<p><span style=\"font-size:14px\">Sau th&agrave;nh c&ocirc;ng qu&aacute; lớn của phi&ecirc;n bản Modern Warfare đầu ti&ecirc;n cũng như sự thắng thế của bối cảnh chiến tranh hiện đại, kh&ocirc;ng ai nghĩ chiến tranh thế giới thứ 2 sẽ l&agrave; yếu tố tạo n&ecirc;n th&agrave;nh c&ocirc;ng cho Call Of Duty: World At War nữa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy nhi&ecirc;n Treyarch đ&atilde; chứng minh rằng m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể l&agrave;m c&oacute; thể l&agrave;m được những việc tưởng chứng như kh&ocirc;ng thể. D&ugrave; kh&ocirc;ng sở hữu một cốt truyện hấp dẫn như Modern Warfare nhưng World At War cũng trang bị cho m&igrave;nh một d&agrave;n diễn vi&ecirc;n lồng tiếng gồm to&agrave;n những t&ecirc;n tuổi lớn như Kiefer Sutherland v&agrave; Gary Oldman, bối cảnh chiến tranh được chuyển dịch về khu vực Th&aacute;i B&igrave;nh Dương đầy lạ lẫm v&agrave; nhiều bất ngờ cộng với việc đẩy mạnh yếu tố h&agrave;nh động bạo lực l&ecirc;n cao chưa từng thấy trong Call Of Duty.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Call of Duty World At War vẫn tạo dựng được một chỗ đứng nhất định trong l&ograve;ng người h&acirc;m mộ, đồng thời khẳng định cho khả năng của h&atilde;ng Treyarch. Nhưng đ&oacute; chưa phải l&agrave; tất cả v&igrave; chế độ chơi mạng mới l&agrave; linh hồn thực sự của tựa game n&agrave;y. Game lần đầu ti&ecirc;n giới thiệu đến người chơi chế độ chơi Zombie v&ocirc; c&ugrave;ng hấp dẫn m&agrave; sau n&agrave;y đ&atilde; được Treyarch &aacute;p dụng th&agrave;nh c&ocirc;ng v&agrave;o c&aacute;c phi&ecirc;n bản Call Of Duty tiếp theo của m&igrave;nh.</span></p>', '<p>CPU: Intel Core i3 3225 3.3 GHz or AMD Ryzen 5 1400</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 8 GB</p>\r\n\r\n<p>OS: Windows 7 64-Bit or later</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 660 @ 2 GB / GTX 1050 or ATI Radeon HD 7850 @ 2GB / AMD RX 550</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 90 GB</p>', '<p>CPU: Intel Core i3 3225 3.3 GHz or AMD Ryzen 5 1400</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 8 GB</p>\r\n\r\n<p>OS: Windows 7 64-Bit or later</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 660 @ 2 GB / GTX 1050 or ATI Radeon HD 7850 @ 2GB / AMD RX 550</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 90 GB</p>', 'Cài đặt và chơi', '1520190408Wallpaper_call_of_duty_5_world_at_war.jpg', 'https://www.youtube.com/embed/Y_Ip_SaJqpg', 2008, '2018-03-08'),
(2, 6, 'Grand Theft Auto V', 'grand-theft-auto-v', '<h3 style=\"text-align:justify\">Grand Theft Auto V l&agrave; tr&ograve; chơi video được ph&aacute;t triển bởi Rockstar North thuộc h&atilde;ng Rockstar Games. L&agrave; phi&ecirc;n bản thứ mười lăm trong D&ograve;ng tr&ograve; chơi Grand Theft Auto v&agrave; l&agrave; phần kế tiếp của Grand Theft Auto IV.</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Game ra từ 17 th&aacute;ng 9 năm 2013 vậy m&agrave; tới b&acirc;y giờ mới rớ v&ocirc; được. L&uacute;c đ&oacute; cũng muốn chơi lắm m&agrave; nh&igrave;n c&aacute;i dung lượng l&agrave; ng&aacute;n tới cổ lu&ocirc;n &ndash; 60Gb th&ocirc;i nghỉ khỏe, với lại m&aacute;y cũng kh&ocirc;ng k&eacute;o nổi, hồi đ&oacute; chơi GTA IV th&ocirc;i m&agrave; n&oacute; như l&ograve; nướng b&aacute;nh m&igrave; rồi. Sau mấy năm r&ograve;ng x&agrave;i k&eacute; được c&aacute;i m&aacute;y, mua một con Asus GTX 950 về gắn phải n&oacute;i l&agrave; mừng hết lớn, c&aacute;i cảm gi&aacute;c cầm c&aacute;i card m&agrave;n h&igrave;nh mới c&oacute;n c&ograve;n thơm &ldquo;m&ugrave;i linh kiện m&aacute;y t&iacute;nh&rdquo;&nbsp;n&oacute; ph&ecirc; như con t&ecirc; t&ecirc;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trước giờ cũng ghiền series GTA lắm, chơi từ bản San&nbsp;Andreas &ndash;&nbsp;Vice City &ndash; IV rồi giờ qua tới V. Bản San Andreas phải n&oacute;i l&agrave; rất hay, tại n&oacute; mi&ecirc;u tả đ&uacute;ng được bối cảnh của một khu ổ chuột, trong đ&oacute; tệ nạn cũng nhiều, m&agrave; phần hay nhất l&agrave; c&aacute;c chỉ số nh&acirc;n vật từ sức mạnh &ndash; thể lực. Qua tới Vice City cũng hay nhưng n&oacute;i về mafia bắn giết c&aacute;c thứ chứ kh&ocirc;ng g&oacute;i gọn như c&aacute;c băng đảng b&ecirc;n San Andreas. Qua phần IV th&igrave; đẹp hơn nhiều, cốt truyện cũng hay nữa, nh&acirc;n vật thiết kế c&oacute; chiều s&acirc;u v&agrave; t&iacute;nh c&aacute;ch. Tới phần V th&igrave; m&aacute; ơi đẹp kinh dị chưa chơi được 1/5 game nữa nhưng m&agrave; thấy m&ecirc; với c&aacute;i đồ họa rồi.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Mở m&agrave;n như Hollywood</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621175947_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621175947_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&aacute;c với mấy phần trước thay v&igrave; nhiệm vụ đầu ti&ecirc;n l&agrave; l&aacute;i xe đi đ&oacute;n ai đ&oacute; hoặc chạy tới nơi n&agrave;o đ&oacute;, GTA V vừa v&ocirc; l&agrave; đ&atilde; cho bạn cướp ng&acirc;n h&agrave;ng liền. Mở m&agrave;n bằng việc la h&eacute;t nh&acirc;n vi&ecirc;n ng&acirc;n h&agrave;ng, xong rồi chỉa s&uacute;ng v&ocirc; mặt để l&ugrave;a hết đ&aacute;m nh&acirc;n vi&ecirc;n v&ocirc; ph&ograve;ng kh&aacute;c, kế đến l&agrave; k&iacute;ch nổ cửa hầm chứa tiền. Vơ v&eacute;t xong hết ra đụng cảnh s&aacute;t đứng chờ, l&uacute;c n&agrave;y cầm s&uacute;ng l&ecirc;n bắn m&agrave; phải headshot kh&ocirc;ng mới chịu nha, chơi mấy game FPS kia quen rồi giờ v&ocirc; GTA bắn thấy như l&ecirc;n đồng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180339_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180339_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau m&agrave;n bắn s&uacute;ng dạo đầu l&agrave; m&agrave;n chạy cong đu&ocirc;i để tho&aacute;t cảnh s&aacute;t. L&uacute;c n&agrave;y bối cảnh đang c&oacute; tuyết, sương m&ugrave; n&agrave;y nọ nhưng vẫn c&oacute; một ch&uacute;t nắng, nh&igrave;n kh&ocirc;ng c&oacute; g&igrave; gọi l&agrave; qu&aacute; rực rỡ&nbsp;nhưng c&aacute;i mấy c&aacute;i chi tiết th&igrave; rất đ&atilde;:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chẳng hạn như l&uacute;c chạy trốn cảnh s&aacute;t từ ng&acirc;n h&agrave;ng, mới v&ocirc; l&aacute;i xe tr&ecirc;n GTA t&igrave;m lại cảm gi&aacute;c n&ecirc;n hơi lạng l&aacute;ch ch&uacute;t, v&ocirc; t&igrave;nh trật khỏi đường ch&iacute;nh, b&aacute;nh xe sau l&uacute;c lấn ra ngo&agrave;i chỗ &iacute;t tuyết th&igrave; bắn đất c&aacute;t ra ph&iacute;a sau. Đang chạy trốn cảnh s&aacute;t m&agrave; to&agrave;n nh&igrave;n cảnh vật, chỗ n&agrave;y phải n&oacute;i l&agrave; rất đẹp m&agrave; tại c&oacute; tuyết che phủ n&ecirc;n kh&ocirc;ng thấy được g&igrave; nhiều, chứ &iacute;t c&acirc;y cối nh&agrave; cửa, nh&igrave;n được cả một v&ugrave;ng ch&acirc;n trời c&oacute; mấy đ&aacute;m m&acirc;y nhỏ, nắng kh&ocirc;ng nhiều lắm nhưng đủ chiếu s&aacute;ng khung cảnh&nbsp;nh&igrave;n rất lung thị linh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180610_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180610_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuyết lạnh trắng x&oacute;a đồ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Bởi lo nh&igrave;n trời m&acirc;y m&agrave; tới chỗ cua qu&ecirc;n quẹo, vậy l&agrave; t&ocirc;ng thẳng v&ocirc; d&agrave;n xe chặn đường của cảnh s&aacute;t ở ph&iacute;a trước, đ&agrave;nh phải chạy lại kh&uacute;c n&agrave;y. Tới cảnh gặp đường c&ugrave;ng th&igrave; sau khi xuy&ecirc;n t&aacute;o được hết cảnh s&aacute;t, nh&acirc;n vật chạy thẳng v&agrave;o cơn b&atilde;o tuyết đang nổi l&ecirc;n, kh&uacute;c n&agrave;y y chang cảnh trong phim m&agrave; theo kiểu kết th&uacute;c mở.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau cảnh đ&oacute; l&agrave; đ&aacute;m tang giả của Michael Townley, l&uacute;c n&agrave;y d&ograve;ng chữ Rockstar Games presents hiện l&ecirc;n l&agrave; thấy rần rần trong người, cứ như đang coi phim vậy. M&agrave;n h&igrave;nh tối lại rồi chữ Grand Theft Auto&nbsp;V hiện l&ecirc;n, trong khi m&igrave;nh vẫn c&ograve;n gật g&ugrave; khen thầm c&aacute;i mở m&agrave;n của game qu&aacute; b&aacute; ch&aacute;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180813_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180813_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tưởng l&agrave; giới thiệu xong rồi n&ecirc;n mới tới cảnh Michael ngồi trị liệu t&acirc;m l&yacute; với &ocirc;ng b&aacute;c sĩ trong một căn hộ kh&aacute; sang cảnh ven bờ biển, đ&uacute;ng l&agrave; game đ&uacute;ng chất Rockstar, c&aacute;c c&acirc;u thoại tuy hơi th&ocirc; nhưng m&agrave; rất thật v&igrave; n&oacute; gắn đ&uacute;ng với t&iacute;nh c&aacute;ch nh&acirc;n vật. C&aacute;c c&acirc;u chửi thề như l&uacute;c Michael n&oacute;i về thằng con trai tối ng&agrave;y quay tay &ndash; h&uacute;t cần &ndash; chơi game kh&ocirc;ng nghề ngỗng sẽ l&agrave;m bạn ph&igrave; cười v&igrave; li&ecirc;n tục đệm &ldquo;f*cking&rdquo; xối xả v&agrave;o mặt &ocirc;ng b&aacute;c sĩ t&acirc;m l&yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kết th&uacute;c buổi trị liệu t&acirc;m l&yacute; Michael bước ra khỏi căn nh&agrave;, nhạc nổi l&ecirc;n v&agrave; GTA V bắt đầu hớp hồn người chơi bằng những g&oacute;c camera độc đ&aacute;o, ph&ocirc; diễn th&agrave;nh phố sống động &nbsp;v&agrave; đẹp v&ocirc; c&ugrave;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621180948_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621180948_1-1024x576.jpg\" style=\"height:419px; width:745px\" />&nbsp;<img alt=\"20160621181002_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621181002_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160621181005_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160621181005_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">L&uacute;c n&agrave;y&nbsp;vừa chiếu khung cảnh trong game vừa hiện chữ giới thiệu nh&agrave; sản xuất v&agrave; nhiều người nữa, nh&igrave;n cứ như một bộ phim lấy chủ đề b&atilde;i biển Miami vậy, đ&uacute;ng l&agrave; c&oacute; phần mở m&agrave;n th&ocirc;i m&agrave; game hớp hồn người chơi qu&aacute; chừng,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau phần thể hiện cảnh đẹp của game th&igrave; gặp được Franklin v&agrave; Lamar đang t&aacute;m chuyện dọc đường, tới một ng&ocirc;i nh&agrave; gần chỗ b&aacute;c sĩ t&acirc;m l&yacute; của Michael. Tới đ&acirc;y từ cuộc n&oacute;i chuyện, game chuyển cảnh theo một c&aacute;ch v&ocirc; c&ugrave;ng mượt đến nỗi phải mất tới 5 gi&acirc;y khi thấy Franklin đứng y&ecirc;n th&igrave; mới biết đ&atilde; v&ocirc; game rồi, l&uacute;c n&agrave;y như thường lệ &ndash; một nh&acirc;n vật da đen ch&ocirc;m xe như GTA San Andreas, bạn sẽ chọn một trong 2 chiếc xe để c&ugrave;ng Lamar l&aacute;i tới tiệm b&aacute;n xe nơi m&agrave; cả 2 đang l&agrave;m việc.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Vẻ đẹp kh&oacute; cưỡng</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616172629_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616172629_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trong th&agrave;nh phố, nh&agrave; cửa, xe cộ rất sống động. Th&iacute;ch nhất l&agrave; c&aacute;i nắng trong GTA V, rạng ngời m&agrave; kh&ocirc;ng ch&oacute;i l&oacute;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616170930_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616170930_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">C&aacute;i nắng sớm th&igrave; dịu nhẹ, c&aacute;i nắng giữa trưa th&igrave; gắt hơn, chiều trở đi th&igrave; bắt đầu dịu dần, tới gần ho&agrave;ng h&ocirc;n l&agrave; đẹp nhất. Nhiều l&uacute;c chạy giữa đường phải đứng lại lựa những g&oacute;c đẹp để m&agrave; ngắm mặc d&ugrave; trong th&agrave;nh phố th&ocirc;i m&agrave; nh&igrave;n rất thật tiếc l&agrave; m&aacute;y kh&ocirc;ng k&eacute;o nổi l&ecirc;n Very High. Đ&oacute; giờ hiếm c&oacute; game n&agrave;o m&agrave; ngắm cảnh nhiều như vậy, v&igrave; th&agrave;nh phố trong phần n&agrave;y nh&igrave;n vui tươi hơn hẳn (cũng do chỉnh cường độ d&acirc;n số cao), xe cộ đa dạng v&agrave; tấp nập đ&uacute;ng nơi, như trung t&acirc;m th&agrave;nh phố th&igrave; l&uacute;c n&agrave;o cũng đ&ocirc;ng xe nhất l&agrave; s&aacute;ng tới trưa, chiều tối th&igrave; vắng hơn một ch&uacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616161949_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616161949_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chơi tới l&uacute;c Trevor nghe tin Michael c&ograve;n sống, vậy l&agrave; sau khi xử băng đảng biker l&agrave; lật đật&nbsp;tức tốc chạy l&ecirc;n th&agrave;nh phố liền. Đường rất d&agrave;i m&agrave; l&uacute;c khởi h&agrave;nh th&igrave; đang mưa nữa, ngay cả mặt đường l&uacute;c l&uacute;c mưa cũng giống thật v&igrave; đang ở ngoại &ocirc; n&ecirc;n đường xấu hơn nhiều, chỗ bằng chỗ l&otilde;m n&ecirc;n l&uacute;c mưa th&igrave; chỗ đọng nước chỗ th&igrave; kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616165621_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616165621_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">L&aacute;i xe từ l&uacute;c mưa cho tới tạnh, đang l&aacute;i giữa chừng th&igrave; nhiệm vụ k&ecirc;u dừng lại giữa đường, tại đi ban đ&ecirc;m n&ecirc;n l&uacute;c đ&oacute; gần s&aacute;ng, nhiệm vụ k&ecirc;u Trevor lại chỗ sườn n&uacute;i đứng ng&oacute; xuống th&agrave;nh phố rồi lầm bẩm chửi Michael, c&ocirc;ng nhận Rockstar rất hay&nbsp;trong chuyện&nbsp;tạo những cảnh &ldquo;đi s&acirc;u v&agrave;o l&ograve;ng người&rdquo; như vầy. Chỗ sườn n&uacute;i đ&oacute; nh&igrave;n xuống th&agrave;nh phố ban đ&ecirc;m, mặt trời sắp l&ecirc;n nhưng đ&egrave;n đường v&agrave; đ&egrave;n nh&agrave; c&ograve;n mở, vẫn thấy xe chạy tr&ecirc;n đường mặc d&ugrave; rất xa, đứng đ&oacute; phải mất khoảng v&agrave;i ph&uacute;t để ngắm cảnh m&agrave; ước l&agrave; m&igrave;nh nhảy v&ocirc; m&agrave;n h&igrave;nh để ngắm tiếp.&nbsp;L&ecirc;n xe l&aacute;i l&ecirc;n th&agrave;nh phố tiếp, trời cũng bắt đầu rạng s&aacute;ng, l&uacute;c l&aacute;i tới gần th&agrave;nh phố nh&agrave; c&agrave;ng l&uacute;c c&agrave;ng nhiều l&agrave; l&uacute;c b&igrave;nh minh l&ecirc;n, nắng xuy&ecirc;n qua mấy khe hở của những ng&ocirc;i nh&agrave; ổ chuột rồi những t&ograve;a cao tầng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Phải n&oacute;i l&agrave; giống như đi phượt vậy đ&oacute;,&nbsp;b&igrave;nh thường mấy phần trước to&agrave;n l&aacute;i xe chạy như đi&ecirc;n tới chỗ nhận nhiệm vụ m&agrave; tới phần n&agrave;y phải chạy từ từ để ngắm cảnh, nhiều l&uacute;c c&ograve;n chạy giống nhu NPC vậy &ndash; c&agrave; rề rề để chụp h&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sắp tới c&ograve;n c&oacute; vụ lặn xuống nước để l&agrave;m nhiệm vụ nữa, m&agrave; tại chưa chơi tới, l&uacute;c trước cũng c&oacute; lặn một lần m&agrave; c&ograve;n giới hạn vụ oxy đồ n&ecirc;n kh&ocirc;ng ở dưới l&acirc;u để kh&aacute;m ph&aacute; được, để từ từ l&agrave;m nhiệm vụ ch&ocirc;m t&agrave;u ngầm rồi kh&aacute;m ph&aacute; tiếp hehe.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Gameplay ấn tượng</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&ocirc;ng những mang quy m&ocirc; lớn như cướp ng&acirc;n h&agrave;ng chẳng hạn m&agrave; nhiệm vụ trong phần V c&ograve;n th&uacute; vị hơn nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.gtanet.com/gta-5/images/characters/chop.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&ocirc;ng quay đi quay lại mấy nhiệm vụ cỏn con chạy xe tới lui m&agrave; n&oacute; c&ograve;n cho người chơi một cảm gi&aacute;c chưa từng c&oacute; khi chơi c&aacute;c phần trước, chẳng hạn như l&aacute;i xe t&ocirc;ng thẳng v&agrave;o chỗ b&aacute;n xe của Franklin rồi l&ocirc;i &ocirc;ng chủ ra nện cho một trận, đạp xe tr&ecirc;n b&atilde;i biển v&agrave;o một ng&agrave;y nắng đẹp hay thậm ch&iacute; c&ograve;n cho bạn v&agrave;o g&oacute;c nh&igrave;n của một ch&uacute; ch&oacute; khi l&agrave;m nhiệm vụ &ldquo;Chop&rdquo;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"20160616163017_1\" src=\"http://www.hiepsibaotap.com/wp-content/uploads/2016/06/20160616163017_1-1024x576.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đ&atilde; nhất l&agrave; nhiệm vụ Trevor trả th&ugrave; một băng nh&oacute;m d&aacute;m gi&agrave;nh mối b&aacute;n ma t&uacute;y đ&aacute; sau đ&oacute; đốt cả căn nh&agrave;, l&uacute;c ng&ocirc;i nh&agrave; nổ Trevor quay lưng đi một c&aacute;ch lạnh l&ugrave;ng đ&uacute;ng chất phim Mỹ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"https://i.ytimg.com/vi/Z25GKAlgNEQ/maxresdefault.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Cốt truyện trong GTA V rất hợp l&yacute; m&agrave; lại sống động vui nhộn, một n&eacute;t nữa c&aacute;c t&igrave;nh tiết đều đến li&ecirc;n tiếp một c&aacute;ch kh&ocirc;ng ngờ nhưng bạn vẫn thấy hợp l&yacute; v&agrave; trơn tru, thậm ch&iacute; c&ograve;n cảm thấy phấn kh&iacute;ch với những g&igrave; m&igrave;nh sắp l&agrave;m. Chẳng hạn như Michael bị vợ cắm sừng bởi huấn luyện vi&ecirc;n tennis, về nh&agrave; thấy cảnh tượng kh&oacute; ưa Michael rượt theo đ&aacute;nh ghen rồi c&ograve;n rủ cả Franklin, tới nơi thằng cha huấn luyện vi&ecirc;n đang n&uacute;p ở mộc căn nh&agrave; sang chảnh tr&ecirc;n đồi, Michael nảy ra &yacute; định k&eacute;o sập cả căn nh&agrave;. Kh&ocirc;ng ngờ nh&agrave; n&agrave;y l&agrave; của &ocirc;ng tr&ugrave;m mafia nổi tiếng, vậy l&agrave; Michael sau khi nghỉ hưu lại phải đi cướp ng&acirc;n h&agrave;ng để trả nợ cho &ocirc;ng tr&ugrave;m, rồi từ đ&oacute; nhiều t&igrave;nh tiết tiếp diễn khi m&agrave; Trevor t&igrave;m được Michael, cả bộ ba Franklin &ndash; Michael &ndash; Trevor phải rửa kiếm đem ra x&agrave;i lại chứ kh&ocirc;ng g&aacute;c nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ngo&agrave;i những nhiệm vụ ch&iacute;nh ra (ch&iacute;nh x&aacute;c l&agrave; mới chơi hơn những c&aacute;i đang n&oacute;i c&oacute; một ch&uacute;t), nh&acirc;n vật c&ograve;n c&oacute; thể tương t&aacute;c được nhiều hơn với thế giới trong game, v&iacute; dụ như bạn c&oacute; thể n&oacute;i chuyện với người đi đường nhưng hầu hết l&agrave; đều th&ugrave; hằng, đứng nh&igrave;n NPC n&agrave;o đ&oacute; th&igrave; một l&agrave; NPC sẽ bỏ đi v&agrave; chửi bạn kh&ugrave;ng &ndash; hai l&agrave; g&acirc;y sự theo kiểu &ldquo;nh&igrave;n g&igrave; m&agrave;y ?!&rdquo; v&agrave; khi r&uacute;t s&uacute;ng ra chỉa v&agrave;o mặt th&igrave; NPC sẽ xin lỗi bạn r&iacute;u r&iacute;t. Bạn c&oacute; thể afk khoảng v&agrave;i ph&uacute;t, nh&acirc;n vật sẽ bắt đầu l&agrave;m v&agrave;i h&agrave;nh động tự do khi kh&ocirc;ng ai điều khiển, th&uacute; vị&nbsp;hơn l&agrave; m&agrave;n h&igrave;nh bắt đầu zoom lại gần v&agrave; lia theo&nbsp;m&ocirc;ng phụ nữ đi ngang qua &ndash; đang rảnh m&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Th&uacute; vị hơn l&agrave; vẻ ngo&agrave;i của nh&acirc;n vật sẽ bị ảnh hưởng bởi m&ocirc;i trường b&ecirc;n ngo&agrave;i, v&iacute; dụ khi chạy nhiều nh&acirc;n vật sẽ đổ mồ h&ocirc;i v&agrave; bạn sẽ thấy &aacute;o ướt phần giữa lưng hoặc khi nhảy xuống nước cả bộ đồ sẽ bị ướt nhẹp, c&aacute;c vết đạn m&aacute;u me của nh&acirc;n vật thật hơn</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">GTA V nằm trong những d&ograve;ng game tui th&iacute;ch nhất từ trước tới giờ, ngay từ thời trẻ tr&acirc;u chơi kh&ocirc;ng thấy hay lắm tại to&agrave;n cướp xe lạng l&aacute;ch nhập cheatcode rồi bắn loạn xạ xong nghĩ, c&agrave;ng biết chơi th&igrave; c&agrave;ng thấy cốt truyện n&oacute; hay v&agrave; rất thực tế. Mới chơi được c&oacute; kh&uacute;c đầu th&ocirc;i m&agrave; đ&atilde; ghiền rồi, sau n&agrave;y chắc chắn sẽ c&ograve;n gặp nhiều nhiệm vụ hay hơn vậy nữa, kh&ocirc;ng hổ danh 10 điểm tr&ecirc;n IGN.</span></p>', '<p>OS: Windows 8.1 64 Bit, Windows 8 64 Bit, Windows 7 64 Bit Service Pack 1, Windows Vista 64 Bit Service Pack 2</p>\r\n\r\n<p>Processor: Intel Core 2 Quad CPU Q6600 @ 2.40GHz (4 CPUs) / AMD Phenom 9850 Quad-Core Processor (4 CPUs) @ 2.5GHz</p>\r\n\r\n<p>Memory: 4GB</p>\r\n\r\n<p>Video Card: NVIDIA 9800 GT 1GB / AMD HD 4870 1GB (DX 10, 10.1, 11)</p>\r\n\r\n<p>Sound Card: 100% DirectX 10 compatible</p>\r\n\r\n<p>HDD Space: 65GB DVD Drive</p>', '<p>OS: Windows 8.1 64 Bit, Windows 8 64 Bit, Windows 7 64 Bit Service Pack 1, Windows Vista 64 Bit Service Pack 2</p>\r\n\r\n<p>Processor: Intel Core 2 Quad CPU Q6600 @ 2.40GHz (4 CPUs) / AMD Phenom 9850 Quad-Core Processor (4 CPUs) @ 2.5GHz</p>\r\n\r\n<p>Memory: 4GB</p>\r\n\r\n<p>Video Card: NVIDIA 9800 GT 1GB / AMD HD 4870 1GB (DX 10, 10.1, 11)</p>\r\n\r\n<p>Sound Card: 100% DirectX 10 compatible</p>\r\n\r\n<p>HDD Space: 65GB DVD Drive</p>', 'Cài đặt và chơi', '15201654701280.jpg', 'https://www.youtube.com/embed/QkkoHAzjnUs', 2013, '2018-03-14'),
(3, 5, 'Total War Rome II', 'total-war-rome-ii', '<h3 style=\"text-align:justify\">Total War ROME II&nbsp;l&agrave;&nbsp;game chiến thuật thời gian thực v&agrave; chiến lược theo lượt,&nbsp;do h&atilde;ng The Creative Assembly ph&aacute;t triển v&agrave; Sega ph&aacute;t h&agrave;nh. Game được x&acirc;y dựng dựa theo&nbsp;thời kỳ cổ đại, v&agrave; tập trung v&agrave;o mi&ecirc;u tả mỗi nền văn h&oacute;a của thời đại một ch&iacute;nh x&aacute;c v&agrave; s&acirc;u sắc nhất c&oacute; thể, tr&aacute;i ngược với người tiền nhiệm của n&oacute; tập trung gần như ho&agrave;n to&agrave;n v&agrave;o Cộng h&ograve;a La M&atilde; v&agrave; ch&iacute;nh trị của n&oacute;. C&aacute;c chiến dịch lớn bắt đầu trong 272BC (trước c&ocirc;ng nguy&ecirc;n), v&agrave; k&eacute;o d&agrave;i trong 300 năm.</h3>', '<p><span style=\"font-size:14px\"><strong>Cũng đ&atilde; ra mắt kh&aacute; l&acirc;u (th&aacute;ng 9 năm 2013) với tận 3 lần cập nhật DLC (phi&ecirc;n bản mở rộng), thế nhưng Rome II Total war vẫn mang trong m&igrave;nh một sức h&uacute;t m&atilde;nh liệt kh&oacute; cưỡng lại đối với d&ograve;ng game RTS lẫn TBS tr&ecirc;n to&agrave;n thế giới. Nếu bạn thật sự l&agrave; game thủ của d&ograve;ng game d&agrave;n trận chiến thuật thời gian thật v&agrave; theo lượt, c&oacute; lẽ kh&ocirc;ng thể kh&ocirc;ng biết tới d&ograve;ng game Total war của nh&agrave; ph&aacute;t triển&nbsp;The Creative Assembly đồng kết hợp với tận 3 h&atilde;ng ph&aacute;t h&agrave;nh lớn (theo từng phi&ecirc;n bản) l&agrave; SEGA, Electronics Art v&agrave; Activision ph&aacute;t h&agrave;nh trong suốt hơn 14 năm qua. H&atilde;y c&ugrave;ng Guide Game xem sơ l&yacute; do m&agrave; d&ograve;ng game n&agrave;y được nhiều người ưa chuộng đến vậy với phần tr&igrave;nh diễn của phi&ecirc;n bản mới nhất: ROME II Total war</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần I: Giới thiệu game</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Rome II total war l&agrave; phần hai của phi&ecirc;n bản Rome Total war được ph&aacute;t h&agrave;nh v&agrave;o tận năm 2004 tức 10 năm trước. L&yacute; do được&nbsp;The Creative Assembly l&agrave;m lại kh&aacute; ch&iacute;nh đ&aacute;ng: sau tận 10 năm trời vẫn c&oacute; hơn 2 triệu game thủ tr&ecirc;n to&agrave;n thế giới ch&igrave;m đắm v&agrave;o c&aacute;c trận đấu LAN trong phi&ecirc;n bản Rome đầu ti&ecirc;n mặc d&ugrave; chất lượng đồ họa cũng như chế độ chơi đ&atilde; qu&aacute; lỗi thời. Với phi&ecirc;n bản Rome II n&agrave;y,&nbsp;The Creative Assembly đ&atilde; thật sự mang lại một game bom tấn cho d&ograve;ng game d&agrave;n trận với c&ocirc;ng nghệ đồ họa độc quyền l&agrave;&nbsp;Warscape engine. Với khả năng t&aacute;i tạo một trận đ&aacute;nh với qu&acirc;n số hơn 10.000 người với những hiệu ứng ch&aacute;y nổ ho&agrave;nh tr&aacute;ng. Chưa kể mọi hoạt cảnh, h&agrave;nh động của từng nh&acirc;n vật đều được đảm bảo kh&ocirc;ng tr&ugrave;ng lặp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/battle-1.jpg\"><img alt=\"Rome\" src=\"https://guidegame.vn/wp-content/uploads/battle-1.jpg\" style=\"height:423px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Một trận đ&aacute;nh thường với hơn 10.000 l&iacute;nh&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần II: Tổng quan</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lấy bối canh ch&acirc;u &Acirc;u thời Hy Lạp cổ đại (năm 200 TCN đến năm 100 SCN) với hơn 40 quốc gia (Factions) với những quốc gia nổi tiếng như Rome (&Yacute; ng&agrave;y nay), Sparta,&nbsp;Macedonia (2 v&ugrave;ng đất thuộc Hy Lạp ng&agrave;y nay),&nbsp;Carthago, Ai Cập, đến những quốc gia chẳng c&oacute; t&ecirc;n tuổi g&igrave; như&nbsp;Suebi (Đức ng&agrave;y nay),&nbsp;Iceni (một bộ tộc nhỏ v&ugrave;ng Đ&ocirc;ng nước Anh). Người chơi c&oacute; thể thỏa th&iacute;ch lựa chọn quốc gia cho ri&ecirc;ng m&igrave;nh để chơi trong suốt qu&aacute; tr&igrave;nh chinh chiến. Về lối chơi Rome II c&oacute; 2 chế độ l&agrave; theo lượt v&agrave; d&agrave;n trận thời gian thật:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Với giao diện ch&iacute;nh l&agrave; theo lượt. Tại giao diện n&agrave;y bạn c&oacute; thể ra lệnh cho quốc gia m&igrave;nh về mọi mặt, từ quan hệ ngoại giao (g&acirc;y chiến, h&ograve;a b&igrave;nh, l&agrave;m&nbsp;<a href=\"https://guidegame.vn/esports/lien-minh-huyen-thoai\" title=\"liên minh\">li&ecirc;n minh</a>, c&ugrave;ng giao thương hoặc rủ nhau hội đồng một nước kh&aacute;c) đến&nbsp;<a href=\"https://guidegame.vn/champions/vi\" title=\"vi\">vi</a>ệc n&acirc;ng cấp th&agrave;nh phố (x&acirc;y nh&agrave; l&iacute;nh, x&acirc;y cảng, x&acirc;y khu chăn nu&ocirc;i, x&acirc;y nh&agrave; văn h&oacute;a như rạp h&aacute;t, hoặc x&acirc;y đền đ&agrave;i thờ thần th&aacute;nh). Việc bạn thỏa th&iacute;ch l&agrave;m mọi thứ theo &yacute; bạn được hoan ngh&ecirc;nh từ nh&agrave; ph&aacute;t triển&nbsp;The Creative Assembly! Sau mỗi lượt đi bạn c&oacute; thể tiếp tục n&acirc;ng cấp nh&agrave; hoặc mua th&ecirc;m tiểu đội (1 lượt l&agrave; 1 m&ugrave;a trong năm, tối đa 4 m&ugrave;a/năm) v&agrave; việc n&acirc;ng cấp nh&agrave; hoặc mua đồ đều được t&iacute;nh bằng tiền v&agrave; lượt đi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/28039TWRII_E3_2013_Pontus.png\"><img alt=\"Rome 2 \" src=\"https://guidegame.vn/wp-content/uploads/28039TWRII_E3_2013_Pontus.png\" style=\"height:418px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Giao diện theo lượt, với mỗi đạo qu&acirc;n được tượng trưng bằng h&igrave;nh ảnh vị tướng/thuyền (với thủy qu&acirc;n) cầm l&aacute; cờ với số gạch ngang tượng trung cho số tiểu đội đang sỡ hữu</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; C&ograve;n giao diện d&agrave;n trận thời gian thực sẽ diễn ra chỉ khi n&agrave;o bạn mở một trận đ&aacute;nh. Với qu&acirc;n số trung b&igrave;nh 1 tiểu đội l&iacute;nh l&agrave; từ 90 l&iacute;nh với kỵ binh tới tận 180 l&iacute;nh với bộ binh. Với một đại đội ho&agrave;n chỉnh bao gồm 19 tiểu đội cộng với 1 tiểu đội với vị Tướng đứng đầu th&igrave; với qu&acirc;n số trung b&igrave;nh l&agrave; hơn 4000 l&iacute;nh cho một đại đội. Ngo&agrave;i ra việc bạn c&oacute; thể mở giao tranh với chiến thuật lấy thịt đ&egrave; người với tối đa 10 đại đội gồm nh&igrave;u quốc gia đồng minh chung th&igrave; việc một trận đ&aacute;nh c&oacute; hơn 80.000 l&iacute;nh cho tổng cả 2 phe l&agrave; quả thật rất lớn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/maxresdefault4.jpg\"><img alt=\"Rome 2\" src=\"https://guidegame.vn/wp-content/uploads/maxresdefault4.jpg\" style=\"height:418px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>1 trận đ&aacute;nh ti&ecirc;u biểu với chiến thuật lấy thịt đ&egrave; người v&agrave; ph&ograve;ng thủ theo từng lớp l&iacute;nh với tướng l&agrave; trung t&acirc;m</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần III: Lối chơi &ndash; chiến thuật</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&uacute;ng ta phải n&oacute;i qua về kinh tế &ndash; ch&iacute;nh trị trước trong game n&agrave;y. Với việc kinh tế ch&iacute;nh l&agrave; giao thương v&agrave; thuế d&acirc;n, bạn kh&ocirc;ng thể n&agrave;o tăng thuế v&ocirc; l&yacute; v&igrave; l&ograve;ng d&acirc;n c&oacute; hạn v&agrave; nếu d&acirc;n ch&uacute;ng qu&aacute; ngh&egrave;o khổ do bạn l&agrave; một &ocirc;ng vua tham lam th&igrave; họ sẽ lật đổ bạn bằng c&aacute;ch khởi nghĩa với nhiều đạo qu&acirc;n Rebels (qu&acirc;n c&aacute;ch mạng). C&ograve;n giao thương bạn phải c&oacute; được chữ k&yacute; của người đứng đầu quốc gia đ&oacute; để c&oacute; quyền giao thương, n&oacute;i l&agrave; chữ k&yacute; nhưng n&oacute; như một bản cam kết v&agrave; việc c&oacute; được chữ k&yacute; đ&oacute; mu&ocirc;n v&agrave;n kh&oacute; khăn: kh&aacute;c biệt chủng tộc, kh&aacute;c biệt t&ocirc;n gi&aacute;o, kh&aacute;c biệt v&ugrave;ng, c&oacute; quan hệ th&acirc;n mật với một quốc gia thứ 3 l&agrave; kẻ th&ugrave; của quốc gia muốn giao thương&hellip; hoặc l&yacute; do đơn giản nhất l&agrave; &ldquo;tao gh&eacute;t tao kh&ocirc;ng th&iacute;ch k&yacute; th&igrave; m&agrave;y l&agrave;m g&igrave; được tao?&rdquo;. Việc đ&uacute;t l&oacute;t, t&acirc;ng bốc hoặc gi&uacute;p đỡ về qu&acirc;n sự, trao đổi kỹ thuật l&agrave; điều bắt buộc để c&oacute; được sự giao thương hoặc rộng hơn l&agrave; quan hệ đồng minh. Về mặt ch&iacute;nh trị trong nước lẫn quốc tế, bạn ho&agrave;n to&agrave;n nắm quyền, l&agrave; vua. Thế nhưng đối với một số nước c&oacute; hội đồng tối cao (V&iacute; dụ như Đế chế La M&atilde;, Đế Chế Carthago&nbsp;) th&igrave; bạn vẫn phải l&agrave;m theo &yacute; hội đồng nếu kh&ocirc;ng muốn bị Hội đồng chia năm xẻ bảy v&agrave; dẫn đến nội chiến (Civil War) Khi đ&oacute; nếu bạn đ&aacute;nh được to&agrave;n bộ c&aacute;c đạo qu&acirc;n từ c&aacute;c nh&agrave; kh&aacute;c (kiểu như qu&acirc;n từ c&aacute;c Ho&agrave;ng Tử kh&aacute;c) v&agrave; kết th&uacute;c nội chiến, bạn sẽ l&agrave;m Vua v&agrave; dẹp lu&ocirc;n cả c&aacute;i hội đồng ồn &agrave;o n&agrave;y. Ngo&agrave;i ra về ch&iacute;nh trị đối với c&aacute;c nước kh&aacute;c, bạn phải thật kh&ocirc;n kh&eacute;o với nhiều ch&iacute;nh s&aacute;ch ngoại giao kh&aacute;c nhau. Việc kết bạn với c&aacute;c quốc gia mạnh xung quanh hoặc g&acirc;y chiến với c&aacute;c quốc gia nhỏ yếu thế hơn để khẳng định sức mạnh qu&acirc;n sự đ&ocirc;i khi l&agrave; điều bắt buộc để quốc gia tồn tại. Đ&ocirc;i khi bạn phải nhờ vả c&aacute;c nước c&oacute; sức mạnh qu&acirc;n sự khủng khiếp hoặc k&ecirc;u gọi c&aacute;c nước yếu xung quanh để c&oacute; thể đ&aacute;nh được một quốc gia kh&aacute;c. Lấy v&iacute; dụ như nếu bạn chơi Ai Cập th&igrave; chỉ cần cỡ 20 lượt đi đầu ti&ecirc;n bạn sẽ vấp phải một chướng ngại cực lớn đến từ ph&iacute;a Đ&ocirc;ng l&agrave; li&ecirc;n minh c&aacute;c quốc gia của đế chế Selucid (gồm c&aacute;c nước thuộc địa cũ của Ba Tư) với hơn 10 nước thuộc địa v&agrave; Selucid sẽ tuy&ecirc;n chiến với bạn (theo đ&uacute;ng lịch sử)</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/1EYCARR.jpg\"><img alt=\"Rome 2\" src=\"https://guidegame.vn/wp-content/uploads/1EYCARR.jpg\" style=\"height:418px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đ&ocirc;i khi phải đ&uacute;t l&oacute;t như trong h&igrave;nh để k&yacute; được hiệp ước giao thương</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">L&agrave; một game chiến thuật, bạn kh&ocirc;ng thể m&atilde;i d&ugrave;ng chiến thuật lấy thịt đ&egrave; người như những g&igrave; c&aacute;c game d&agrave;n trận kh&aacute;c đ&atilde; l&agrave;m. Với mỗi quốc gia đều c&oacute; thế mạnh của mỗi quốc gia đ&oacute;. V&iacute; dụ như Đế chế La M&atilde; (Rome) th&igrave; mạnh về qu&acirc;n cầm khi&ecirc;ng với đội h&igrave;nh con r&ugrave;a nổi tiếng (đội h&igrave;nh Testudo) tốt cho việc đỡ c&aacute;c vũ kh&iacute; s&aacute;t thương từ xa. C&aacute;c nước thuộc khối Hy Lạp như Sparta, Athens hoặc Macedonia th&igrave; mạnh về qu&acirc;n cầm gi&aacute;o ngắn với đội h&igrave;nh Phalanx huyền thoại c&oacute; khả năng trụ vững trước những pha x&ocirc;ng v&agrave;o gi&aacute;p l&aacute; c&agrave;. Ngo&agrave;i ra cũng n&ecirc;n kể về đội h&igrave;nh Phalanx Pikeman cầm gi&aacute;o d&agrave;i của qu&acirc;n Ai Cập v&agrave; c&aacute;c nước v&ugrave;ng Trung Đ&ocirc;ng c&oacute; khả năng giết chết ngay những kỵ binh liều lĩnh x&ocirc;ng v&agrave;o. Ngo&agrave;i ra cũng n&ecirc;n n&oacute;i về c&aacute;c trận thủy chiến với t&agrave;u gỗ m&aacute;i ch&egrave;o khi xưa với những pha Roam (t&ocirc;ng) thẳng v&agrave;o t&agrave;u đối phương hoặc Sige (cập v&agrave;o mạng t&agrave;u đối phương để cho qu&acirc;n sang). Thậm ch&iacute; phi&ecirc;n bản Rome II đ&atilde; c&oacute; cải tiến đ&aacute;ng kể m&agrave; những phi&ecirc;n bản trước chưa c&oacute; ch&iacute;nh l&agrave; việc thủy binh nay đ&atilde; c&oacute; thể hỗ trợ bộ binh để c&ocirc;ng th&agrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/Testudo.jpg\"><img alt=\"Testudo\" src=\"https://guidegame.vn/wp-content/uploads/Testudo.jpg\" style=\"height:418px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&nbsp;Đội h&igrave;nh con r&ugrave;a Testudo nổi tiếng của người La M&atilde; khi c&ocirc;ng th&agrave;nh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/Phalanx.jpg\"><img alt=\"Phalanx\" src=\"https://guidegame.vn/wp-content/uploads/Phalanx.jpg\" style=\"height:466px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&nbsp;Đội h&igrave;nh Phalanx của Sparta với khi&ecirc;n giơ cao ngang vai v&agrave; gi&aacute;o g&aacute;c tr&ecirc;n khi&ecirc;ng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://guidegame.vn/wp-content/uploads/Pike-Man.jpg\"><img alt=\"Pike Man\" src=\"https://guidegame.vn/wp-content/uploads/Pike-Man.jpg\" style=\"height:466px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đội h&igrave;nh Phalanx Pikeman của Sparta với gi&aacute;o d&agrave;i chống kỵ binh cực tốt</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Phần IV: Kết</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Vẫn c&ograve;n rất nhiều thứ m&agrave; Guide Game muốn chia sẽ cho c&aacute;c bạn về Rome II Total war, thế nhưng nếu n&oacute;i hết th&igrave; c&ograve;n rất rất d&agrave;i n&ecirc;n Guide Game sẽ chỉ tổng hợp nhưng th&ocirc;ng tin n&oacute;ng của tựa game n&agrave;y để mọi người c&ograve;n hy vọng, t&igrave;m hiểu v&agrave; chơi thử. Guide Game mong rằng với những th&ocirc;ng tin tr&ecirc;n người chơi sẽ c&oacute; được c&aacute;i nh&igrave;n sơ lượt về d&ograve;ng game n&agrave;y v&agrave; sắp tới Guide Game sẽ c&ograve;n mang rất nhiều d&ograve;ng game kh&aacute;c cho mọi người, h&atilde;y c&ugrave;ng đ&oacute;n đọc nh&eacute;.</span></p>', '<p>OS: XP/ Vista / Windows 7 / Windows 8</p>\r\n\r\n<p>Processor:2 GHz Intel Dual Core processor / 2.6 GHz Intel Single Core processor</p>\r\n\r\n<p>Memory: 2GB RAM</p>\r\n\r\n<p>Graphics:512 MB DirectX 9.0c compatible card (shader model 3, vertex texture fetch support).</p>\r\n\r\n<p>DirectX&reg;:9.0c</p>\r\n\r\n<p>Hard Drive: 35 GB HD space</p>', '<p>OS: XP/ Vista / Windows 7 / Windows 8</p>\r\n\r\n<p>Processor:2 GHz Intel Dual Core processor / 2.6 GHz Intel Single Core processor</p>\r\n\r\n<p>Memory: 2GB RAM</p>\r\n\r\n<p>Graphics:512 MB DirectX 9.0c compatible card (shader model 3, vertex texture fetch support).</p>\r\n\r\n<p>DirectX&reg;:9.0c</p>\r\n\r\n<p>Hard Drive: 35 GB HD space</p>', 'Cài đặt và chơi', '15201676045069.jpg', 'https://www.youtube.com/embed/JReGDeaxaDI', 2013, '2018-03-14'),
(4, 6, 'Assassin’s Creed: Origins', 'assassin’s-creed:-origins', '<h3 style=\"text-align:justify\">Tiếp nối truyền thống tốt đẹp của d&ograve;ng game&nbsp;Assassin&rsquo;s Creed,&nbsp;Assassin&rsquo;s Creed Origins&nbsp;tiếp tục đưa game thủ đi ngược d&ograve;ng thời gian để t&igrave;m đến với những nền văn minh xưa cũ ẩn chứa v&ocirc; v&agrave;n điều b&iacute; ẩn chưa thể t&igrave;m ra lời giải đ&aacute;p. Tuy nhi&ecirc;n, kh&aacute;c với c&aacute;c phi&ecirc;n bản đang c&oacute; xu hướng &ldquo;hiện đại h&oacute;a&rdquo; trong thời gian gần đ&acirc;y,&nbsp;Assassin&rsquo;s Creed Origins&nbsp;lại đưa game thủ đến một mốc thời gian kh&aacute; xa x&ocirc;i &ndash; Ai Cập cổ đại, mốc thời gian khoảng năm 49 TCN. Đến đ&acirc;y sẽ c&oacute; kh&ocirc;ng &iacute;t người tự hỏi: &ldquo;Thời cổ đại đ&atilde; c&oacute; Templar v&agrave; Assassins Brotherhood hay sao?&rdquo;</h3>', '<p><span style=\"font-size:14px\"><strong>Assassin&rsquo;s Creed: Origins l&agrave;</strong>&nbsp;<strong>game phi&ecirc;u lưu&nbsp;h&agrave;nh động<strong>&nbsp;</strong>tiếp theo của series&nbsp;game Assassin&rsquo;s Creedđ&igrave;nh đ&aacute;m nh&agrave;&nbsp;Ubisoft. Game Assassin&rsquo;s Creed: Origins vượt qua những người đ&agrave;n anh trước đ&oacute; để mang 1 thế giới mở lớn nhất từ trước đến nay cho c&aacute;c game thủ thỏa sức kh&aacute;m ph&aacute;.</strong>&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-danh.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&oacute; thể n&oacute;i th&ocirc;ng qua Assassin&rsquo;s Creed: Origins nh&agrave; ph&aacute;t h&agrave;nh game Ubisoft đang nỗ lực lấy lại h&igrave;nh ảnh của m&igrave;nh sau những thất vọng kh&ocirc;ng đ&aacute;ng c&oacute; với nhiều c&aacute;i t&ecirc;n đ&atilde; l&agrave;m c&aacute;c game thủ kỳ vọng v&agrave; sau đ&oacute; l&agrave; chuỗi ng&agrave;y thất vọng:&nbsp;Assassin&#39;s Creed 3,&nbsp;Watch Dogs 2, vv. Chắc chắn bạn sẽ kh&ocirc;ng gặp lại những điều đ&oacute; khi bước ch&acirc;n v&agrave;o thế giới của game Assassin&rsquo;s Creed: Origins.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-hanh-dong.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&acirc;y l&agrave; phi&ecirc;n bản ch&iacute;nh thứ 10, Assassins&rsquo;s Creed Origins nhận được kh&aacute; nhiều k&igrave; vọng nhằm vực dậy lại d&ograve;ng Assassins&rsquo;s Creed. Assassins&rsquo;s Creed Origins cho thấy tham vọng của h&atilde;ng n&agrave;y v&agrave;o phi&ecirc;n bản lấy bối cảnh tại Ai Cập với rất nhiều tiểu tiết bao gồm quang cảnh nh&acirc;n vật, văn h&oacute;a của thời kỳ n&agrave;y, nếu kh&ocirc;ng l&agrave;m tốt th&igrave; Assassins&rsquo;s Creed Origins ch&iacute;nh l&agrave; sự kết th&uacute;c của Assassins&rsquo;s Creed.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-nhap-vai.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Assassin&#39;s Creed: Origins đưa người chơi đến với Ai Cập cổ đại với cuộc phi&ecirc;u lưu của g&atilde; s&aacute;t thủ Ba Yek. Trong game Assassins&rsquo;s Creed Origins thay v&igrave; s&uacute;ng đạn của c&aacute;c phi&ecirc;n bản mới gần đ&acirc;y ch&uacute;ng ta sẽ sử dụng cung t&ecirc;n l&agrave;m vũ kh&iacute; tầm xa cực kỳ tin cậy của nh&acirc;n vật ch&iacute;nh. Chủ đề của ch&uacute;ng ta ch&iacute;nh l&agrave; Ai Cập cổ đại n&oacute;i về hội s&aacute;t thủ đầu ti&ecirc;n tr&ecirc;n thế giới đ&atilde; được th&agrave;nh lập.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-nu-hoang.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Người chơi sẽ điều khiển hai nh&acirc;n vật bao gồm cả nam v&agrave; nữ với hệ thống kỹ năng ri&ecirc;ng biệt, c&aacute;c kỹ năng n&agrave;y c&oacute; thể n&acirc;ng cấp th&ocirc;ng qua hệ thống kỹ năng giống như c&aacute;c game nhập vai kh&aacute;c. Tuy nhi&ecirc;n thay v&igrave; nh&acirc;n vật sẽ sử dụng loại vũ kh&iacute; đặc trưng th&igrave; c&aacute;c nh&acirc;n vật trong game Assassins&rsquo;s Creed Origins sẽ d&ugrave;ng l&aacute; chắn cũng như c&aacute;c loại vũ kh&iacute; mang hơi hướng chiến binh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-phieu-luu.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thay v&igrave; sử dụng c&aacute;c loại vũ kh&iacute; đặc trưng của s&aacute;t thủ, nh&acirc;n vật trong Origins sẽ sử dụng cả l&aacute; chắn, thứ vũ kh&iacute; mang hơi hướng của c&aacute;c chiến binh. Theo những đồn đo&aacute;n trước đ&oacute;, Origins sẽ c&oacute; nhiều yếu tố về biển (giống như người tiền nhiệm Black Flag). Tuy nhi&ecirc;n những yếu tố đ&oacute; lại kh&ocirc;ng thấy xuất hiện trong bức h&igrave;nh quảng c&aacute;o n&agrave;y. Do vậy, ch&uacute;ng ta vẫn chưa thể khẳng định được Assassin&rsquo;s Creed: Origins sẽ c&oacute; những m&agrave;n chơi tr&ecirc;n biển hay kh&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-sat-thu.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Để c&oacute; thể di chuyển thuận tiện trong thế giới mở rộng lớn của Ai Cập, ch&uacute;ng ta c&oacute; thể lựa chọn cưỡi th&uacute; như: ngựa v&agrave; lạc đ&agrave;, di chuyển đường thủ bằng thuyền tr&ecirc;n s&ocirc;ng Nile thơ mộng v&agrave; lặn s&ocirc;ng cũng l&agrave; 1 trải nghiệm v&ocirc; c&ugrave;ng th&uacute; vị. Tuy nhi&ecirc;n để c&oacute; được những lựa chọn tr&ecirc;n bạn phải t&igrave;m kiếm v&agrave; thu thập trong suốt h&agrave;nh tr&igrave;nh để c&oacute; được thứ tốt nhất cho chuyến đi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://ig.down.vn/data/image/2017/12/12/Assassins-Creed-Origins-tan-cong.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chuyến h&agrave;nh tr&igrave;nh v&ocirc; số những thử nghiệm sẽ để lại nhiều ấn tượng cho c&aacute;c game thủ giống như 1 chuyến đi bụi xuy&ecirc;n qua Ai Cập của d&acirc;n du lịch. Bạn c&oacute; cơ hội bắt c&aacute; bằng mũi t&ecirc;n đ&acirc;m xuy&ecirc;n m&aacute;u, nhưng l&uacute;c n&agrave;y m&aacute;u loang ra s&ocirc;ng c&oacute; thể l&ocirc;i k&eacute;o c&aacute; sấu đến g&acirc;y hại cho bạn. C&ugrave;ng đ&oacute; l&agrave; rất nhiều h&agrave;nh động bộc ph&aacute;t v&agrave; thuận theo tự nhi&ecirc;n cứ thế diễn ra khiến cho cuộc phi&ecirc;u lưu của Assassin&rsquo;s Creed: Origins trở n&ecirc;n hấp dẫn hơn bao giờ hết. Tải game Assassin&rsquo;s Creed: Origins để bắt đầu tham gia cuộc phi&ecirc;u lưu kỳ th&uacute; n&agrave;y c&ugrave;ng nh&oacute;m s&aacute;t thủ chuy&ecirc;n nghiệp.</span></p>', '<p>Hệ Điều H&agrave;nh: Windows 7, Windows 8, Windows 8.1 v&agrave; Windows 10 (64bit)</p>\r\n\r\n<p>Bộ vi xử l&yacute;: Intel Core i5-2400s @ 2.5 GHz or AMD FX-6350 @ 3.9 GHz hoặc tương đương</p>\r\n\r\n<p>Bộ nhớ RAM: 6 GB</p>\r\n\r\n<p>Ổ cứng c&agrave;i đặt game trống: 42 GB</p>\r\n\r\n<p>DirectX: phi&ecirc;n bản 11</p>\r\n\r\n<p>Đồ họa: NVIDIA GeForce GTX 660 or AMD R9 270 (2048 MB VRAM with Shader Model 5.0 hoặc tốt hơn)</p>', '<p>Hệ Điều H&agrave;nh: Windows 7, Windows 8, Windows 8.1 v&agrave; Windows 10 (64bit)</p>\r\n\r\n<p>Bộ vi xử l&yacute;: Intel Core i5-2400s @ 2.5 GHz or AMD FX-6350 @ 3.9 GHz hoặc tương đương</p>\r\n\r\n<p>Bộ nhớ RAM: 6 GB</p>\r\n\r\n<p>Ổ cứng c&agrave;i đặt game trống: 42 GB</p>\r\n\r\n<p>DirectX: phi&ecirc;n bản 11</p>\r\n\r\n<p>Đồ họa: NVIDIA GeForce GTX 660 or AMD R9 270 (2048 MB VRAM with Shader Model 5.0 hoặc tốt hơn)</p>', 'Cài đặt và chơi', '1520168328ss_1a0ab42e93e51c48ad9a6bda7f408958e09a43b0.1920x1080.jpg', 'https://www.youtube.com/embed/8QCVi7kfgrw', 2017, '2018-03-14');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(5, 8, 'Pro Evolution Soccer 2018', 'pro-evolution-soccer-2018', '<h3>Tựa game b&oacute;ng đ&aacute; được mong chờ nhất tại Việt Nam sắp ra l&ograve; với những bổ sung th&uacute; vị. PES 2018 hứa hẹn c&oacute; đồ hoạ đẹp mắt, lối chơi ch&acirc;n thật v&agrave; rực lửa hơn so phi&ecirc;n bản 2017.</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ng&agrave;y đầu ti&ecirc;n tại E3 2017, PES 2018 đ&atilde; đem đến trailer mới toanh c&ugrave;ng chi tiết c&aacute;c t&iacute;nh năng mới khiến c&aacute;c fan ngất ng&acirc;y.</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trailer E3 của PES 2018 nổi bật nhất trong show diễn,&nbsp;ch&uacute;ng ta h&atilde;y c&ugrave;ng chi&ecirc;m ngưỡng&nbsp;v&agrave; ph&acirc;n t&iacute;ch&nbsp;c&aacute;c t&iacute;nh năng v&agrave; th&ocirc;ng tin của phi&ecirc;n bản Pro Evolution Soccer tiếp theo&nbsp;được Konami đem đến:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>1. Gameplay MasterClass:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>a. R&ecirc; b&oacute;ng chiến thuật:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện việc điều khiển: Cảm nhận việc điều khiển to&agrave;n bộ khi đang kiểm so&aacute;t b&oacute;ng, khả năng tạo được những bước di chuyển nhỏ v&agrave; quyết định để duy tr&igrave; tr&aacute;i b&oacute;ng hay tạo khoảng trống.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_01_dribbling_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thể hiện vật l&yacute;: Việc điều khiển b&oacute;ng th&ocirc;ng minh cho ph&eacute;p người chơi giữ b&oacute;ng tr&aacute;nh xa khỏi c&aacute;c cầu thủ khỏe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">R&ecirc; b&oacute;ng chiến thu&acirc;t: Điều khiển việc chạm b&oacute;ng theo toan t&iacute;nh thời gian chỉ qua việc điều khiển&nbsp;tr&ecirc;n tay cầm hoặc b&agrave;n ph&iacute;m. Dễ d&agrave;ng r&ecirc; b&oacute;ng qua người khi đưa b&oacute;ng v&agrave;o chận kh&ocirc;ng thuận của hậu vệ hoặc d&ugrave;ng giả s&uacute;t&nbsp;&quot;vu&ocirc;ng X&quot;.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>b. Real Touch+ (chạm thực tế):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chạm&nbsp;to&agrave;n bộ cơ thể: Kiểm so&aacute;t mượt m&agrave; hơn th&ocirc;ng qua chuyển động thực tế từ c&aacute;c bộ phận tr&ecirc;n cơ thể như ngực, bụng v&agrave; đ&ugrave;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhận thức xung quanh: Cầu thủ sẽ ch&uacute; &yacute; xung quanh khi nhận b&oacute;ng, thường th&igrave; sẽ đẩy cơ thể v&agrave;o giữa đối phương v&agrave; tr&aacute;i b&oacute;ng. Ri&ecirc;ng với những cầu thủ khỏe sẽ c&oacute; nhiều giải ph&aacute;p hơn trong việc giữ b&oacute;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_02_opposition_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vật l&yacute; b&oacute;ng thực tế: Cải thiện độ thực tế như việc nẩy b&oacute;ng,&nbsp;với những cầu thủ c&oacute; kỹ thuật tốt sẽ điều khiển mượt m&agrave; hơn theo từng t&igrave;nh huống b&oacute;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>c. T&igrave;nh huống b&oacute;ng chết mới:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phạt g&oacute;c/Đ&aacute; phạt: Cải thiện việc điều khiển khi loại bỏ phần hướng dẫn.&nbsp;Với lựa chọn g&oacute;c&nbsp;camera mới sẽ đem lại cho game thủ c&aacute;i nh&igrave;n tốt nhất để đ&aacute; phạt. Người chơi cũng sẽ nh&igrave;n thấy nhiều h&agrave;nh động hơn khi bước l&ecirc;n đ&aacute; phạt g&oacute;c, trong khi việc điều khiển chiến thuật c&oacute; thể được thực hiện v&agrave;o thời điểm thực hiện c&uacute; đ&aacute; đem đến sự kịch t&iacute;nh hơn cho trận đấu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_03_corner-kick_01_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_1_04_corner-kick_02_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>2. Cải thiện t&iacute;nh thực tế của h&igrave;nh ảnh:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>a. Khu&ocirc;n mẫu cầu thủ ch&iacute;nh x&aacute;c:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Qu&eacute;t h&igrave;nh ảnh to&agrave;n bộ cơ thể cầu thủ: Sử dụng dữ liệu scan thật để t&aacute;i tạo lại 1 c&aacute;ch ho&agrave;n hảo cầu thủ bao gồm cả cỡ &aacute;o hay độ vừa vặn khi mặc &aacute;o đấu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_01_playermodels_pes.jpg\" style=\"height:394px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>b. Thực hiện lại chuyển động:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&acirc;n t&iacute;ch mở rộng: C&aacute;c chuyển động được l&agrave;m lại từ đầu, bắt đầu từ những chuyển động nhỏ như đi bộ, xoay v&agrave; tư thế cầu thủ</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chuyển động cơ thể: Với h&agrave;ng trăm chuyển động được đưa v&agrave;o game, sự tự nhi&ecirc;n sẽ được thể hiện chưa từng c&oacute; trong d&ograve;ng game PES.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Biểu cảm khu&ocirc;n mặt: Chuyển động khu&ocirc;n mặt được n&acirc;ng cấp đ&aacute;ng kể, cho ph&eacute;p người chơi xem được những biến chuyển phức tạp trong cảm x&uacute;c với bất kỳ t&igrave;nh huống n&agrave;o.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thủ m&ocirc;n: Người g&aacute;c đền được đ&aacute;nh gi&aacute; cao sẽ c&oacute; bước cải tiến lớn, Ngo&agrave;i ra c&oacute; th&ecirc;m nhiều chuyển động mới cho thủ m&ocirc;n, GK sẽ giữ b&oacute;ng tự nhi&ecirc;n hơn sau mỗi pha bắt b&oacute;ng v&agrave; n&eacute;m b&oacute;ng th&ocirc;ng minh hơn khi chuyền cho đồng đội.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_02_goalkeepers_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Yếu tố hiển thị mới: Bộ HUD mới (bảng tỉ số, radar,...) sẽ bao gồm chỉ số trong trận đấu của từng cầu thủ v&agrave; đội b&oacute;ng. C&aacute;c cầu thủ cũng sẽ được sử dụng ảnh thật trong PES 2018. C&aacute;c cảnh cắt như thay người hay l&uacute;c trọng t&agrave;i phạt thẻ sẽ được th&ecirc;m mới. Ngo&agrave;i ra, l&uacute;c cầu thủ lập hat-trick cũng sẽ c&oacute; ph&acirc;n cảnh kh&aacute;c ho&agrave;n to&agrave;n PES 2017.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_03_matchexperience_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>c. H&igrave;nh ảnh đời thật&nbsp;(chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&Aacute;nh s&aacute;ng thực: Với việc sử dụng nghi&ecirc;n cứu dữ liệu từ c&aacute;c s&acirc;n vận động đối t&aacute;c như Nou Camp hay Signal Iduna-Park, PES 2018 sẽ đem đến &aacute;nh s&aacute;ng thật của mặt cỏ.&nbsp;H&igrave;nh ảnh cầu thủ trong đường hầm cả l&uacute;c ban ng&agrave;y lẫn ban đ&ecirc;m cũng được dựa tr&ecirc;n dữ liệu thực tế thu thập từ c&aacute;c trận b&oacute;ng ngo&agrave;i đời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_2_04_lighting_pes.jpg\" style=\"height:274px; width:888px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&ograve;ng thay đồ: H&igrave;nh ảnh ph&ograve;ng thay đồ sẽ mang lại cảm gi&aacute;c thực tế hơn cho PES 2018</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&aacute;n giả: H&igrave;nh ảnh kh&aacute;n giả v&agrave; cảm x&uacute;c của họ với từng pha b&oacute;ng tr&ecirc;n s&acirc;n được cải thiện theo c&aacute;ch tự nhi&ecirc;n nhưng vẫn đầy kịch t&iacute;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>3. Chế độ Online Co-op (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thi đấu với bạn b&egrave;: Chế độ mới hỗ trợ chơi 2v2 v&agrave;&nbsp;3v3. cho ph&eacute;p người chơi thi đấu c&ugrave;ng bạn b&egrave; hoặc người kh&aacute;c với h&agrave;ng triệu đối thủ kh&aacute;c tr&ecirc;n thế giới</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chơi với phong c&aacute;ch của bạn: Bạn c&oacute; thể tạo nh&oacute;m với việc gh&eacute;p đ&ocirc;i tự động hoặc tạo 1 clan c&ugrave;ng bạn b&egrave; của m&igrave;nh để đua rank.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Huấn luyện vi&ecirc;n Co-op: Hỗ trợ 2 kh&aacute;ch chơi tr&ecirc;n c&ugrave;ng 1 m&aacute;y console v&agrave; chơi 3 người c&ugrave;ng 1 đội khi đ&aacute; online</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_3_01_online-coop_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_3_01_online-coop_result_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_3_01_online-coop_profile_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hợp nhất c&aacute;c chế độ: Chơi 3v3 qua c&aacute;c chế độ bao gồm myClub v&agrave; PES League</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>4. C&aacute;c chế độ v&agrave; t&iacute;nh năng mới (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>a. myClub:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hợp nhất PES League: Thi đấu c&aacute;c giải đấu eSports với myClub</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện việc đấu gi&aacute; cầu thủ: C&aacute;c chức năng như t&igrave;m kiếm dữ liệu những loại cầu thủ ch&iacute;nh x&aacute;c m&agrave; người chơi muốn. lịch sử c&aacute;c cuộc đấu gi&aacute; trong qu&aacute; khứ v&agrave; tự động đặt gi&aacute; đấu thầu được đưa v&agrave;o game.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">myClub Online Co-op: X&acirc;y dựng đội Co-op của bạn c&ugrave;ng với bạn b&egrave;,&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>b. Master League:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thử th&aacute;ch v&agrave; mục ti&ecirc;u: Ở đầu m&ugrave;a giải mới, bạn sẽ được quyền đặt mục ti&ecirc;u dựa v&agrave;o t&igrave;nh h&igrave;nh của đội b&oacute;ng. Đ&acirc;y l&agrave; điều quan trọng gi&uacute;p bạn giữ được c&ocirc;ng việc HLV.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_4_02_challenges_pes.jpg\" style=\"height:500px; width:745px\" />&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Giải đấu trước m&ugrave;a giải: Thi đấu với c&aacute;c đội b&oacute;ng lớn trước khi m&ugrave;a giải ch&iacute;nh thức bắt đầu</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện hệ thống chuyển nhương: Th&ecirc;m những yếu tố mới như giải ph&oacute;ng ph&iacute; chuyển nhượng trong hợp đồng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c yếu tố mới: Menu mới, phỏng vấn trước trận đấu v&agrave; cảnh ở ph&ograve;ng thay đồ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>c. Random Selection Match:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chế độ y&ecirc;u th&iacute;ch trở lại: Đặt ti&ecirc;u ch&iacute; của người chơi với cầu thủ được chọn sẽ trở th&agrave;nh đội b&oacute;ng của bạn</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hệ thống mua b&aacute;n: Khi bạn đ&atilde; c&oacute; những cầu thủ của m&igrave;nh, h&atilde;y thử v&agrave; mặc cả việc trao đổi cầu thủ m&agrave; bạn muốn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_4_03_trade_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>5. Tu sửa lại giao diện (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menu: Menu được thiết kế lại theo phong c&aacute;ch hiện đại hơn. HUD in-game (bảng tỉ số, radar,...) đem đến cảm gi&aacute;c giống như xem truyền h&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/main_new_feature_5_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ảnh thật cầu thủ: H&igrave;nh ảnh thật của cầu thủ được đưa v&agrave;o Game Plan v&agrave; m&agrave;n h&igrave;nh hiển thị chỉ số trong trận</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_5_01_life_photos_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>6. Hợp nhất PES League (chỉ c&oacute; tr&ecirc;n PS4, Xbox One v&agrave; PC):&nbsp;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_6_01_pesleague_pes.jpg\" style=\"height:500px; width:745px\" /></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">PES League mở rộng th&ecirc;m c&aacute;c chế độ như myClub, Co-op v&agrave; Random Selection Match</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&acirc;n bằng thống k&ecirc; người chơi: Cho ph&eacute;p tất cả c&aacute;c đội c&oacute; chỉ số trung b&igrave;nh giống nhau.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>7. Cải thiện nền tảng PC:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đồ họa v&agrave; nội dung của PC đảm bảo chất lượng như PS4 v&agrave; Xbox One</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_topic_new_feature_7_01_pc_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>8. PES 2018 Online Beta:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/main_open_beta_test_pes.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">PES 2018 Online Beta sẽ được Konami ph&aacute;t h&agrave;nh tr&ecirc;n PS4 v&agrave; Xbox One từ th&aacute;ng 7 năm nay. Phi&ecirc;n bản n&agrave;y sẽ bao gồm chế độ Quickmatch v&agrave; Online Co-op.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>9. Usain Bolt v&agrave; Maradona&nbsp;trở th&agrave;nh đại sứ mới của PES:</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/bolt_img_01.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/bolt_img_02.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_maradona_face.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://www.konami.com/wepes/2018/s/img/topic/img_maradona_dribble.jpg\" style=\"height:500px; width:745px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể n&oacute;i, với những thay đổi đ&aacute;ng kể như tr&ecirc;n, PES 2018 với&nbsp;kỳ vọng l&agrave; &quot;phi&ecirc;n bản PES hay nhất 10 năm qua&quot; đang dần dần lấy lại niềm tin nơi người h&acirc;m mộ v&agrave; chắc chắn sẽ khiến kh&ocirc;ng &iacute;t c&aacute;c fan phải nao l&ograve;ng.</span></span></p>', '<p>OS: Windows 7 64 bit (32 bit NOT supported)</p>\r\n\r\n<p>Processor: Intel Core i5-3230M @ 2.60GHz or equivalent AMD processor and above</p>\r\n\r\n<p>Memory: 4 GB RAM</p>\r\n\r\n<p>Graphics: 256 MB DX 9 Compliant videocard with pixel shader 3,0</p>\r\n\r\n<p>DirectX: Version 9.0c</p>\r\n\r\n<p>Storage: 2 GB available space</p>\r\n\r\n<p>Sound Card: DirectX 9 Compatible Audio Additional</p>\r\n\r\n<p>Notes: Minimum Resolution: 1024 x 768</p>', '<p>OS: Windows 7 64 bit (32 bit NOT supported)</p>\r\n\r\n<p>Processor: Intel Core i5-3230M @ 2.60GHz or equivalent AMD processor and above</p>\r\n\r\n<p>Memory: 4 GB RAM</p>\r\n\r\n<p>Graphics: 256 MB DX 9 Compliant videocard with pixel shader 3,0</p>\r\n\r\n<p>DirectX: Version 9.0c</p>\r\n\r\n<p>Storage: 2 GB available space</p>\r\n\r\n<p>Sound Card: DirectX 9 Compatible Audio Additional</p>\r\n\r\n<p>Notes: Minimum Resolution: 1024 x 768</p>', 'Cài đặt và chơi', '1520169876U5dtzfc4c23qQ9fzoRH5mLqtWDSQchQ_1680x8400.jpg', 'https://www.youtube.com/embed/jGgNxN0rQYw', 2017, '2018-03-14'),
(6, 7, 'Little Nightmares', 'little-nightmares', '<h3>Một tựa game phi&ecirc;u lưu giải đố c&oacute; phong c&aacute;ch kh&aacute; giống với Limbo hay Inside. Một sản phẩm của BANDAI NAMCO dưới sự ph&aacute;t triển của Tarsier Studios, những người đ&atilde; l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng cho series LittleBigPlanet.&nbsp;</h3>', '<h1><span style=\"font-size:14px\"><strong>Những cơn &aacute;c mộng của tuổi thơ sẽ quay lại dưới dạng một tựa game h&agrave;nh động m&agrave;n h&igrave;nh ngang với t&ecirc;n gọi Little Nightmares.</strong></span></h1>\r\n\r\n<p><span style=\"font-size:14px\">Trong tuổi thơ của từng người, lu&ocirc;n c&oacute; những khoảnh khắc sợ h&atilde;i vu vơ như bị &ocirc;ng kẹ bắt, hay qu&aacute;i vật đang trốn dưới gầm giường của m&igrave;nh mỗi đ&ecirc;m. Khi bạn c&agrave;ng lớn l&ecirc;n, những cơn &aacute;c mộng vu vơ&nbsp;n&agrave;y&nbsp;dần biến mất v&agrave; bạn hầu như chỉ xem ch&uacute;ng như một c&acirc;u chuyện cười về sự ng&acirc;y thơ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thế nhưng mới đ&acirc;y, những cơn &aacute;c mộng sẽ quay lại dưới dạng một tựa game h&agrave;nh động m&agrave;n h&igrave;nh ngang với t&ecirc;n gọi Little Nightmares. Đảm bảo sẽ gi&uacute;p bạn bạn một lần nữa trải nghiệm những nỗi &aacute;m ảnh kinh ho&agrave;ng nhất ẩn giấu trong tuổi thơ&nbsp;của&nbsp;một đứa thơ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-7.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-7.jpg\" style=\"height:419px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">V&agrave;o vai một đứa trẻ b&iacute; ẩn t&ecirc;n l&agrave; Six mang tr&ecirc;n m&igrave;nh một tấm &aacute;o kho&aacute;c m&agrave;u v&agrave;ng ch&oacute;i mắt cực k&igrave; tương phản với thế giới k&igrave; quặc v&agrave; u &aacute;m trong game. Trong thế giới m&agrave;n h&igrave;nh ngang 3D của Little Nightmares, Six phải vượt qua một loạt những căn ph&ograve;ng&nbsp;kh&aacute;c nhau được dựng n&ecirc;n bởi những linh hồn lạc lối để đi đến c&aacute;i đ&iacute;ch cuối c&ugrave;ng một c&aacute;ch an to&agrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-8.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-8.jpg\" style=\"height:419px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Little Nightmares mang mang phong c&aacute;ch nghệ thuật&nbsp;ri&ecirc;ng trong thiết kế của m&igrave;nh nhưng cũng đồng thời cực k&igrave; nghệ thuật. Một ch&uacute;t phong c&aacute;ch của Tim Burton pha trộn với những h&igrave;nh ảnh mập mờ trong bối cảnh tranh tối tranh s&aacute;ng mang đến một ch&uacute;t cảm gi&aacute;c kh&oacute; thở v&agrave; bất an cho game thủ trong qu&aacute; tr&igrave;nh chơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Little Nightmares ho&agrave;n to&agrave;n kh&ocirc;ng&nbsp;lạm dụng jump scare như nhiều tựa game kinh dị kh&aacute;c hiện nay m&agrave; dựa nhiều v&agrave;o c&aacute;ch tạo ra bầu kh&ocirc;ng kh&iacute; căng thẳng, hồi hộp với những thứ m&agrave; m&igrave;nh chưa biết, như liệu trong khoảng trống trước mặt c&oacute; c&aacute;nh tay ma n&agrave;o đ&oacute; đang chực chờ t&oacute;m lấy m&igrave;nh hay kh&ocirc;ng? Bạn chỉ muốn trốn tho&aacute;t khỏi thế giới trong game c&agrave;ng nhanh c&agrave;ng tốt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-6.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-6.jpg\" style=\"height:419px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhưng kẻ th&ugrave; của bạn trong game l&agrave; những sinh vật h&igrave;nh người nhưng to lớn hơn bạn đến chục lần, v&agrave; bạn kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o tr&aacute;nh được ch&uacute;ng n&ecirc;n tốt hết l&agrave; cứ tr&aacute;nh đi th&ocirc;i. Đ&oacute; l&agrave; tất cả những nổi sợ được hiện thực ho&aacute; để trừng phạt bất cứ khi n&agrave;o ch&uacute;ng ph&aacute;t hiện được bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-5.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-5.jpg\" style=\"height:419px; width:745px\" /></a><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-3.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-3.jpg\" style=\"height:419px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần &acirc;m thanh trong game cũng l&agrave; một bước tiến đồng h&agrave;nh với đồ hoạ, với những sinh vật k&ecirc;u g&agrave;o đi&ecirc;n cuồng khiến bạn cảm thấy ph&aacute;t bệnh. &Acirc;m nhạc trở n&ecirc;n nhẹ nh&agrave;ng một c&aacute;ch ma qu&aacute;i trong những trường đoạn l&eacute;n l&uacute;t rồi đột nhi&ecirc;n trở n&ecirc;n căng thẳng khi bạn bị ph&aacute;t hiện như k&iacute;ch th&iacute;ch từng d&acirc;y thần kinh của nỗi sợ trong người của bạn vậy.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-1.jpg\" rel=\"prettyPhoto[pp_gal]\"><img alt=\"\" src=\"http://www.xemgame.com/data/pictures/xemgame/2017/04/27/little-nightmares-1.jpg\" style=\"height:419px; width:745px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Little Nightmares c&oacute; thể khiến người ta gợi nhớ nhiề đến c&aacute;c tựa game tương tự l&agrave;&nbsp;Limbo&nbsp;v&agrave; Inside. Cũng lối chơi đi cảnh giải đố, cũng phong c&aacute;ch đồ hoạ độc nhất, sự vận dụng b&oacute;ng tối v&agrave; cắt bỏ ho&agrave;n to&agrave;n phần lời thoại để tập trung v&agrave;o gameplay. N&ecirc;n đ&acirc;y sẽ l&agrave; lựa chọn kh&ocirc;ng thể bỏ qua nếu bạn đ&atilde; từng m&ecirc; mẩn 2 tựa game ở tr&ecirc;n.</span></p>', '<p>OS: Windows 7 64-bit</p>\r\n\r\n<p>CPU: Intel Core i3-2115C 2.0 GHz or AMD Athlon II X3 460 3.4 GHz</p>\r\n\r\n<p>RAM: 6 GB System Memory</p>\r\n\r\n<p>GPU VRAM: 1.5 GB Video Memory</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 480 or AMD Radeon R7 265</p>\r\n\r\n<p>DX: DirectX 11</p>', '<p>OS: Windows 7 64-bit</p>\r\n\r\n<p>CPU: Intel Core i3-2115C 2.0 GHz or AMD Athlon II X3 460 3.4 GHz</p>\r\n\r\n<p>RAM: 6 GB System Memory</p>\r\n\r\n<p>GPU VRAM: 1.5 GB Video Memory</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 480 or AMD Radeon R7 265</p>\r\n\r\n<p>DX: DirectX 11</p>', 'Cài đặt và chơi', '1520172192maxresdefault (1).jpg', 'https://www.youtube.com/embed/aOadxZBsPiA', 2017, '2018-03-14'),
(7, 4, 'The Sims 4', 'the-sims-4', '<h3 style=\"text-align:justify\">D&ograve;ng game The Sim của EA từ l&uacute;c ra mắt phi&ecirc;n bản đầu ti&ecirc;n đến nay đ&atilde; trở th&agrave;nh một hiện tượng v&agrave; tượng đ&agrave;i vững chắc của thể loại&nbsp; game m&ocirc; phỏng. Kh&ocirc;ng c&oacute; tựa game n&agrave;o trong gần một thập kỉ c&oacute; thể thay thế vị tr&iacute; của seri The Sim trong mắt của game thủ. Sau gần 5 năm thống trị v&agrave; l&agrave;m mửa l&agrave;m gi&oacute; với phi&ecirc;n bản The Sim 3 k&egrave;m h&agrave;ng chục DLC đi k&egrave;m, EA đ&atilde; quyết định khởi động lại d&ograve;ng game n&agrave;y với phi&ecirc;n bản The Sim 4.&nbsp;</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau thời gian thống trị thị trường game m&ocirc; phỏng m&agrave; gần như kh&ocirc;ng một h&atilde;ng game hay tựa game n&agrave;o c&oacute; thể hất đổ được vị tr&iacute; ho&agrave;ng kim của The Sims 3, EA cảm thấy đ&atilde; đến l&uacute;c tạo n&ecirc;n một vị &ldquo;vua&rdquo; mới đễ thay thế tựa game 5 năm tuổi của m&igrave;nh, với sự hứa hẹn sẽ l&agrave; cuộc đột ph&aacute; về lối chơi v&agrave; những cải tiến đ&aacute;ng gi&aacute; hấp dẫn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trong The Sims 4, vẫn như mọi khi, người chơi sẽ được điều khiển một nh&acirc;n vật Sim v&agrave; ho&agrave;n to&agrave;n x&acirc;y dựng kiểm so&aacute;t cuộc sống của anh ta hoặc c&ocirc; ta. Khả năng c&aacute; nh&acirc;n h&oacute;a nh&acirc;n vật của game cực k&igrave; đa dạng khi người chơi c&oacute; rất nhiều những lựa chọn để tạo ra một nh&acirc;n vật Sim m&agrave; họ muốn, từ v&oacute;c d&aacute;ng, kiểu c&aacute;ch, c&acirc;n nặng, c&aacute; t&iacute;nh, t&oacute;c tai, gi&agrave;y d&eacute;p, quần &aacute;o v&agrave; đặc biệt l&agrave; to&agrave;n bộ khu&ocirc;n mặt của nh&acirc;n vật. Ở The Sims 4, hệ thống tạo dựng khu&ocirc;n mặt của game được n&acirc;ng cấp th&ecirc;m rất nhiều, người chơi ho&agrave;n to&agrave;n c&oacute; thể tạo ra những khu&ocirc;n mặt c&oacute; thật nếu kh&eacute;o tay v&agrave; tinh &yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"The-Sims-4 1\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/15\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Khả năng tương t&aacute;c của game cũng được tinh chỉnh, bạn c&oacute; thể chia sẻ nh&acirc;n vật v&agrave; cuộc sống của nh&acirc;n vật với những người chơi kh&aacute;c, tạo th&agrave;nh một x&atilde; hội ảo thu nhỏ cực k&igrave; th&uacute; vị. Bạn c&oacute; thể t&ugrave;y chỉnh t&iacute;nh c&aacute;ch, phong c&aacute;ch của Sim qua những loại quần &aacute;o c&oacute; sẵn hoặc tải th&ecirc;m từ trang chủ bởi v&igrave; ở phi&ecirc;n bản gốc, s&ocirc; lượng quần &aacute;o đồ đạc c&oacute; thể n&oacute;i l&agrave; c&oacute; phần hạn chế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chức năng tự x&acirc;y dựng nh&agrave; cửa cũng được giữ lại trong The Sims 4. Người chơi c&oacute; thể tự x&acirc;y dựng ng&ocirc;i nh&agrave; th&acirc;n y&ecirc;u của m&igrave;nh từ gốc đến rễ theo sở th&iacute;ch hoặc mua lại những căn hộ c&oacute; sẵn v&agrave; tự cải tiến dần th&ecirc;m. Khả năng chỉnh sửa n&acirc;ng cấp trong phi&ecirc;n bản gốc c&oacute; thể n&oacute;i chỉ dừng ở mức tạm chấp nhận được nếu như so với những g&igrave; người chơi c&oacute; thể l&agrave;m ở The Sims 3. Tuy nhi&ecirc;n, điều n&agrave;y cũng kh&ocirc;ng hẳn l&agrave; điểm yếu của game bởi v&igrave; như bạn đ&atilde; biết đấy, EA nổi tiếng với&nbsp;<a href=\"https://guidegame.vn/champions/vi\" title=\"vi\">vi</a>ệc &ldquo;h&uacute;t m&aacute;u&rdquo; người h&acirc;m mộ qua những bản DLC bổ sung cơ m&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Sims4-057\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/13\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Một trong những thay đổi dễ nhận ra nhất, đ&oacute; ch&iacute;nh l&agrave; nh&agrave; sản xuất đ&atilde; ch&uacute; &yacute; hơn v&agrave;o những xu hướng x&atilde; hội mới mẻ của cuộc sống thật v&agrave; mang v&agrave;o game một c&aacute;ch rất th&uacute; vị. Những h&agrave;nh động như chụp ảnh tự sướng, cập nhật trạng th&aacute;i cuộc sống tr&ecirc;n những trang mạng x&atilde; hội Sim Social, d&ugrave;ng phần mềm chỉnh sửa h&igrave;nh ảnh để bức h&igrave;nh lung linh hay thậm ch&iacute; l&agrave; tự khởi nghiệp th&ocirc;ng qua những m&ocirc; h&igrave;nh hoạt động nh&oacute;m. Những yếu tố n&agrave;y khiến game trở n&ecirc;n sống động v&agrave; ch&acirc;n thật hơn những phiển bản tiền nhiệm rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Một cải tiến đ&aacute;ng khen ngợi nhất, đ&oacute; l&agrave; nh&acirc;n vật Sim của người chơi trong The Sims 4 c&oacute; thể thực hiện nhiều h&agrave;nh động c&ugrave;ng một l&uacute;c chứ kh&ocirc;ng cần phải dừng h&agrave;nh động n&agrave;y để l&agrave;m việc kh&aacute;c hay chờ đợi nữa. C&ograve;n g&igrave; tuyệt vời hơn việc vừa đọc s&aacute;ch vừa ăn s&aacute;ng hay vừa nghe nhạc vừa trồng c&acirc;y. Đặc biệt nhất l&agrave; những cuộc tr&ograve; chuyện giờ đ&acirc;y đ&atilde; kh&ocirc;ng c&ograve;n bị giới hạn chỉ 2 người với nhau m&agrave; c&oacute; thể từ 3 đến 5 người c&ugrave;ng một l&uacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Sims4-4\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/10\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Nhưng chức năng m&agrave; EA tự h&agrave;o nhất về The Sims 4 kh&ocirc;ng phải l&agrave; khả năng tương t&aacute;c của nh&acirc;n vật đối với thế giới xung xanh, m&agrave; l&agrave; sự thay đổi nội t&acirc;m cảm x&uacute;c nh&acirc;n vật của Sim, thứ m&agrave; The Sims 3 kh&ocirc;ng hề c&oacute;. Mỗi một Sim, t&ugrave;y v&agrave;o những t&iacute;nh c&aacute;ch m&agrave; người chơi đ&atilde; chọn l&uacute;c bắt đầu game. Kh&ocirc;ng những thế, những yếu tố b&ecirc;n ngo&agrave;i cũng sẽ t&aacute;c động rất nhiều l&ecirc;n cảm x&uacute;c của nh&acirc;n vật. Nếu như nh&acirc;n vật Sim vừa mới bị mất việc, anh ta hoặc c&ocirc; ta sẽ buồn ch&aacute;n v&agrave; l&agrave;m hỏng những h&agrave;nh động sau đ&oacute;, hoặc l&uacute;c Sim vừa mới &ldquo;cưa&rdquo; được người bạn t&igrave;nh y&ecirc;u dấu l&acirc;u năm của m&igrave;nh, Sim đ&oacute; sẽ rất hưng phấn v&agrave; vui vẻ, khiến những việc li&ecirc;n quan đến cảm x&uacute;c n&agrave;y cũng được hưởng l&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuy c&oacute; nhiều cải tiến l&agrave; v&acirc;y nhưng kh&ocirc;ng phải l&agrave; game kh&ocirc;ng c&oacute; khuyết điểm. Điều đầu ti&ecirc;n dễ nhận ra nhất, đ&oacute; l&agrave; kh&ocirc;ng gian hoạt động của tr&ograve; chơi bị b&oacute;p lại một c&aacute;ch rất khủng khiếp. Kh&ocirc;ng c&ograve;n những th&agrave;nh phố rộng m&ecirc;nh m&ocirc;ng với đủ thứ để kh&aacute;m ph&aacute;, giờ đ&acirc;y trong The Sims 4, tất cả những g&igrave; bạn c&ograve;n lại khi bước ra khỏi nh&agrave; chỉ l&agrave; một khu phố nhỏ với những ng&ocirc;i nh&agrave; v&agrave; khu vực tr&ocirc;ng na n&aacute; nhau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"The-Sims-4 2\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/12\" style=\"height:421px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Thậm ch&iacute; với bản đồ nhỏ l&agrave; vậy, mỗi khi đi đến một khu vực n&agrave;o đ&oacute;, người chơi sẽ phải nh&igrave;n m&agrave;n h&igrave;nh loading li&ecirc;n tục đến ph&aacute;t mệt m&otilde;i, cho d&ugrave; chỉ l&agrave; đi đến nh&agrave; của anh h&agrave;ng x&oacute;m n&agrave;o đ&oacute; ngay cạnh nh&agrave; của m&igrave;nh. Nếu như so s&aacute;nh với những g&igrave; m&agrave; The Sims 3 đ&atilde; l&agrave;m được với thế giới mở của n&oacute;, thật sự v&agrave;o thời điểm n&agrave;y, The Sims 4 đ&atilde; kh&ocirc;ng thể s&aacute;nh được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Khi th&agrave;nh phố bị r&uacute;t ngắn như vậy, một v&agrave;i t&iacute;nh năng nho nhỏ cũng bị cắt x&eacute;n bớt đi, một trong số đ&oacute; l&agrave; việc sử dụng phương tiện di chuyển, hay n&oacute;i c&aacute;ch kh&aacute;c, đ&oacute; l&agrave; bạn kh&ocirc;ng c&oacute; cơ hội để tự sở hữu một chiếc xế y&ecirc;u cho ri&ecirc;ng m&igrave;nh nữa. C&ograve;n đ&acirc;u cảm gi&aacute;c l&acirc;ng l&acirc;ng kh&oacute; tả khi tiết kiệm một đống tiền để chạm tay v&agrave;o chiếc xe y&ecirc;u th&iacute;ch, c&ugrave;ng n&oacute; dạo quanh những h&agrave;ng c&acirc;y dọc bờ biển v&agrave; đi &ldquo;l&ograve;e&rdquo; với bạn g&aacute;i của m&igrave;nh. Đ&acirc;y l&agrave; một điểm trừ lớn khi n&oacute; khiến người chơi mất đi một niềm vui nhỏ b&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Sims4-3\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/11\" style=\"height:391px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Hệ thống điều khiển v&agrave; giao diện h&igrave;nh ảnh của game cũng khiến đa số game thủ, đặc biệt l&agrave; những fan trung th&agrave;nh l&acirc;u năm của d&ograve;ng game The Sims, bất b&igrave;nh v&agrave; phản ứng gay gắt. Giờ đ&acirc;y, người chơi sẽ phải l&agrave;m quen với một giao diện mi&ecirc;u tả trạng th&aacute;i ho&agrave;n to&agrave;n mới v&agrave; rất phức tạp. Nếu bạn muốn biết th&ocirc;ng tin về điều g&igrave; đ&oacute;, người chơi sẽ phải chọn v&agrave;o nhiều mục v&agrave; thanh trạng th&aacute;i kh&aacute;c nhau để đến nơi m&igrave;nh cần t&igrave;m, trong khi ở The Sims 3, tất cả đều nằm r&otilde; r&agrave;ng dưới m&agrave;n h&igrave;nh của game.</span></p>\r\n\r\n<h4 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&hellip; HỨA HẸN TRONG TƯƠNG LAI.</strong></span></h4>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"the-sims-4-early-access-download\" src=\"https://guidegame.vn/wp-content/uploads/2015/02/16\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đồ họa v&agrave; &acirc;m thanh của game c&oacute; một bước thay đổi lớn so với The Sims 3. Vẫn l&agrave; phong c&aacute;ch đồ họa rực rỡ đầy m&aacute;u sắc tươi vui nhưng The Sims 4 c&ograve;n thể hiện th&ecirc;m những khung h&igrave;nh v&agrave; h&agrave;nh động h&agrave;i h&ograve;a hơn, đầy m&agrave;u sắc v&agrave; dịu d&agrave;ng. &Acirc;m nhạc của game kh&ocirc;ng c&oacute; g&igrave; thay đổi r&otilde; n&eacute;t, khi vẫn l&agrave; những ng&ocirc;n ngữ Sims đặc trưng, &acirc;m thanh reo rắt nh&iacute; nhảnh v&agrave; nhạc nền dịu d&agrave;ng, nh&igrave;n chung cả 2 đều đ&atilde; ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuy kh&ocirc;ng c&oacute; qu&aacute; nhiều điểm nổi bật cũng như xuất hiện nhiều lỗi v&agrave; chỉnh sửa kh&ocirc;ng hợp l&yacute;, nhưng The Sims 4 vẫn c&ograve;n rất nhiều điều để mong đợi bởi v&igrave; EA vẫn chưa tung ra những DLC, thứ thậm ch&iacute; c&ograve;n cốt l&otilde;i v&agrave; quan trọng hơn. Nếu bạn tr&ocirc;ng chờ một sự đột ph&aacute; về lối chơi, GuideGame sẽ đề nghị bạn tạm thời chọn The Sims 3 v&agrave;o l&uacute;c n&agrave;y, c&ograve;n nếu bạn muốn cảm nhận những t&iacute;nh năng mới mẻ, h&atilde;y thử qua The Sims 4 nh&eacute;.</span></p>', '<p>CPU: Intel Core 2 Duo E4300 or AMD Athlon 64 X2 4000+ (2.0 GHz Dual Core required if using integrated graphics)</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 2 GB</p>\r\n\r\n<p>OS: Windows XP</p>\r\n\r\n<p>VIDEO CARD: NVIDIA GeForce 6600 or ATI Radeon X1300 or Intel GMA X4500</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 10 GB</p>', '<p>CPU: Intel Core 2 Duo E4300 or AMD Athlon 64 X2 4000+ (2.0 GHz Dual Core required if using integrated graphics)</p>\r\n\r\n<p>CPU SPEED: Info</p>\r\n\r\n<p>RAM: 2 GB</p>\r\n\r\n<p>OS: Windows XP</p>\r\n\r\n<p>VIDEO CARD: NVIDIA GeForce 6600 or ATI Radeon X1300 or Intel GMA X4500</p>\r\n\r\n<p>SOUND CARD: Yes</p>\r\n\r\n<p>FREE DISK SPACE: 10 GB</p>', 'Cài đặt và chơi', '1520188708DVjGLWzVwAA5dlp.jpg', 'https://www.youtube.com/embed/z00mK3Pxc8w', 2014, '2018-03-14');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(8, 3, 'Need For Speed: Payback', 'need-for-speed:-payback', '<h3 style=\"text-align:justify\">Need for Speed đ&atilde; trở lại sau 1 năm vắng b&oacute;ng kể từ phi&ecirc;n bản 2015. Tiếp tục được ph&aacute;t triển bởi Ghost Games, phi&ecirc;n bản mang t&ecirc;n&nbsp;Need for Speed Payback&nbsp;lần n&agrave;y quyết định đi theo phong c&aacute;ch thi&ecirc;n về h&agrave;nh động với những pha takedown v&agrave; những trường đoạn đậm chất Fast &amp; Furious.</h3>', '<h2 style=\"text-align:justify\"><span style=\"font-size:14px\">Một cốt truyện mới khi c&oacute; tới 3 nh&acirc;n vật ch&iacute;nh, thế giới mở rộng lớn v&agrave; hơn 80 mẫu xe đua đủ thể loại sẽ xuất hiện trong Need for Speed Payback.</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Need for Speed Payback</strong> sẽ l&agrave; tựa game đua xe đ&aacute;ng ch&uacute; &yacute; nhất trong th&aacute;ng 11 n&agrave;y, với một cốt truyện mới v&agrave; c&aacute;c t&iacute;nh năng độc đ&aacute;o lần đầu ti&ecirc;n được đưa v&agrave;o seri Need for Speed. C&aacute;c fan h&acirc;m mộ gạo cội lẫn l&iacute;nh mới chưa từng chơi thử Need for Speed cũng sẽ c&oacute; c&aacute;c trải nghiệm th&uacute; vị như nhau, đ&oacute; l&agrave; lời hứa của nh&agrave; ph&aacute;t h&agrave;nh Need for Speed Payback, vậy h&atilde;y c&ugrave;ng Mọt game điểm qua c&aacute;c điểm độc đ&aacute;o ch&iacute;nh của game trước khi n&oacute; ra mắt nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-payback-2-e1509441902712.jpg\" style=\"height:420px; width:745px\" /></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Cốt truyện mới </strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đ&acirc;y l&agrave; thay đổi khiến nhiều fan h&acirc;m mộ mong chờ nhất, khi Need for Speed Payback th&ocirc;ng b&aacute;o sẽ cải tiến cốt truyện của m&igrave;nh theo hướng phim ảnh với 3 nh&acirc;n vật ch&iacute;nh kh&aacute;c nhau. Xoay quanh 3 nh&acirc;n vật ch&iacute;nh l&agrave; Tyler Morgan, Jessica Miller v&agrave; Sean McAlister trong một phi vụ trả th&ugrave; băng nh&oacute;m thế giới ngầm. Phần chơi đơn của Need for Speed Payback sẽ xoay quanh li&ecirc;n tục giữa 3 người n&agrave;y, với những t&igrave;nh tiết đan xen lẫn nhau với thủ lĩnh l&agrave; Tyler Morgan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Mỗi nh&acirc;n vật trong Need for Speed Payback sẽ đ&oacute;ng c&aacute;c vai tr&ograve; ri&ecirc;ng trong cốt truyện, như Tyler l&agrave; một tay đua đường phố kh&eacute;t tiếng, Jessica nhận nhiệm vụ như một &ldquo;người giao h&agrave;ng&rdquo; bất hợp ph&aacute;p v&agrave; Sean th&igrave; lại y&ecirc;u th&iacute;ch c&aacute;c cuộc rượt đuổi c&ugrave;ng cảnh s&aacute;t. Cốt truyện của Need for Speed Payback n&oacute;i về sự xung đột băng đảng v&agrave; phản bội giữa c&aacute;c th&agrave;nh vi&ecirc;n với nhau, thế n&ecirc;n n&oacute; mang d&aacute;ng dấp giống một bộ phim nhiều hơn c&aacute;c phi&ecirc;n bản trước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-e1509441005116.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><em>Tyler Morgan, một trong ba nh&acirc;n vật ch&iacute;nh</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">C&aacute;c fan sẽ rất h&agrave;i l&ograve;ng khi biết rằng giờ đ&acirc;y Need for Speed Payback sẽ thay đổi theo hệ thống ng&agrave;y đ&ecirc;m, với c&aacute;c nhiệm vụ v&agrave; t&igrave;nh tiết mới dựa theo thời gian trong game. Hơn nữa với việc c&oacute; tới 3 nh&acirc;n vật ch&iacute;nh, phần chơi đơn c&oacute; thể được ho&agrave;n th&agrave;nh c&ugrave;ng những đồng đội kh&aacute;c khi chơi multiplayer.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Thế giới mở rộng lớn</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-payback-1-e1509441939168.jpg\" style=\"height:338px; width:745px\" /></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Bối cảnh của Need for Speed Payback diễn ra tại một khu d&acirc;n cư c&oacute; t&ecirc;n l&agrave; Fortune Valley, ngo&agrave;i c&aacute;c khu phố đ&ocirc;ng đ&uacute;c l&agrave; nơi đ&oacute;ng qu&acirc;n của băng đảng The House, th&igrave; bản đồ c&ograve;n chia ra l&agrave;m 4 khu vực ri&ecirc;ng biệt l&agrave; v&ugrave;ng cao nguy&ecirc;n Providence, sa mạc Liberty, d&atilde;y n&uacute;i Silver v&agrave; thung lũng Silver. Mỗi khu vực của Fortune Valley sẽ c&oacute; từ 5 tới 6 g&oacute;c nhỏ ri&ecirc;ng biệt, với c&aacute;c nhiệm vụ phụ, trophy v&agrave; tuyến nh&acirc;n vật phụ để người chơi kh&aacute;m ph&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><a href=\"https://motgame.vn/need-speed-payback-se-co-cot-truyen-giong-nhu-fast-furious.game\" target=\"_blank\"><img alt=\"\" src=\"https://media.motgame.vn/2017/10/vuhoang/needforspeedpayback-2-1-368x207.jpg\" style=\"height:419px; width:745px\" /></a></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Kh&ocirc;ng chỉ l&agrave; những m&agrave;n đua xe v&agrave; độ xế quen thuộc, Need for Speed Payback sẽ c&oacute; một cốt truyện đậm m&ugrave;i Fast &amp; Furious để người chơi trải nghiệm.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Cả v&ugrave;ng đất Fortune Valley sẽ l&agrave; một trường đua khổng lồ, giờ đ&acirc;y người chơi sẽ c&oacute; thể l&aacute;i xe khắp nơi v&agrave; thu thập c&aacute;c điểm reputation để tăng độ nổi tiếng cho m&igrave;nh, hoặc thử th&aacute;ch những nhiệm vụ &ldquo;SpeedList&rdquo; độc đ&aacute;o. Tại Fortune Valley th&igrave; ngo&agrave;i The House l&agrave; băng đảng nắm quyền ch&iacute;nh ra th&igrave; vẫn c&ograve;n rất nhiều nh&oacute;m nhỏ hoạt động độc lập, bọn ch&uacute;ng sẽ th&aacute;ch thức người chơi th&ocirc;ng qua c&aacute;c &ldquo;SpeedList&rdquo; ri&ecirc;ng biệt, c&aacute;c nhiệm vụ n&agrave;y kh&oacute; khăn hơn v&agrave; chia theo từng cấp từ dễ đến kh&oacute;, đủ để cho người chơi bận rộn với game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/nfsp-world-game-map-1.jpg\" style=\"height:497px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><em>Bản đồ Fortune Valley</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ngo&agrave;i việc đua xe v&agrave; thử th&aacute;ch bản th&acirc;n với c&aacute;c &ldquo;SpeedList&rdquo;, người chơi cũng sẽ đi thu thập nguy&ecirc;n liệu để độ xe rải r&aacute;c khắp nơi tr&ecirc;n bản đồ Fortune Valley. C&oacute; thể n&oacute;i Need for Speed Payback đ&atilde; x&acirc;y dựng bối cảnh của m&igrave;nh ho&agrave;n to&agrave;n theo kiểu thế giới mở, tự do hơn v&agrave; c&oacute; phần kh&aacute; giống với GTA. Một kh&aacute;i niệm mới đ&atilde; được đưa v&agrave;o gọi l&agrave; Heat Level, khi bạn để chỉ số n&agrave;y l&ecirc;n qu&aacute; cao v&igrave; đi sinh sự, hoặc đua xe bạt mạng giữa đường phố th&igrave; cảnh s&aacute;t sẽ v&agrave;o cuộc để thăm hỏi nh&acirc;n vật. Nh&igrave;n chung khi Need for Speed Payback đ&atilde; thay đổi về cốt truyện, th&igrave; việc đổi mới cả về bối cảnh cũng l&agrave; một bước đi hợp l&yacute;.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>5 loại xe với t&iacute;nh năng kh&aacute;c nhau</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-4-e1509441014849.jpg\" style=\"height:418px; width:745px\" /></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Với một game đua xe th&igrave; sự đa dạng trong c&aacute;c mẫu m&atilde; &ldquo;xế độ&rdquo; lu&ocirc;n l&agrave; thứ thu h&uacute;t người chơi, số lượng xe trong Need for Speed Payback hiện tại đang l&agrave; hơn 80 mẫu. Một điểm đổi mới của game l&agrave; giờ đ&acirc;y những mẫu xe n&agrave;y sẽ được ra l&agrave;m những &ldquo;class&rdquo; ri&ecirc;ng biệt, phục vụ theo từng loại nhiệm vụ theo cốt truyện, tổng cộng c&oacute; 5 loại xe kh&aacute;c nhau:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Race: </strong>Mẫu xe thường d&ugrave;ng v&agrave; chiếm số lượng lớn nhất trong Need for Speed Payback, Race l&agrave; c&aacute;c loại xe đua th&ocirc;ng thường m&agrave; ch&uacute;ng ta c&oacute; thể gặp thường xuy&ecirc;n tr&ecirc;n đường. C&acirc;n bằng, đẹp đẽ v&agrave; phổ biến l&agrave; những g&igrave; để m&ocirc; tả về lớp n&agrave;y, tất nhi&ecirc;n l&agrave; ở c&aacute;c đoạn đường bằng phẳng Race sẽ l&agrave; vua, nhưng trong Need for Speed Payback đ&acirc;u chỉ c&oacute; mỗi đường nhựa th&ocirc;ng thường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Off-Road: </strong>D&ugrave;ng cho những chặng đường gồ ghề, đất đ&aacute; m&agrave; xe đua b&igrave;nh thường kh&ocirc;ng thể đi nổi. C&aacute;c loại xe thuộc lớp Off-road c&oacute; thể giữ vững tốc độ kể cả khi đi trong c&aacute;c đoạn đường sa mạc, cũng như khả năng giữ tay l&aacute;i cho người chơi tr&ecirc;n c&aacute;c đoạn đường kh&oacute; nhằn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"Need for Speed Payback: Những điều bạn nên biết trước khi game ra mắt\" src=\"https://motgame.vn/wp-content/uploads/2017/10/vuhoang/need-for-speed-2-e1509440996536.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><em>Off-Road</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Drift: </strong>Bạn y&ecirc;u th&iacute;ch những c&uacute; bo cua nảy lửa, hoặc quay đầu đến ch&aacute;y cả lốp tr&ecirc;n những đoạn đường hẹp&hellip; vậy th&igrave; Drift chắc chắn sẽ l&agrave; d&ograve;ng xe d&agrave;nh cho bạn. Ở những thử th&aacute;ch đường n&uacute;i với những kh&uacute;c cua li&ecirc;n tục th&igrave; bạn sẽ cần tới Drift, v&igrave; sức mạnh v&ocirc; địch của n&oacute; trong những lần &ldquo;lết b&aacute;nh&rdquo; với tốc độ đ&aacute;ng kinh ngạc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Drag: </strong>Kh&ocirc;ng cần phanh, kh&ocirc;ng cần thắt d&acirc;y an to&agrave;n v&agrave; kh&ocirc;ng cần bất cứ phụ t&ugrave;ng g&igrave; đi k&egrave;m. Tốc độ ch&iacute;nh l&agrave; thứ m&agrave; những tay đua Drag hướng tới, loại xe n&agrave;y được thiết kế với những ống đẩy phản lực như rocket ph&iacute;a sau gi&uacute;p xe đạt tốc độ tối đa tr&ecirc;n c&aacute;c đoạn đường thẳng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&ndash; Runner: </strong>Nếu như Race l&agrave; mẫu xe đua để bạn chạy tr&ecirc;n đường nhựa, th&igrave; Runner l&agrave; phi&ecirc;n bản n&acirc;ng cấp của n&oacute; với gầm xe thấp c&ugrave;ng kich thước v&ocirc; c&ugrave;ng khi&ecirc;m tốn, cực kỳ th&iacute;ch hợp để cắt đu&ocirc;i khi bị cảnh s&aacute;t truy đuổi. Runner d&agrave;nh cho những ai th&iacute;ch ph&aacute; luật, khi n&oacute; c&oacute; thể luồn l&aacute;ch khắp c&aacute;c con ng&otilde; nhỏ hoặc chạy trối chết khi bị số đ&ocirc;ng cảnh s&aacute;t truy n&atilde;.</span></p>', '<p>OS: 64-bit Windows 7 or later</p>\r\n\r\n<p>CPU: Intel i3 6300 @ 3.8GHz or AMD FX 8150 @ 3.6GHz with 4 hardware threads</p>\r\n\r\n<p>RAM: 6GB</p>\r\n\r\n<p>Disc Drive: DVD ROM drive required for installation only Hard Drive: 30GB</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 750 Ti, AMD Radeon HD 7850, or equivalent DX11 compatible GPU with 2GB of memory</p>\r\n\r\n<p>DirectX: 11-compatible video card or equivalent</p>', '<p>OS: 64-bit Windows 7 or later</p>\r\n\r\n<p>CPU: Intel i3 6300 @ 3.8GHz or AMD FX 8150 @ 3.6GHz with 4 hardware threads</p>\r\n\r\n<p>RAM: 6GB</p>\r\n\r\n<p>Disc Drive: DVD ROM drive required for installation only Hard Drive: 30GB</p>\r\n\r\n<p>GPU: Nvidia GeForce GTX 750 Ti, AMD Radeon HD 7850, or equivalent DX11 compatible GPU with 2GB of memory</p>\r\n\r\n<p>DirectX: 11-compatible video card or equivalent</p>', 'Cài đặt và chơi', '15201894440027_Gamescom_stills_sc01_sh060_blockbuster_v001.jpg', 'https://www.youtube.com/embed/K-5EdHZ0hBs', 2017, '2018-03-14'),
(9, 1, 'Far Cry Primal', 'far-cry-primal', '<h3 style=\"text-align:justify\">Far Cry thường đặt bạn ở r&igrave;a của thế giới được biết đến, trong một bi&ecirc;n giới xinh đẹp, v&ocirc; luật v&agrave; man rợ. The Stone Age, trong một c&aacute;ch, bi&ecirc;n giới đầu ti&ecirc;n cho lo&agrave;i người; đ&oacute; l&agrave; thời gian khi con người đặt một c&acirc;y gậy xuống đất v&agrave; tuy&ecirc;n bố mặt đất cho ri&ecirc;ng m&igrave;nh, thời gian khi ch&uacute;ng ta bắt đầu leo l&ecirc;n c&aacute;c chuỗi thức ăn. Điều đ&oacute; đến với c&aacute;c cuộc xung đột, kh&ocirc;ng chỉ chống lại những con người kh&aacute;c của lo&agrave;i người, m&agrave; c&ograve;n chống lại thi&ecirc;n nhi&ecirc;n.</h3>', '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Far Cry Primal đ&atilde; l&agrave;m rất tốt đ&aacute;c đặc điểm vốn c&oacute; trước đ&acirc;y của d&ograve;ng game, song lại kh&ocirc;ng mang đến nhiều cải tiến đ&aacute;ng gi&aacute;.</strong><br />\r\n<br />\r\nUbisoft đ&atilde; thật sự dung cảm khi đưa d&ograve;ng game Far Cry về một thời đại m&agrave; một số gi&aacute; trị cốt l&otilde;i của d&ograve;ng game n&agrave;y sẽ kh&ocirc;ng thể xuất hiện được. Từ trước đến nay, Far Cry lu&ocirc;n l&agrave; tựa game li&ecirc;n quan tới giải cứu những c&aacute; nh&acirc;n, những cộng đồng bị ch&agrave; đạp khỏi những t&ecirc;n bạo ch&uacute;a bạo ngược- bằng c&aacute;ch d&ugrave;ng đủ mọi loại s&uacute;ng v&agrave; thuốc nổ để thổi bay bất cứ thứ g&igrave; tr&ecirc;n đường đi.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-1(1).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Ấy thế m&agrave; bạn sẽ kh&ocirc;ng thể t&igrave;m thấy những điều đ&oacute; trong phi&ecirc;n bản mới nhất n&agrave;y. Far Cry Primal sẽ đưa bạn trở lại thời kỳ 10000 năm trước c&ocirc;ng nguy&ecirc;n, v&agrave;o thời đại đồ đ&aacute; giữa, sau kỷ băng h&agrave; cuối c&ugrave;ng, thời điểm m&agrave; thuốc s&uacute;ng, trồng trọt v&agrave; khai th&aacute;c chưa hề được ph&aacute;t minh ra. Bạn sẽ v&agrave;o vai Takkar, một th&agrave;nh vi&ecirc;n của bộ tộc Wenja. Trong một lần săn voi mamut, Takkar v&agrave; c&aacute;c chiến hữu (đ&atilde; đ&oacute;i lả sau nhiều ng&agrave;y) bất ngờ bị tấn c&ocirc;ng bỏi một con hổ răng kiếm hung dữ. Cuộc đi săn biến th&agrave;nh một cuộc thảm s&aacute;t đẫm m&aacute;u, nh&oacute;m thợ săn bị giết gần hết, chỉ c&ograve;n mỗi Takkar. Sau khi nghe lời trăn trối cuối c&ugrave;ng của người anh em, Takkar l&ecirc;n đường để ho&agrave;nh th&agrave;nh ước muốn của người đồng đội đ&oacute;: t&igrave;m ra v&ugrave;ng đất hứa Oros, v&agrave; những người c&ograve;n lại của bộ tộc Wenja đ&atilde; thất lạc từ l&acirc;u.<br />\r\n<br />\r\n<strong>Thế giới Oros thực sự tuyệt vời</strong><br />\r\n&nbsp;<br />\r\nThế nhưng, khi đặt ch&acirc;n tới Oros, Takkar mới ph&aacute;t hiện ra đ&oacute; chẳng phải l&agrave; thi&ecirc;n đường mật ngọt n&agrave;o cả. X&aacute;c chết người Wenja nằm rải r&aacute;c khắp nơi, số c&ograve;n lại bị bắt l&agrave;m n&ocirc; lệ cho 2 bộ tộc mạnh hơn kh&aacute;c: Udam &ndash; một bộ tộc man rợ chuy&ecirc;n ăn thịt người, sống ở v&ugrave;ng đất băng gi&aacute; ph&iacute;a Bắc, v&agrave; Izila &ndash; một bộ tộc cuồng t&iacute;n chuy&ecirc;n thi&ecirc;u sống t&ugrave; nh&acirc;n để c&uacute;ng tế cho c&aacute;c thần linh.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-2(1).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Nhiệm vụ của Takkar rất r&otilde; rang: tập hợp những người Wenja c&ograve;n sống s&oacute;t lại, g&acirc;y dựng lại bộ tộc v&agrave; chiến đấu với 2 kẻ th&ugrave; đ&aacute;ng sợ kia. Kh&ocirc;ng c&oacute; bất cứ n&uacute;t thắt hay chi tiết đặc biệt n&agrave;o trong c&acirc;u chuyện cả, kh&ocirc;ng c&oacute; bất cứ một t&ecirc;n độc t&agrave;i hay phản diện n&agrave;o cần phải loại bỏ.&nbsp; Nội dung của Far Cry Prinal cũng giống như c&aacute;ch m&agrave; ch&uacute;ng ta tưởng tượng về thời kỳ văn minh sơ khai của lo&agrave;i người, đ&oacute; ho&agrave;n to&agrave;n l&agrave; chiến đấu, giết ch&oacute;c để chiếm l&atilde;nh thổ.<br />\r\n<br />\r\nĐơn giản như vậy n&ecirc;n th&agrave;nh ra c&acirc;u chuyện cũng tương đối nh&agrave;m ch&aacute;n. C&oacute; lẽ nh&agrave; sản xuất đ&atilde; cố gắng cải thiện một ch&uacute;t bằng c&aacute;ch loại bỏ hệ thống nhiệm vụ tuyến t&iacute;nh, v&agrave; cho ph&eacute;p bạn thoải m&aacute;i đi theo những c&acirc;u chuyện của những người d&acirc;n thời tiền sử theo một thứ tự do ch&iacute;nh bạn đặt ra. Tuy nhi&ecirc;n, dấu ấn đạt được kh&aacute; mờ nhạt: kh&ocirc;ng c&oacute; khoảnh khắc đặc biệt đ&aacute;ng nhớ n&agrave;o, kh&ocirc;ng c&oacute; những miếng gh&eacute;p ho&agrave;n hảo để tạo n&ecirc;n một t&aacute;c phẩm liền mạch, thống nhất v&agrave; đủ hấp dẫn khiến bạn n&ocirc;n n&oacute;ng muốn xem kết th&uacute;c sẽ ra sao. Thực tế, chả ai trong Far Cry Primal ho&agrave;n to&agrave;n l&agrave; người tốt cả, đơn giản chỉ l&agrave; bạn c&oacute; 3 bộ tộc man rợ thời tiền sử đang tranh đấu một v&ugrave;ng đất m&agrave; kh&ocirc;ng thể n&agrave;o sống ho&agrave;n b&igrave;nh cạnh nhau được, chỉ c&oacute; vậy m&agrave; th&ocirc;i. C&oacute; thể 10000 năm trước đ&acirc;y người ta thực sự sống như vậy, nhưng t&ocirc;i vẫn ước rằng gi&aacute; m&agrave; 15 giờ tr&ocirc;i qua đ&oacute; c&oacute; thứ g&igrave; đ&aacute;ng nhớ v&agrave; suy ngẫm hơn th&igrave; tốt biết mấy.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-3(1).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">&nbsp;<br />\r\n<strong>C&agrave;ng thay đổi, c&agrave;ng như cũ</strong><br />\r\n<br />\r\nGameplay kh&ocirc;ng c&oacute; nhiều thay đổi cải tiến d&aacute;ng gi&aacute;. Đ&acirc;y vẫn ch&iacute;nh x&aacute;c l&agrave; một tựa game Far Cry, chỉ l&agrave; mọi thứ (ngoại trừ c&aacute;c đ&agrave;i ph&aacute;t thanh radio) &nbsp;m&agrave; bạn biết v&agrave; th&iacute;ch ở Far Cry c&aacute;c bản trước đ&atilde; thay đổi để th&iacute;ch ứng với một thế giới hoang d&atilde;.<br />\r\n<br />\r\nThay v&igrave; ống nh&ograve;m, giờ đ&acirc;y bạn sẽ ph&oacute;ng tầm mắt v&agrave; theo d&otilde;i thế giới bằng một con c&uacute;, mắt n&oacute; c&oacute; thể đ&aacute;nh dấu v&agrave; theo đu&ocirc;i c&aacute;c mục ti&ecirc;u cho bạn. Thay v&igrave; c&aacute;c chốt canh, nơi m&agrave; bạn cần phải hạ gục l&iacute;nh g&aacute;c để chiếm như c&aacute;c bản trước, th&igrave; giờ bạn c&oacute; những lều trại v&agrave; lửa trại người tiền sử phục vụ chức năng tương tự. Tương đương với c&aacute;c ph&aacute;o đ&agrave;i (Fortress) trong Far Cry 4, trong Far Cry Primal cũng xuất hiện những doanh trại quy m&ocirc; lớn hơn b&igrave;nh thường, nơi bạn sẽ phải d&ugrave;ng đến rất nhiều sự kh&ocirc;n ngoan, chuẩn x&aacute;c v&agrave; sức mạnh để chinh phục.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-4(1).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Tương tự như c&aacute;c bản trước, bạn cũng sẽ phải d&agrave;nh nhiều thời gian để săn động vật, lột da ch&uacute;ng, d&ugrave;ng da v&agrave; nội tạng để n&acirc;ng cấp vũ kh&iacute; v&agrave; ng&ocirc;i l&agrave;ng của m&igrave;nh. Tr&ecirc;n v&ugrave;ng dất Oros n&agrave;y, khi giải cứu những người d&acirc;n Wenja bị lạc v&agrave; đưa về l&agrave;ng của m&igrave;nh, bạn sẽ c&oacute; cơ hội mở kh&oacute;a th&ecirc;m những khả năng v&agrave; phần thưởng kh&aacute;c nữa. Như dự đo&aacute;n, bản đồ của game cũng d&agrave;y đặc với đủ mọi loại hoạt động v&agrave; nhiệm vụ, từ săn bắn, sưu tập, đến đ&agrave;o kho b&aacute;u. C&oacute; c&aacute;i th&igrave; cực kỳ th&uacute; vị v&agrave; cũng c&oacute; c&aacute;i nh&agrave;m ch&aacute;n đến ph&aacute;t nản.<br />\r\n<br />\r\n<strong>Trước d&ugrave;ng s&uacute;ng, nay d&ugrave;ng dao</strong><br />\r\n<br />\r\nThay đổi lớn nhất đương nhi&ecirc;n l&agrave; vũ kh&iacute;. Giờ đ&acirc;y bạn sẽ phải l&agrave;m quen với những vũ kh&iacute; cận chiến như ch&ugrave;y hay gi&aacute;o m&aacute;c hay thậm ch&iacute; cả n&aacute; nữa. Cung t&ecirc;n sẽ l&agrave; vũ kh&iacute; tầm xa chủ đạo, v&agrave; to&agrave;n bộ vũ kh&iacute; bạn c&oacute; đều c&oacute; thể n&acirc;ng cấp được. Tr&ecirc;n bản đồ bạn thi thoảng sẽ t&igrave;m thấy những người d&acirc;n c&oacute; kỹ năng đặc biệt (specialist), đưa họ về sẽ gi&uacute;p mở ra nh&aacute;nh kỹ năng hay n&acirc;ng cấp. Gi&aacute;o c&ugrave;n ban đầu sẽ được thay thế bằng những ngọn gi&aacute;o nhọn v&agrave; cứng hơn, cung t&ecirc;n bắn xa hơn, v&agrave; dần dần, Far Cry Primal c&oacute; cảm gi&aacute;c như một game sinh tồn thực thụ vậy.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-5(2).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Bạn cũng sẽ thu thập item để chế vũ kh&iacute; đạn được, chế tạo mũi t&ecirc;n tr&ecirc;n đường đi, hay đ&ugrave;ng đ&aacute; đẻ trang tr&iacute; cho ch&ugrave;y v&agrave; gi&aacute;o. Nếu bạn nhớ đến s&uacute;ng trong những bản trước th&igrave; cũng đừng c&oacute; lo, v&igrave; nay bạn đ&atilde; c&oacute; một thứ vũ kh&iacute; mới khủng bố hơn nhiều: những con th&uacute; tiền sử m&agrave; nay đ&atilde; tuyệt chủng. Hổ răng kiếm, sư tử n&uacute;i, gấm x&aacute;m, s&oacute;i v&agrave; nhiều con th&uacute; kh&aacute;c sẽ phục vụ dưới ch&acirc;n bạn khi bạn đ&atilde; l&agrave;m chủ kỹ năng thuần h&oacute;a ch&uacute;ng.<br />\r\n<br />\r\nHướng ch&uacute;ng tới kẻ địch, nhấn n&uacute;t l&agrave; bạn c&oacute; thể thoải m&aacute;i ngồi xem m&agrave;n những &ldquo;con đệ&rdquo; tiền sử của m&igrave;nh x&eacute; x&aacute;c kẻ th&ugrave; th&agrave;nh từng mảnh. Hầu hết những con th&uacute; n&agrave;y bạn đều cưỡi được: hổ răng kiếm l&agrave; phương tiện di chuyển tốt hơn nhiều so với xe cộ trong Far Cry 4. Bạn cũng c&oacute; thể trang bị vũ kh&iacute; cho đ&aacute;m voi ma m&uacute;t, v&agrave; cưỡi n&oacute; để đi gi&agrave;y x&eacute;o v&agrave; ph&aacute; tan hết đồn bốt n&agrave;y tới ph&aacute;o đ&agrave;i kh&aacute;c của địch. Ph&ecirc; cực kỳ.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-7(1).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">C&aacute;c nhiệm vụ săn Beast Master trong game cũng cực kỳ th&uacute; vị. Đ&acirc;y l&agrave; những nhiệm vụ cho bạn săn t&igrave;m, đặt bẫy v&agrave; ti&ecirc;u diệt những con th&uacute; huyền thoại, những con th&uacute; thậm ch&iacute; c&ograve;n to lớn v&agrave; hung dữ hơn những loại th&ocirc;ng thường. T&ocirc;i đặc biệt th&iacute;ch nhiệm vụ săn t&igrave;m con hổ răng kiếm đ&atilde; hạ s&aacute;t đồng đội của Takkar l&uacute;c đầu game. Sau một thời gian đi săn, t&ocirc;i đ&atilde; th&agrave;nh c&ocirc;ng trong việc ph&aacute;t hiện v&agrave; đả thương n&oacute; trước khi n&oacute; kịp hiểu chuyện g&igrave; xảy ra v&agrave; th&aacute;o chạy. T&ocirc;i v&agrave; người bạn s&oacute;i đồng h&agrave;nh đuổi theo con th&uacute; bị thương tới tận hang ổ của n&oacute;, v&agrave; đ&oacute; cũng l&agrave; nơi trận đấu boss cực kh&oacute; nhằn diễn ra. D&ugrave; sao th&igrave; cuối c&ugrave;ng th&igrave; t&ocirc;i cũng hạ gục được n&oacute;. C&oacute; thể thuần h&oacute;a con qu&aacute;i vật n&agrave;y, song &yacute; ch&iacute; b&aacute;o th&ugrave; của t&ocirc;i đ&atilde; trỗi dậy mạnh hơn...</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-8(1).jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">Tuy nhi&ecirc;n, Far Cry Primal c&oacute; một điểm trừ kh&aacute; lớn, đ&oacute; l&agrave; kh&ocirc;ng c&oacute; g&igrave; li&ecirc;n quan tới phần chơi multiplayer cả. Đ&oacute; l&agrave; một sự l&atilde;ng ph&iacute;, bởi v&igrave; săn những con th&uacute; lớn c&ugrave;ng với bạn b&egrave; l&uacute;c n&agrave;o cũng ẩn chứa cực kỳ nhiều niềm vui, v&agrave; chắc chắn việc đ&oacute; sẽ y&ecirc;u cầu sự phối hợp chặt chẽ của cả nh&oacute;m. C&ugrave;ng với bạn b&egrave; g&acirc;y loạn khắp nơi đ&atilde; trở th&agrave;nh một th&uacute; vui trong Far Cry 4, ch&iacute;nh v&igrave; thế việc thiếu hụt t&iacute;nh năng chơi mạng trong bạn n&agrave;y khiến t&ocirc;i c&oacute; một ch&uacute;t thất vọng. Săn qu&aacute;i th&uacute; huyền thoại, chuẩn bị vũ kh&iacute; v&agrave; thảo ra chiến thuật chắc chắn sẽ l&agrave; những trải nghiệm tuyệt vời, chỉ tiếc l&agrave; bạn sẽ kh&ocirc;ng t&igrave;m thấy n&oacute; ở đ&acirc;y.<br />\r\n<br />\r\nĐấu boss trong game cũng l&agrave; một vấn đề. Hai con boss ch&iacute;nh của game đều c&oacute; đặc điểm giống nhau, đ&oacute; l&agrave; đều rất tr&acirc;u b&ograve;, v&agrave; l&uacute;c n&agrave;o bạn cũng c&oacute; cảm gi&aacute;c chỉ c&oacute; thể g&atilde;i ngứa cho ch&uacute;ng trong khi phải chống chọi với cả một bầy l&acirc;u nh&acirc;u v&acirc;y quanh vậy. Chả c&oacute; g&igrave; kh&aacute;c biệt hay s&aacute;ng tạo g&igrave; cả, ch&uacute;ng chỉ đứng đ&oacute; để chứng minh rằng bạn đang chơi game m&agrave; th&ocirc;i, v&agrave; h&igrave;nh như c&aacute;c nh&agrave; l&agrave;m game cũng n&agrave;o cũng cho rằng tr&ugrave;m cuối cần phải m&aacute;u thật tr&acirc;u nhưng lại thật ngu hay sao ấy.</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://media.esports.vn/images/images_news/images/quanghuy/Far-Cry-Primal-9(1).jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\">T&oacute;m lại, Far Cry Primal l&agrave; một game kh&aacute;, nhưng chưa phải l&agrave; tuyệt vời. Cốt truyện của Far Cry 4 đ&atilde; kh&ocirc;ng phải hay cho lắm, phần n&agrave;y thậm ch&iacute; c&ograve;n k&eacute;m hơn. D&ugrave; sao n&oacute; vẫn mang đầy đủ d&aacute;ng dấp của một game Far Cry, một tựa game thế giới mở với h&agrave;ng loạt nhiệm vụ b&ecirc;n lề để kh&aacute;m ph&aacute;, săn t&igrave;m v&agrave; sưu tập. Sự hấp dẫn của game c&oacute; lẽ sẽ đến từ những lựa chọn của bạn m&agrave; th&ocirc;i.<br />\r\n<br />\r\nFar Cry Primal kh&ocirc;ng c&oacute; nhiều sự đổi mới so với những g&igrave; m&agrave; c&aacute;c fan của series đ&atilde; qu&aacute; quen thuộc trước đ&acirc;y, thậm ch&iacute; c&oacute; l&uacute;c c&ograve;n thể hiện k&eacute;m hơn trong một bối cảnh m&agrave; lẽ ra cần phải tốt hơn. Nếu bạn muốn một game mang đ&uacute;ng chất để quẩy nhiệt t&igrave;nh v&agrave; dữ dội ở thời điểm qu&aacute; khứ, Far Cry Primal l&agrave; lựa chọn kh&ocirc;ng tồi. Tuy nhi&ecirc;n nếu bạn ưa th&iacute;ch moojt tựa game c&oacute; nội dung ho&agrave;n chỉnh, s&acirc;u sắc m&agrave; hấp dẫn, bạn sẽ phải thất vọng đấy.</span></p>', '<p>CPU: Core i3-550 / Phenom II X4 955 CPU SPEED: Info</p>\r\n\r\n<p>RAM: 4 GB</p>\r\n\r\n<p>OS: Windows 7, Windows 8.1, Windows 10 (64-bit versions only)</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 460 / Radeon HD 5770 1 GB</p>\r\n\r\n<p>FREE DISK SPACE: 20 GB</p>', '<p>CPU: Core i3-550 / Phenom II X4 955 CPU SPEED: Info</p>\r\n\r\n<p>RAM: 4 GB</p>\r\n\r\n<p>OS: Windows 7, Windows 8.1, Windows 10 (64-bit versions only)</p>\r\n\r\n<p>VIDEO CARD: Nvidia GeForce GTX 460 / Radeon HD 5770 1 GB</p>\r\n\r\n<p>FREE DISK SPACE: 20 GB</p>', 'Cài đặt và chơi', '1520189896ngay-ra-mat-far-cry-primal-2.jpg', 'https://www.youtube.com/embed/LJ2iH57Fs3M', 2016, '2018-03-14');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(10, 8, 'FIFA 18', 'fifa-18', '<h3 style=\"text-align:justify\">Phim ảnh, truyền h&igrave;nh, vật lưu niệm, trang phục thi đấu&hellip; l&agrave; những thứ đầu ti&ecirc;n người ta sẽ nghĩ đến nếu n&oacute;i về việc kinh doanh ăn theo b&oacute;ng đ&aacute;. Nhưng c&oacute; lẽ bạn thừa biết, d&ugrave; chỉ sở hữu hai đại diện nổi bật l&agrave; FIFA v&agrave; PES nhưng ng&agrave;nh c&ocirc;ng nghiệp game cũng đ&aacute;ng kiếm về cho m&igrave;nh h&agrave;ng t&aacute; b&aacute;nh tr&aacute;i thơm ngon từ tr&aacute;i b&oacute;ng tr&ograve;n. Nếu như từ trước tới nay vị thế của PES lu&ocirc;n cao hơn th&igrave; từ năm 2008 trở đi vị thế đ&oacute; đ&atilde; bị lung lay dữ dội bởi FIFA &ndash; một tựa game b&oacute;ng đ&aacute; trẻ trung, năng động v&agrave; lu&ocirc;n chịu kh&oacute; lắng nghe cộng đồng người chơi.</h3>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&oacute; ai đ&oacute; đ&atilde; từng n&oacute;i b&oacute;ng đ&aacute; ch&iacute;nh l&agrave; hiện th&acirc;n bằng xương bằng thịt của Midas, vị vua c&oacute; thể h&oacute;a v&agrave;ng mọi thứ khi chạm tay v&agrave;o.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ở thời điểm hiện tại, kh&ocirc;ng mấy người d&aacute;m nghi ngờ điều đ&oacute; bởi m&ocirc;n thể thao vua lu&ocirc;n c&oacute; khả năng gi&uacute;p những kẻ kinh doanh n&oacute; kiếm ra h&agrave;ng n&uacute;i tiền bất kể ở b&ecirc;n trong hay ngo&agrave;i trận đấu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phim ảnh, truyền h&igrave;nh, vật lưu niệm, trang phục thi đấu&hellip; l&agrave; những thứ đầu ti&ecirc;n người ta sẽ nghĩ đến nếu n&oacute;i về việc kinh doanh ăn theo b&oacute;ng đ&aacute;. Nhưng c&oacute; lẽ bạn thừa biết, d&ugrave; chỉ sở hữu hai đại diện nổi bật l&agrave; FIFA v&agrave; PES nhưng ng&agrave;nh c&ocirc;ng nghiệp game cũng đ&aacute;ng kiếm về cho m&igrave;nh h&agrave;ng t&aacute; b&aacute;nh tr&aacute;i thơm ngon từ tr&aacute;i b&oacute;ng tr&ograve;n. Nếu như từ trước tới nay vị thế của PES lu&ocirc;n cao hơn th&igrave; từ năm 2008 trở đi vị thế đ&oacute; đ&atilde; bị lung lay dữ dội bởi FIFA &ndash; một tựa game b&oacute;ng đ&aacute; trẻ trung, năng động v&agrave; lu&ocirc;n chịu kh&oacute; lắng nghe cộng đồng người chơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-9.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ugrave;ng t&igrave;m hiểu về những thay đổi đắt gi&aacute; của FIFA 18 trước khi tr&ograve; chơi ch&iacute;nh thức đến tay game thủ th&ocirc;ng qua phi&ecirc;n bản chơi sớm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều game thủ từng cho rằng việc PES 2018 ra mắt trước người k&igrave;nh địch của m&igrave;nh nửa th&aacute;ng sẽ khiến cho EA Sports v&ocirc; c&ugrave;ng bị động, c&oacute; lẽ b&acirc;y giờ đang hối hận bởi ph&aacute;t ng&ocirc;n đ&oacute; bởi <strong>FIFA 18</strong> đang nhận được v&ocirc; số đ&aacute;nh gi&aacute; t&iacute;ch cực từ giới chuy&ecirc;n m&ocirc;n lẫn cộng đồng game thủ. Dẫu biết rằng bất cứ tr&ograve; chơi n&agrave;o cũng kh&ocirc;ng thể ho&agrave;n mỹ thế nhưng với những g&igrave; m&agrave; FIFA 18 l&agrave;m được, trong cuộc chiến vĩnh hằng giữa Konami v&agrave; EA, năm nay phần thắng c&oacute; vẻ nghi&ecirc;ng về kẻ đến từ phương T&acirc;y vậy.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>H&igrave;nh &acirc;m cải tiến nhưng c&oacute; ch&uacute;t kh&ocirc;ng trọn vẹn</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu chỉ phải d&ugrave;ng hai từ để mi&ecirc;u tả những g&igrave; Frostbite 3 đ&atilde; l&agrave;m với FIFA 18, th&igrave; đ&oacute; hẳn phải l&agrave; &ldquo;Hết sẩy&rdquo;. D&ugrave; phi&ecirc;n bản năm 2017 đ&atilde; kh&aacute; xuất sắc thế nhưng đồ họa của game năm nay đ&atilde; thật sự đạt đến một đẳng cấp khiến ch&uacute;ng ta kh&oacute; c&oacute; thể ch&ecirc; bai &ndash; trừ một v&agrave;i ngu xuẩn nho nhỏ trong kh&acirc;u thiết kế tạo h&igrave;nh. Nếu vẫn cần một đối tượng để tham chiếu th&igrave; d&ograve;ng game NBA2K l&agrave; một v&iacute; dụ tương đối sinh động v&agrave; ho&agrave;n hảo, nh&igrave;n chung hai d&ograve;ng game n&agrave;y c&oacute; chất lượng đồ họa kh&aacute; ngang bằng, đẹp v&agrave; độc. C&ugrave;ng với rất nhiều c&aacute;c cut-scenes đa dạng được bổ sung cho hầu hết c&aacute;c bối cảnh trong trận đấu, Career hay The Journey, đặc biệt sẽ c&oacute; những g&oacute;c m&aacute;y cận cảnh v&agrave;o mặt cầu thủ hơn để khoe xử l&yacute; đồ họa c&ugrave;ng khả năng tạo h&igrave;nh ch&acirc;n thật của đội ngũ ph&aacute;t triển.</span></p>\r\n\r\n<blockquote>\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><em>FIFA 18 c&oacute; yếu điểm về tạo h&igrave;nh cầu thủ, ch&iacute;nh l&agrave; những người đ&atilde; được NSX l&agrave;m giống th&igrave; sẽ cực kỳ giống bản gốc ngo&agrave;i đời, c&ograve;n &ldquo;nhỡ tay&rdquo; kh&ocirc;ng giống th&igrave; bảo đảm tr&ocirc;ng y như hai t&ecirc;n huynh đệ th&acirc;n sinh c&ugrave;ng t&ecirc;n kh&aacute;c cha mẹ vậy.</em></strong></span></p>\r\n</blockquote>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể n&oacute;i đồ hoạ l&agrave; một trong những điểm nhấn đ&aacute;ng kể nhất của FIFA 18. Bộ engine Frostbite 3 với sức mạnh đ&atilde; được khẳng định qua nhiều tựa game t&ecirc;n tuổi như Battlefield 4 hay Dragon Age: Inquisition c&oacute; thể l&agrave;m cho bất k&igrave; một fan b&oacute;ng đ&aacute; cảm thấy th&iacute;ch th&uacute;. Điều dễ nhận ra nhất c&oacute; lẻ đ&oacute; l&agrave; gương mặt của c&aacute;c nh&acirc;n vật trong game đ&atilde; ch&acirc;n thật hơn rất nhiều, &aacute;nh mắt s&acirc;u sắc v&agrave; tỏ ra c&oacute; hồn với con ngươi chuyển động t&ugrave;y theo trang th&aacute;i t&acirc;m l&yacute;, điều n&agrave;y khiến nh&acirc;n vật trở n&ecirc;n sinh động hơn trong những hoạt cảnh cần b&agrave;y tỏ cảm x&uacute;c, chứ kh&ocirc;ng đơn điệu v&agrave; v&ocirc; hồn như c&aacute;c phi&ecirc;n bản trước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-12.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ecirc;n cạnh cầu thủ, c&aacute;c chiến lược gia bậc thầy cũng gi&agrave;nh cho m&igrave;nh kh&ocirc;ng &iacute;t sự ch&uacute; &yacute; b&ecirc;n ngo&agrave;i đường pitch. Thật thiếu s&oacute;t khi qu&ecirc;n đi những vị gi&aacute;o chủ b&oacute;ng đ&aacute; n&agrave;y, một trận cầu l&agrave;m sao c&oacute; thể hấp dẫn được khi thiếu một Antonio Conte vung tay g&agrave;o th&eacute;t; n&eacute;m phăng &aacute;o vest của m&igrave;nh mỗi khi cảm x&uacute;c d&acirc;ng tr&agrave;o khi đội b&oacute;ng ghi b&agrave;n hay một Mourinho đầy tranh c&atilde;i, c&oacute; l&uacute;c ưa c&agrave;u nh&agrave;u v&agrave; ph&agrave;n n&agrave;n với trong t&agrave;i như một kẻ hay hờn dỗi nhưng c&oacute; l&uacute;c lại th&acirc;m trầm v&agrave;o xảo quyệt cả trong v&agrave; ngo&agrave;i s&acirc;n b&oacute;ng. Pep Guardiola nh&atilde; nhặn, J&uuml;rgen Klopp nhiệt huyết, Ancelotti ph&uacute;c hậu&hellip; mỗi vị gi&aacute;o chủ đều sở hữu một t&iacute;nh c&aacute;ch ri&ecirc;ng v&agrave; ch&iacute;nh bởi điều đ&oacute; lại khiến cho từng đội b&oacute;ng c&oacute; một phong th&aacute;i đặc trưng kh&ocirc;ng lẫn v&agrave;o đ&acirc;u được bất chấp việc c&oacute; thể bọn họ đang d&ugrave;ng chung một sơ đồ chiến thuật.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave; những sự thay đổi về đồ họa chắc chắn kh&ocirc;ng chỉ dừng lại ở đ&oacute;. S&acirc;n vận động, kh&aacute;n giả, &aacute;nh s&aacute;ng, mặt cỏ&hellip; cộng hưởng với nhau v&agrave; gi&uacute;p FIFA 18 ch&acirc;n thực hơn c&ugrave;ng với khoản m&agrave;u sắc đa dạng hơn. C&ocirc;ng nghệ m&ocirc; phỏng thời tiết cũng in đậm dấu vết hơn với fog tech được bổ sung những hiệu ứng từ bụi, mưa ph&ugrave;n v&agrave; d&agrave;n đ&egrave;n nhưng sẽ được thiết kế hợp l&iacute; hơn v&agrave; kh&ocirc;ng bị lạm dụng qu&aacute; thường xuy&ecirc;n so với Dynamic Weather cũ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-1.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ecirc;n cạnh đ&oacute; c&ograve;n rất nhiều chi tiết kh&aacute;c chứng minh <a href=\"https://motgame.vn/ea-sports.tag\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>EA Sports</strong></a> đ&atilde; chỉnh chu hơn cho đứa con cưng của m&igrave;nh. Bắt đầu từ c&aacute;c kh&aacute;n đ&agrave;i, kh&aacute;n giả c&oacute; nhiều phản ứng hơn v&agrave; tạo được sự kh&aacute;c nhau khiến tổng thể đa dạng, hơn nữa, kh&aacute;n giả cũng biết&hellip; n&eacute; b&oacute;ng khi c&oacute; một c&uacute; &ldquo;bắn chim&rdquo; với đ&iacute;ch đến l&agrave; kh&aacute;n đ&agrave;i. Cuối c&ugrave;ng, EA Sports đ&atilde; trau chuốt c&aacute;c đoạn cắt cảnh nhiều hơn, l&agrave;m cho những g&oacute;c quay cận mang lại cảm gi&aacute;c hưng phấn kh&ocirc;ng kh&aacute;c g&igrave; đời thực. C&aacute;c đoạn tr&igrave;nh chiếu đội h&igrave;nh trước trận với h&igrave;nh ảnh nguy&ecirc;n người cầu thủ cũng khiến những m&agrave;n giới thiệu đội h&igrave;nh trở n&ecirc;n sinh động hơn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; khen ắt sẽ phải ch&ecirc; nếu so với bản tiền nhiệm của m&igrave;nh, t&ocirc;ng m&agrave;u của FIFA 18 trở n&ecirc;n m&agrave;u m&egrave; hoa l&aacute; hẹ hơn &ndash; d&acirc;n trong nghề thường k&ecirc;u l&agrave; Dynamic Color đ&oacute;, theo đ&oacute; c&aacute;c mảng m&agrave;u cũng nổi bật hơn. C&aacute;i n&agrave;y khen ch&ecirc; t&ugrave;y người c&oacute; kẻ th&iacute;ch thanh đạm nhẹ nh&agrave;ng, qu&acirc;n tử chi giao nhạt như nước l&atilde; nhưng cũng c&oacute; người th&iacute;ch nồng n&agrave;n kiểu đế G&ograve; Đen ủ chục năm khui nắp bay m&ugrave;i theo gi&oacute; khắp nơi. D&ugrave; kh&ocirc;ng thể x&aacute;c định thay đổi n&agrave;y l&agrave; tốt hay xấu nhưng tối thiểu đ&acirc;y sẽ l&agrave; yếu tố khiến kẻ khen kh&ocirc;ng dứt miệng m&agrave; kẻ ch&ecirc; cũng chẳng kiệm lời vậy.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Lối chơi th&igrave; OK nhưng đủ để l&ecirc;n đỉnh chưa?</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&Atilde; 4 năm tr&ocirc;i qua kể từ khi EA tung ra phi&ecirc;n bản FIFA 14 với bộ engine Ignite d&agrave;nh cho thế hệ console mới tới giờ. Hầu như FIFA chưa c&oacute; một sự thay đổi r&otilde; rệt n&agrave;o, c&oacute; chăng chỉ l&agrave; những n&acirc;ng cấp nhỏ lẻ về đồ hoạ kh&ocirc;ng ảnh hưởng qu&aacute; nhiều đến lối chơi. Tuy nhi&ecirc;n từ phi&ecirc;n bản FIFA 18, họ đ&atilde; mạnh dạn n&acirc;ng cấp th&ecirc;m rất nhiều t&iacute;nh năng m&agrave; trong đ&oacute; nổi bật v&agrave; ảnh hưởng đến lối chơi nhất bao gồm: Set Piece Rewrite, Active Intelligence System, New Attacking Techniques v&agrave; Physical Play Overhaul.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Set Piece Rewrite: T&iacute;nh năng n&agrave;y gi&uacute;p người chơi được tự do lựa chọn khoảng c&aacute;ch lấy đ&agrave;, g&oacute;c s&uacute;t, tốc độ di chuyển của cầu thủ trong c&aacute;c t&igrave;nh huống cố định chứ kh&ocirc;ng c&ograve;n phụ thuộc ho&agrave;n to&agrave;n v&agrave;o đặc trưng của từng c&aacute; nh&acirc;n cầu thủ như trước.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Active Intelligence System: đ&acirc;y l&agrave; t&iacute;nh năng hay tuy nhi&ecirc;n lại kh&aacute; kh&oacute; sử dụng khi bạn phải c&oacute; một sự am hiểu về chiến thuật kh&aacute; tốt. N&oacute; sẽ gi&uacute;p bạn sắp xếp c&aacute;ch chạy chỗ của đồng đội, gi&uacute;p họ dễ d&agrave;ng t&igrave;m đến c&aacute;c vị tr&iacute; th&iacute;ch hợp để nhận banh.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">New Attacking Techniques: đơn giản l&agrave; l&agrave;m phong ph&uacute; hơn khả năng tấn c&ocirc;ng của đội b&oacute;ng. N&oacute; đặc biệt hữu &iacute;ch với c&aacute;c tiền đạo c&oacute; kỹ năng dứt điểm tốt khi gia tăng độ hiểm h&oacute;c cho c&aacute;c c&uacute; s&uacute;t.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Physical Play Overhaul: l&agrave; c&ocirc;ng cụ gi&uacute;p tăng khả năng ph&ograve;ng ngự với c&aacute;c pha d&ugrave;ng người che b&oacute;ng. Tuy nhi&ecirc;n t&iacute;nh năng n&agrave;y tỏ ra tương đối &iacute;t khi được sử dụng khi nhịp độ trận đấu cao.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute;i chung cả bốn t&iacute;nh năng tr&ecirc;n tương đối ch&igrave;m, kh&ocirc;ng tạo ra một cuộc c&aacute;ch mạng mới về lối chơi nhưng n&oacute; đem lại nhiều điều mới mẻ m&agrave; th&ocirc;ng qua đ&oacute; gi&uacute;p cho FIFA 17 được đ&aacute;nh gi&aacute; l&agrave; hấp dẫn v&agrave; cuốn h&uacute;t hơn kh&aacute; nhiều so với đối thủ truyền kiếp của m&igrave;nh l&agrave; PES. Bước sang FIFA 18 những t&iacute;nh năng n&agrave;y vẫn c&ograve;n tồn tại v&agrave; n&acirc;ng cấp ch&uacute;t đỉnh, dĩ nhi&ecirc;n vụ th&ecirc;m thắt n&agrave;y thoạt nh&igrave;n c&oacute; ch&uacute;t vẽ rắn th&ecirc;m ch&acirc;n cho c&oacute; vẻ mới lạ bởi phi&ecirc;n bản tiền nhiệm đ&atilde; l&agrave;m kh&aacute; tốt v&agrave; rất kh&oacute; để NSX c&oacute; thể đưa v&agrave;o đ&oacute; thứ g&igrave; đ&oacute; thật sự mới lạ m&agrave; kh&ocirc;ng ph&aacute; hủy to&agrave;n bộ kết cấu của lối chơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-6.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Về tổng thể, c&aacute;c đội trong FIFA 18 đều tu&acirc;n thủ chiến thuật một c&aacute;ch rất kỉ luật, v&igrave; thế những trận đấu được diễn ra kh&aacute; tr&ocirc;i chảy v&agrave; nằm trong tầm xử l&iacute; của người chơi. AI trước đ&acirc;y thường khiến cho cầu thủ qu&aacute; bị thu h&uacute;t bởi b&oacute;ng, điều n&agrave;y diễn ra kh&aacute; thường xuy&ecirc;n khiến người chơi đ&ocirc;i khi thắc mắc h&agrave;ng ph&ograve;ng ngự của m&igrave;nh chạy đ&acirc;u mất? Điều n&agrave;y nay đ&atilde; được khắc phục triệt để. Một trong những sự quan t&acirc;m kh&aacute;c ch&iacute;nh l&agrave; kh&acirc;u xử l&iacute; những đường chuyền. Cụ thể l&agrave; &ldquo;Passing with Purpose&rdquo; giờ c&oacute; thể &aacute;p dụng cho bất k&igrave; những h&igrave;nh thức t&aacute;c động lực l&ecirc;n b&oacute;ng kh&aacute;c, như goal-kicks, chọc khe v&agrave; dứt điểm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Những pha phất b&oacute;ng l&ecirc;n từ thủ m&ocirc;n nay đ&atilde; được c&acirc;n bằng giữa lực v&agrave; độ ch&iacute;nh x&aacute;c, kể cả với những c&uacute; n&eacute;m b&oacute;ng cũng vậy. Thực ra những kiểu ph&aacute;t b&oacute;ng d&agrave;i vẫn kh&aacute; dễ kiểm so&aacute;t, tuy nhi&ecirc;n sự kh&oacute; khăn trong việc kiểm so&aacute;t &ldquo;first touch&rdquo; v&agrave; những bất lợi trong việc mất kiểm so&aacute;t điểm rơi vẫn sẽ l&agrave; trở ngại lớn trong FIFA 18 n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-18.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cảm gi&aacute;c ban đầu của những đường chuyền ngắn trong FIFA 18 nh&igrave;n c&oacute; thể sẽ c&oacute; một ch&uacute;t chậm chạp nếu người chơi đ&atilde; quen kiểu bấm l&agrave; banh được đ&aacute; trong PES, nhưng điều n&agrave;y vẫn t&ugrave;y thuộc v&agrave;o cảm nhận ri&ecirc;ng v&agrave; sở th&iacute;ch ri&ecirc;ng của từng người chơi. Nếu bạn cần đẩy nhanh tốc độ của c&aacute;c pha phối hợp ngắn th&igrave; giờ đ&acirc;y Passing with Purpose sẽ gi&uacute;p bạn thực hiện n&oacute; dễ d&agrave;ng hơn với những cải tiến mới.</span></p>\r\n\r\n<blockquote>\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><em>FIFA dễ chơi nhưng muốn trở th&agrave;nh một tay đ&aacute; FIFA cừ kh&ocirc;i th&igrave; bạn cần phải c&oacute; ch&uacute;t thi&ecirc;n ph&uacute; c&ugrave;ng qu&aacute; tr&igrave;nh r&egrave;n luyện nghi&ecirc;m t&uacute;c. T&oacute;m lại l&agrave; ăn h&agrave;nh nhiều chưa chắc sẽ đ&aacute; hay nhưng chỉ đ&aacute; với m&aacute;y bảo đảm sẽ kh&ocirc;ng bao giờ đ&aacute; hay.</em></strong></span></p>\r\n</blockquote>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sự mới mẻ trong việc thiết lập những pha b&oacute;ng chết cũng l&agrave; một điểm nhấn đ&aacute;ng ch&uacute; &yacute;. Bạn c&oacute; thể t&ugrave;y chỉnh mọi thứ: phạt g&oacute;c, đ&aacute; phạt tr&ecirc;n 40m, lay-off freekicks, thậm ch&iacute; l&agrave; cả n&eacute;m bi&ecirc;n. Về cảm gi&aacute;c ban đầu, n&oacute; giống như cơ chế cũ m&agrave; EA đ&atilde; từng d&ugrave;ng rất l&acirc;u rồi, từ FIFA 2004. Nhưng lần n&agrave;y, sẽ l&agrave; một cuộc c&aacute;ch mạng thật sự.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-19.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thay v&igrave; phải&hellip; nh&igrave;n c&aacute;i lưng c&oacute; số &aacute;o của người đ&aacute; phạt, giờ đ&acirc;y người chơi được ph&eacute;p mở rộng tầm nh&igrave;n bao qu&aacute;t khu vực tấn c&ocirc;ng để c&oacute; thể &ldquo;chạy&rdquo; chiến thuật d&agrave;n xếp của m&igrave;nh một c&aacute;ch trơn tru nhất. C&oacute; thể l&agrave; d&ugrave;ng c&ocirc;ng binh h&uacute;t hậu vệ đối phương, c&oacute; thể l&agrave; chạy &ldquo;cut-in-box&rdquo; song song hai b&ecirc;n v&ograve;ng cấm&hellip; Bạn sẽ được định trước điểm rơi tương đối của b&oacute;ng bằng v&ograve;ng tr&ograve;n nhỏ được đ&aacute;nh dấu tr&ecirc;n mặt s&acirc;n. Tuy nhi&ecirc;n việc chọn điểm rơi lại hoạt động theo cơ chế &ldquo;hold-up&rdquo; &ndash; tức l&agrave; bạn phải giữ analog sau khi chọn được điểm rơi, nếu thả ra điểm chọn sẽ bị quay về chỗ cũ. Điều n&agrave;y đ&ograve;i hỏi người chơi phải cẩn thận hơn v&agrave; ch&uacute; t&acirc;m hơn trong c&aacute;c t&igrave;nh huống thực hiện đ&aacute; phạt.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau đ&oacute; dĩ nhi&ecirc;n, bạn phải canh lực. Thanh lực giờ đ&acirc;y sẽ chạy nhanh hay chậm t&ugrave;y v&agrave;o khả năng phất b&oacute;ng của cầu thủ. Kết hợp với việc chọn điểm rơi, những t&igrave;nh huống b&oacute;ng chết giờ đ&acirc;y sẽ thực sự l&agrave; một thử th&aacute;ch thực sự kh&oacute; nhằn, nhưng sẽ mang lại một sự c&acirc;n bằng tốt hơn. Hệ thống mới n&agrave;y ban đầu c&oacute; thể sẽ l&agrave;m bạn thực sự gặp rắc rối, nhưng chỉ cần một v&agrave;i tiếng đồng hồ l&agrave;m quen v&agrave; mọi thao t&aacute;c sẽ trơn tru như ăn b&aacute;nh tr&ocirc;i nước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-13.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thực tế m&agrave; n&oacute;i, hệ thống AI kh&ocirc;ng c&oacute; qu&aacute; nhiều thay đổi. D&ugrave; cũng c&oacute; những m&agrave;n cải thiện kh&aacute; đ&aacute;ng kinh ngạc trong vấn đề bỏ khoảng trống, nhưng tổng thể kh&ocirc;ng c&oacute; qu&aacute; nhiều những sự đổi mới đ&aacute;ng tr&ocirc;ng chờ, kể cả trong đội của người chơi hay bản th&acirc;n của AI. &Iacute;t ra th&igrave; mọi thứ c&oacute; vẻ trơn tru hơn, chẳng hạn như suy nghĩ về c&aacute;ch di chuyển của cầu thủ được l&agrave;m tốt hơn, kh&ocirc;ng c&ograve;n t&igrave;nh trạng chạy giật cục hay di chuyển ngớ ngẩn theo kiểu đang bận suy nghĩ về nh&acirc;n sinh tr&ecirc;n s&acirc;n b&oacute;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">M&aacute;y vẫn tấn c&ocirc;ng v&agrave;o h&agrave;ng ph&ograve;ng ngự của bạn với tư duy cũ của FIFA 17, v&agrave; cầu thủ dường như vẫn thiếu c&aacute; t&iacute;nh ri&ecirc;ng trong lối chơi. Chẳng hạn khi Mkhitaryan c&oacute; b&oacute;ng ở ngo&agrave;i đời, c&aacute;c fan MU đều sẽ thấy những pha xử l&yacute; mang thương hiệu c&aacute; nh&acirc;n của cầu thủ người Armenia v&agrave; sau đ&oacute; d&ugrave;ng đ&ocirc;i ch&acirc;n ma thuật của m&igrave;nh để uy hiếp thủ m&ocirc;n hoặc tung ra những đường chuyền như dao giải phẫu xuy&ecirc;n thủng hang ph&ograve;ng ngự của đối thủ. Nếu bạn đang điều khiển Mkhitaryan, điều đ&oacute; sẽ xảy ra giống như ngo&agrave;i thực tế nhưng nếu MU do m&aacute;y điều khiển, trừ khi bạn chơi ở những độ kh&oacute; si&ecirc;u cấp như Worldclass hay Legendary, c&aacute;c pha xử l&yacute; động l&ograve;ng người n&agrave;y rất hiếm khi xuất hiện.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>H&agrave;o hứng với cốt truyện của The Journey: Hunter Returns</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu như sự xuất sắc của c&aacute;c vị huấn luận vi&ecirc;n đ&aacute;ng k&iacute;nh vẫn chưa khiến bạn h&agrave;i l&ograve;ng th&igrave; sự trở lại của m&agrave;n chơi cẩu huyết mang đậm chất kịch t&igrave;nh đ&ocirc; thị The Journey chắc chắn sẽ l&agrave;m được điều đ&oacute;. Đ&acirc;y l&agrave; lần thứ hai lối chơi theo cốt truyện được đưa v&agrave;o <a href=\"https://motgame.vn/game-bong-da.tag\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>game b&oacute;ng đ&aacute;</strong></a>. Bạn sẽ được ho&aacute; th&acirc;n v&agrave;o vai &ldquo;một vi&ecirc;n ngọc th&ocirc;&rdquo; c&oacute; t&ecirc;n Alex Hunter bắt đầu cuộc h&agrave;nh tr&igrave;nh để trở th&agrave;nh một cầu thủ đẳng cấp thế giới&hellip; một lần nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-7.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">D&ugrave; hai phi&ecirc;n bản The Journey của FIFA 17 v&agrave; 18 c&oacute; sự li&ecirc;n lạc nhất định thế nhưng t&iacute;nh kế thừa lại kh&ocirc;ng được trơn tru cho lắm v&agrave; đ&ocirc;i khi bản sẽ phải phiền muộn khi nhận ra rằng, những g&igrave; bạn l&agrave;m được cho Alex Hunter trong phần trước dường như kh&ocirc;ng c&oacute; nhiều gi&aacute; trị trong FIFA 18, bởi vậy lời khuy&ecirc;n l&agrave; cứ mặc kệ phần trước v&agrave; xem như trải nghiệm lại cuộc sống của một thanh ni&ecirc;n c&oacute; t&igrave;nh y&ecirc;u m&atilde;nh liệt với b&oacute;ng đ&aacute;, nh&igrave;n nh&acirc;n vật của m&igrave;nh từng bước ph&aacute;t triển từ vi&ecirc;n ngọc th&ocirc; tiềm năng trở th&agrave;nh một si&ecirc;u cấp cự tinh mang tầm v&oacute;c thế giới, c&oacute; lẽ sẽ thoải m&aacute;i hơn l&agrave; việc ho&agrave;i nghi cốt truyện của phần The Journey trước c&oacute; ảnh hưởng thế n&agrave;o đến phần n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn sẽ được biết đến thế n&agrave;o l&agrave; cuộc sống của một vận động vi&ecirc;n chuy&ecirc;n nghiệp từ kh&acirc;u đ&agrave;o tạo, chiến thuật cho đến c&aacute;c g&oacute;c tối ph&iacute;a sau s&acirc;n cỏ hay sự trổi dậy của c&aacute;c thế lực ngầm trong ph&ograve;ng thay đồ. B&ecirc;n cạnh đ&oacute; bạn sẽ th&iacute;ch th&uacute; khi thấy minh được đ&aacute; cặp s&aacute;nh vai với c&aacute;c cầu thủ đẳng cấp thế giới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một số h&igrave;nh ảnh kh&aacute;c của FIFA 18:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-2.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-3.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-5.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-8.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-10.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-11.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-14.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-16.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-20.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-21.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-22.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá FIFA 18, thay đổi nhiều nhưng giá trị mang lại được bao nhiêu?\" src=\"https://motgame.vn/wp-content/uploads/2017/10/Kujimatowa/fifa-18-dg-23.jpg\" style=\"height:419px; width:745px\" /></span></p>', '<p><strong>OS: Windows 10 - 64-Bit&nbsp;</strong></p>\r\n\r\n<p><strong>CPU: Intel i3 6300T or equivalent benchmark (5,199) - Intel i3 4340 (5,226), and Intel i3 4350 (5,302) as alternatives.</strong></p>\r\n\r\n<p><strong>AMD equivalent is AMD Athlon X4 870K (5,219) - AMD FX-4350 (5,272) and FX-4330 (5,297) as alternatives.</strong></p>\r\n\r\n<p><strong>RAM: 8GB</strong></p>\r\n\r\n<p><strong>Hard Drive Space Required: 50.0 GB</strong></p>\r\n\r\n<p><strong>Minimum Supported Video Cards: NVidia GeForce GTX 660 (4,116) 2GB or equivalent * AMD equivalent is Radeon RX 460 (4,218)/R9 270 (4,258)/R7 370 (4,258)</strong></p>\r\n\r\n<p><strong>DirectX: 12</strong></p>', '<p><strong>OS: Windows 10 - 64-Bit&nbsp;</strong></p>\r\n\r\n<p><strong>CPU: Intel i3 6300T or equivalent benchmark (5,199) - Intel i3 4340 (5,226), and Intel i3 4350 (5,302) as alternatives.</strong></p>\r\n\r\n<p><strong>AMD equivalent is AMD Athlon X4 870K (5,219) - AMD FX-4350 (5,272) and FX-4330 (5,297) as alternatives.</strong></p>\r\n\r\n<p><strong>RAM: 8GB</strong></p>\r\n\r\n<p><strong>Hard Drive Space Required: 50.0 GB</strong></p>\r\n\r\n<p><strong>Minimum Supported Video Cards: NVidia GeForce GTX 660 (4,116) 2GB or equivalent * AMD equivalent is Radeon RX 460 (4,218)/R9 270 (4,258)/R7 370 (4,258)</strong></p>\r\n\r\n<p><strong>DirectX: 12</strong></p>', 'Cài đặt và chơi', '1521040399maxresdefault.jpg', 'https://www.youtube.com/embed/0ETKAYRzvjs', 2017, '2018-03-14'),
(11, 6, 'Dynasty Warriors 9', 'dynasty-warriors-9', '<h3><strong>Dynasty Warriors 9 l&agrave; phi&ecirc;n bản mới nhất của d&ograve;ng game đề t&agrave;i Tam Quốc nổi tiếng thế giới, v&agrave; như những tiền nhiệm, n&oacute; cũng bị &quot;n&eacute;m đ&aacute;&quot; kh&ocirc;ng ngớt...</strong></h3>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhắc nhở với&nbsp;c&aacute;c&nbsp;Mọt trước l&agrave; b&agrave;i viết vẫn c&oacute; t&iacute; tinh thần fanboy, nhưng&nbsp;tui vẫn sẽ viết một c&aacute;ch kh&aacute;ch quan nhất c&oacute; thể về Dynasty Warriors 9, phi&ecirc;n bản mới nhất của d&ograve;ng game chặt ch&eacute;m huyền thoại đề t&agrave;i Tam Quốc n&agrave;y. Một phi&ecirc;n bản mới cả về lượng&hellip; lẫn về chất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warrior 9\" src=\"https://media.motgame.vn/2018/03/Mike741/183054_kkhwg6sd25_dy-1.jpg\" style=\"height:332px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vẫn phong c&aacute;ch hack n&rsquo; slash quen thuộc vốn tạo n&ecirc;n thương hiệu của m&igrave;nh, nhưng lồng gh&eacute;p v&agrave;o một thế giới mở rộng lớn, một yếu tố rất mới lạ của phi&ecirc;n bản thứ 9 n&agrave;y. Tuy nhi&ecirc;n, n&oacute; cũng hốt về kha kh&aacute; &ldquo;gạch đ&aacute;&rdquo; đủ để x&acirc;y dựng th&agrave;nh Lạc Dương lu&ocirc;n, trong đ&oacute; c&oacute; kh&ocirc;ng &iacute;t gạch của t&ecirc;n viết b&agrave;i n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nội dung của d&ograve;ng Dynasty Warriors 9&nbsp;th&igrave; chắc kh&ocirc;ng cần phải giải th&iacute;ch g&igrave; th&ecirc;m v&igrave; l&agrave;m vậy n&oacute; thừa th&atilde;i qu&aacute;, c&aacute;i cần tập trung ở đ&acirc;y ch&iacute;nh l&agrave; gameplay. Một chiến trường rộng lớn với h&agrave;ng đống thứ để thực hiện trước khi c&oacute; thể ho&agrave;n th&agrave;nh chiến dịch được giao. Đầu game, người chơi sẽ được điều khiển một trong ba b&aacute; chủ nổi tiếng của Ngụy, Thục, Ng&ocirc; trong một đoạn gameplay để giới thiệu v&agrave; thể hiện những sự kiện quan trọng của mỗi người như kết nghĩa vườn đ&agrave;o của Lưu Bị. Sau đ&oacute; mới mở kh&oacute;a c&aacute;c nh&acirc;n vật kh&aacute;c c&oacute; li&ecirc;n quan&nbsp;v&agrave;&nbsp;bắt đầu v&agrave;o chiến dịch đầu ti&ecirc;n vốn được xem l&agrave; tiền đề cho cuộc nội chi&ecirc;n chia ba thi&ecirc;n hạ nổi tiếng sau n&agrave;y: Chiến dịch diệt giặc Khăn V&agrave;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warrior 9\" src=\"https://media.motgame.vn/2018/03/Mike741/dynasty-warriors-9-yellow-turbans.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Điểm cộng đầu ti&ecirc;n ch&iacute;nh l&agrave; t&iacute;nh &ldquo;chặt ch&eacute;m&rdquo; vẫn được giữ nguy&ecirc;n, với bản đồ rộng khắp c&ugrave;ng số lượng kẻ địch đ&ocirc;ng đảo tha hồ cho bạn ch&eacute;m giết đ&atilde; tay. Lần n&agrave;y Koei đem đến cho ch&uacute;ng ta một hệ thống chiến đấu ho&agrave;n to&agrave;n mới chứ kh&ocirc;ng chỉ g&oacute;i gọn trong c&ocirc;ng thức vu&ocirc;ng, vu&ocirc;ng, vu&ocirc;ng rồi tam gi&aacute;c bla bla như hồi xưa nữa. Ngo&agrave;i c&aacute;c đ&ograve;n đ&aacute;nh thường, bạn c&ograve;n c&oacute; thể kết hợp R1 để thực hiện th&ecirc;m Launch Combo, Stun Combo v&agrave; cả Knockdown Combo nữa. Đi sau đ&oacute; l&agrave; hệ thống Flow Attack k&egrave;m theo, tạo n&ecirc;n những ph&acirc;n đoạn&nbsp;&ldquo;đ&aacute;nh như l&agrave;m xiếc&rdquo;&nbsp;ngầu qu&aacute; x&aacute;, đảm bảo cảm gi&aacute;c kh&ocirc;ng thua g&igrave; &ldquo;pro&rdquo; lu&ocirc;n. Đ&acirc;y được xem như l&agrave; điểm cộng rất lớn m&agrave; Dynasty Warriors 9 mang lại. Ngo&agrave;i hạn chế kiểu chơi game mash n&uacute;t &ldquo;như chết n&atilde;o&rdquo;, bạn c&ograve;n c&oacute; thể t&ugrave;y biến combo của m&igrave;nh theo bất cứ c&aacute;ch n&agrave;o m&igrave;nh mong muốn. Nếu kh&eacute;o l&eacute;o v&agrave; tập luyện th&agrave;nh thục, tr&ugrave;m đ&atilde; bị bạn đ&aacute;nh tr&uacute;ng th&igrave; sẽ kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o đứng dậy được nữa cho tới khi hết m&aacute;u &ldquo;ngủm củ tỏi&rdquo; lu&ocirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warroir 9\" src=\"https://media.motgame.vn/2018/03/Mike741/img3-2_eprh.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kế tiếp l&agrave; hệ thống bản đồ cực kỳ lớn v&agrave; trải d&agrave;i, mỗi nơi bạn đi qua đều c&oacute; nhiệm vụ hay thứ g&igrave; đ&oacute; hay ho để kh&aacute;m ph&aacute;. Tất cả nhiệm vụ hầu như đều c&oacute; mối li&ecirc;n quan với nhau v&agrave; sẽ gi&uacute;p giảm độ kh&oacute; của chiến dịch đi rất nhiều, l&agrave;m cho việc giết tr&ugrave;m&nbsp;trở n&ecirc;n&nbsp;dễ thở hơn ch&uacute;t. Tốt nhất l&agrave; bạn cứ ưu ti&ecirc;n&nbsp;c&agrave;y hết nhiệm vụ trước khi kết th&uacute;c một chiến dịch.&nbsp;Vừa c&oacute; kinh nghiệm để c&oacute; điểm n&acirc;ng cấp sức mạnh cho bản th&acirc;n, vừa c&oacute; thể thu thập nguy&ecirc;n liệu để tạo ra vũ kh&iacute; mới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warrior 9\" src=\"https://media.motgame.vn/2018/03/Mike741/encountering-panda.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một điểm s&aacute;ng nữa l&agrave; cơ chế chế tạo khảm.&nbsp;Khảm trang bị&nbsp;th&igrave; chắc bạn n&agrave;o từng chơi mấy game như Diablo hay&nbsp;c&aacute;c game online&nbsp;th&igrave; sẽ biết rất r&otilde;, kh&ocirc;ng cần nhắc đến nhiều. Bạn được khảm tối đa bốn loại ngọc thuộc t&iacute;nh cho từng vị tr&iacute; đ&ograve;n đ&aacute;nh trong game. V&iacute; dụ Launch Combo th&igrave; d&ugrave;ng ngọc S&eacute;t, Knockdown Combo th&igrave; d&ugrave;ng Ngọc Hỏa chẳng hạn. Kh&ocirc;ng chỉ&nbsp;cường h&oacute;a đ&ograve;n đ&aacute;nh, m&agrave; c&ograve;n đi k&egrave;m hiệu ứng đẹp mắt s&aacute;ng l&oacute;a cả m&agrave;n h&igrave;nh. V&agrave; điều hay ho nhất l&agrave;, bạn c&oacute; thể tự chế tạo&nbsp;những&nbsp;thứ n&agrave;y được hết chứ kh&ocirc;ng cần phải t&igrave;m kiếm đ&acirc;u xa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể n&oacute;i, trong c&aacute;i thế giới Dynasty Warriors 9 n&agrave;y &ocirc;ng thợ r&egrave;n l&agrave; đa dụng nhất. C&aacute;i g&igrave; &ocirc;ng nội n&agrave;y&nbsp;cũng biết l&agrave;m từ thập b&aacute;t ban binh kh&iacute; tới c&aacute;c loại ngọc đều một tay &ocirc;ng ấy m&agrave; th&agrave;nh.&nbsp;Ngo&agrave;i ra &ocirc;ng n&agrave;y&nbsp;c&ograve;n c&oacute; nghề tay tr&aacute;i l&agrave; bu&ocirc;n b&aacute;n vũ kh&iacute;&nbsp;với gi&aacute; cắt cổ&nbsp;(b&aacute;n cho tướng lĩnh cũng d&aacute;m h&eacute;t gi&aacute; như thường). Chỉ cần bạn đủ tiền, hoặc đủ nguy&ecirc;n vật liệu th&igrave; cứ tới gặp thằng cha n&agrave;y l&agrave; được. C&ograve;n mấy thứ thuốc hay thực phẩm chức năng tăng cường m&aacute;u s&aacute;t thương g&igrave; g&igrave; đ&oacute; l&agrave; do một b&agrave; kh&aacute;c b&aacute;n. B&agrave; cũng gi&uacute;p m&igrave;nh điều chế nếu c&oacute; đủ nguy&ecirc;n liệu. Đ&uacute;ng chuẩn nhập vai thế giới mở nhỉ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thế nhưng, Mọt tui vẫn m&eacute;o hiểu tại sao c&aacute;i cơ chế chiến đấu với &yacute; tưởng hay thế, m&agrave; c&aacute;i thế giới mở của game lại &ldquo;như đống shit&rdquo; ấy.&nbsp;Dẫu biết đ&acirc;y l&agrave; lần đầu ti&ecirc;n, nhưng trước đ&oacute; sản phẩm th&agrave;nh c&ocirc;ng c&oacute; đầy m&agrave;, d&agrave;nh thời gian học hỏi c&aacute;i hay của người ta rồi l&agrave;m của m&igrave;nh cũng được. Chứ tham lam để rồi chất lượng giảm xuống th&igrave; c&oacute; phải lợi bất cập hại kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warrior 9\" src=\"https://media.motgame.vn/2018/03/Mike741/lu-bu.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thứ nhất l&agrave; ch&iacute;nh l&agrave; việc bản đồ qu&aacute; to. To th&igrave; đ&uacute;ng rồi, nhưng to vừa vừa th&ocirc;i chứ. C&aacute;i game chặt ch&eacute;m g&igrave; m&agrave; d&agrave;nh gần hết thời gian để &ldquo;cắm đầu chạy&rdquo; tới c&aacute;c chỗ chiến trường th&igrave; ai m&agrave; th&iacute;ch nổi. D&ugrave; đ&atilde; th&ecirc;m khả năng dịch chuyển ở những khu vực qu&acirc;n m&igrave;nh chiếm đ&oacute;ng, nhưng nếu mục ti&ecirc;u m&agrave; nằm&nbsp;t&iacute;t tắp&nbsp;trong l&ograve;ng địch, th&igrave; bạn cũng chạy bở hơi tai mới đến nơi. Đ&oacute; l&agrave; chưa t&iacute;nh c&aacute;i hệ thống auto -run của Dynasty Warriors 9 kh&ocirc;ng biết n&ecirc;n gọi l&agrave; tốt hay xấu nữa. Mục ti&ecirc;u chỉ cần băng qua c&aacute;nh đồng l&agrave; tới nơi. Kh&ocirc;ng! Phải chạy ra đường c&aacute;i, rồi chạy men theo đường c&aacute;i v&ograve;ng v&egrave;o mới tới được. Tu&acirc;n thủ luật lệ giao th&ocirc;ng l&agrave; tốt thật, nhưng đ&aacute;nh trận dầu s&ocirc;i lửa bỏng thế n&agrave;y th&igrave; chả c&oacute; chốt n&agrave;o đứng đ&acirc;u m&agrave; cứ phải đi theo lộ tr&igrave;nh mở đ&uacute;ng đ&egrave;n t&iacute;n hiệu thế hả?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warrior 9\" src=\"https://media.motgame.vn/2018/03/Mike741/review-dynasty-warriors-9-scenery.png\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thứ hai n&ecirc;n b&agrave;n đến l&agrave; AI của game v&agrave; hệ thống phản đ&ograve;n của game kh&aacute; l&agrave; ngu. Phản đ&ograve;n trong game n&ocirc;m na l&agrave; khi kẻ địch tấn c&ocirc;ng bạn, bạn chỉ cần nhấn tam gi&aacute;c l&agrave; nh&acirc;n vật tự lao tới kết liễu kẻ địch. Nhưng nhiều khi chả cần n&oacute; đ&aacute;nh m&igrave;nh m&agrave; chỉ cần vung tay một c&aacute;i m&agrave; phản đ&ograve;n l&agrave; m&igrave;nh bay tới cả nửa bản đồ để kiết liễu n&oacute; như một vị thần. Hay th&igrave; c&oacute; hay m&agrave; thấy n&oacute; phi l&yacute; thế n&agrave;o ấy, bị y chang <a href=\"https://motgame.vn/middle-earth-shadow-of-mordor.tag\"><strong>Shadow of Mordor</strong></a> lu&ocirc;n. V&agrave; d&ugrave; Mọt tui đ&atilde; chỉnh tới chế độ Chaos, thế m&agrave; AI vẫn cứ lơ tơ mơ thế n&agrave;o ấy. M&agrave; tui cũng chưa r&otilde; l&agrave; địch Normal với địch Chaos kh&aacute;c nhau chỗ n&agrave;o nốt, khi bắn cung một ph&aacute;t cũng chết. Hay l&agrave; do ham&nbsp;l&agrave;m&nbsp;thế giới mở qu&aacute; n&ecirc;n qu&ecirc;n vặn n&uacute;m chỉnh lại c&aacute;c mức độ kh&oacute; cho game nhỉ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tiếp nữa l&agrave; hệ thống nhiệm vụ trong game. N&oacute; gặp cực kỳ nhiều vấn đề, c&oacute; thể coi như lỗi game. V&iacute; dụ trong nhiệm vụ hủy diệt&nbsp;c&aacute;c&nbsp;Ritual Tools, nhiệm vụ b&aacute;o chạy đến vị tr&iacute;. Chạy tới nơi th&igrave; chả thấy c&aacute;i qu&aacute;i g&igrave; ở đ&oacute; để đập ph&aacute; hay hủy hết. Cứ nghĩ chắc do m&aacute;y m&igrave;nh hay lag n&ecirc;n load lại file save, n&oacute; vẫn cứ như vậy. M&agrave;&nbsp;&ldquo;thốn&rdquo; ở chỗ&nbsp;l&agrave; bắt buộc phải thực hiện xong nhiệm vụ đ&oacute; v&igrave; n&oacute; nằm trong cốt truyện v&agrave; mở kh&oacute;a nhiệm vụ mới đấy nh&eacute;. Khổi l&agrave;m xong cứ đứng đ&oacute; khỏi qua lu&ocirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Dynasty Warrior 9\" src=\"https://media.motgame.vn/2018/03/Mike741/swp40641_s2.jpg\" style=\"height:418px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">M&agrave; thế giới mở qu&aacute; đ&acirc;m ra cũng kh&ocirc;ng hợp với đặc t&iacute;nh của c&aacute;i game n&agrave;y.&nbsp;Dường&nbsp;như l&agrave; Koei cố &yacute; bỏ qua c&aacute;i gọi l&agrave; &ldquo;giới hạn thời gian&rdquo; cho từng chiến dịch. Chiến tranh th&igrave; ai cũng biết rồi, thời gian l&agrave; thứ qu&yacute; gi&aacute; nhất, nhưng n&oacute; lại chả đ&uacute;ng Dynasty Warriors 9. Trong khi người ta đang h&ocirc; h&agrave;o bắt thi&ecirc;n tử lệnh chư hầu c&ograve;n nh&acirc;n vật ch&iacute;nh như m&igrave;nh th&igrave; thong dong đi sắn cọp, săn gấu hay h&aacute;i hoa bắt bướm về l&agrave;m nguy&ecirc;n liệu?&nbsp;Bạn cứ việc rong chơi ch&aacute;n th&igrave; th&ocirc;i, Trương Gi&aacute;c hay Đổng Tr&aacute;c n&oacute; sẽ cắm trại ngồi uống tr&agrave; đợi bạn.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', '<p>Hệ điều h&agrave;nh: Windows 7/8.1/10 64bit</p>\r\n\r\n<p>Bộ vi xư l&yacute;: Core i5 2400 trở l&ecirc;n</p>\r\n\r\n<p>Bộ nhớ: 6 GB RAM</p>\r\n\r\n<p>Card đồ họa: NVIDIA GeForce GTX 660 trở l&ecirc;n</p>\r\n\r\n<p>DirectX: Version 11</p>\r\n\r\n<p>Dung lượng: trống hơn 50 GB</p>', '<p>Hệ điều h&agrave;nh: Windows 7/8.1/10 64bit</p>\r\n\r\n<p>Bộ vi xư l&yacute;: Core i5 2400 trở l&ecirc;n</p>\r\n\r\n<p>Bộ nhớ: 6 GB RAM</p>\r\n\r\n<p>Card đồ họa: NVIDIA GeForce GTX 660 trở l&ecirc;n</p>\r\n\r\n<p>DirectX: Version 11</p>\r\n\r\n<p>Dung lượng: trống hơn 50 GB</p>', 'Cài đặt và chơi', '1521042775DynastyWarriors9_Review_01.jpg', 'https://www.youtube.com/embed/BlE4tQl-8is', 2018, '2018-03-14');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(12, 6, 'Portal 2', 'portal-2', '<h3 style=\"text-align:justify\">Trong ng&agrave;nh c&ocirc;ng nghiệp phim ảnh, một người đạo diễn được coi l&agrave; th&agrave;nh c&ocirc;ng nếu &ocirc;ng ta bắt kịp được tần suất cứ một thập kỉ th&igrave; lại l&agrave;m được một bộ phim lớn.&nbsp;Với ng&agrave;nh c&ocirc;ng nghiệp game, tần suất ấy kh&ocirc;ng thể c&aacute;ch 10 năm được, một h&atilde;ng game chỉ c&oacute; thể coi l&agrave; th&agrave;nh c&ocirc;ng nếu như họ ra đời li&ecirc;n tiếp những sản phẩm chất lượng tốt, v&agrave; nếu chẳng may ra đời một tựa game kh&ocirc;ng xứng tầm t&ecirc;n tuổi, một khả năng cao t&ecirc;n tuổi studio sẽ bị một vết nhơ v&agrave; bị c&aacute;c ch&iacute;nh c&aacute;c fan quay lưng thất vọng.</h3>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Với Valve, một studio với t&ecirc;n tuổi được x&acirc;y dựng bởi những sản phẩm th&agrave;nh c&ocirc;ng li&ecirc;n tiếp, thật vậy, bạn h&atilde;y thử l&ecirc;n Wikipedia v&agrave; xem c&oacute; game n&agrave;o bị c&aacute;c nh&agrave; ph&ecirc; b&igrave;nh ch&ecirc; tr&aacute;ch, một sản phẩm tồi sẽ l&agrave;m hỏng tất cả những tiếng tăm m&agrave; Valve từng bước x&acirc;y dựng suốt hơn một thập kỉ qua. Sản phẩm gần đ&acirc;y nhất của studio,&nbsp;Portal 2, một thương hiệu kh&aacute; mới mẻ, lại thuộc thể loại k&eacute;n người chơi - giải đố, liệu c&oacute; thể &iacute;t nhất thu h&uacute;t được người chơi kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thật vui mừng, khi c&acirc;u trả lời l&agrave; c&oacute;. Thậm ch&iacute; d&ugrave; bạn l&agrave; bất cứ ai, bạn sẽ c&oacute; thể tận hưởng được&nbsp;Portal 2.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn đ&atilde; chơi Portal 1, về cốt l&otilde;i, c&aacute;ch chơi của&nbsp;Portal 2&nbsp;kh&ocirc;ng c&oacute; thay đổi g&igrave; đ&aacute;ng kể. Người chơi v&agrave;o vai Chell, một c&ocirc; g&aacute;i cứng đầu (v&agrave; cũng &iacute;t n&oacute;i như ch&agrave;ng Gordon Freeman), sử dụng khẩu s&uacute;ng gọi tắt l&agrave; Portal gun để giải những c&acirc;u đố trong m&agrave;n chơi, cụ thể l&agrave; t&igrave;m c&aacute;ch mở được c&aacute;nh cửa Exit. Điểm đặc biệt của Portal ch&iacute;nh l&agrave; khẩu s&uacute;ng bắn cổng kh&ocirc;ng gian v&agrave;ng / xanh tr&ecirc;n những bức tường trắng. Với hai cổng kh&ocirc;ng gian n&agrave;y, Chell c&oacute; thể bước v&agrave;o một cổng v&agrave; tho&aacute;t ra ở cổng kia, cho d&ugrave; hai bức tường trắng ấy ở c&aacute;ch nhau bao xa.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n, đ&atilde; l&agrave; game giải đố th&igrave; phải c&oacute; những thử th&aacute;ch. Qua từng m&agrave;n chơi, người chơi được giới thiệu th&ecirc;m một chức năng mới, cũng như những thử th&aacute;ch của n&oacute;. Với độ kh&oacute; tăng dần, từ rất đơn giản khi người chơi mới lần đầu gặp chiếc v&aacute;n l&ograve; xo chẳng hạn, cho tới kh&aacute; phức tạp khi phải kết hợp với những kĩ năng người chơi c&oacute; từ những m&agrave;n chơi trước trong những c&acirc;u đố tiếp theo.&nbsp;Portal 2&nbsp;giới thiệu th&ecirc;m kh&aacute; nhiều yếu tố mới ho&agrave;n to&agrave;n so với phần 1, như cầu Hard-light bridge để người chơi kết hợp với s&uacute;ng Portal để bắc cầu; c&aacute;c tấm v&aacute;n l&ograve; xo cực mạnh Aerial fate plate; v&agrave; th&uacute; vị nhất l&agrave; 3 loại gel xanh, cam v&agrave; trắng với 3 chức năng kh&aacute;c nhau.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn cảm thấy ngại kh&oacute;, th&igrave; đừng lo, bởi&nbsp;Portal 2&nbsp;đ&atilde; chứng tỏ được sự t&agrave;i t&igrave;nh v&agrave; chuy&ecirc;n nghiệp trong c&aacute;ch thiết kế m&agrave;n chơi của Valve. D&ugrave; thế n&agrave;o đi chăng nữa, bạn cũng kh&ocirc;ng thể bị b&iacute; trong một c&acirc;u đố của&nbsp;Portal 2. V&igrave; ch&uacute;ng qu&aacute; dễ ư? Kh&ocirc;ng phải vậy, nhiều c&acirc;u đố phải n&oacute;i thật l&agrave; kh&aacute; thử th&aacute;ch, nhưng chỉ cần c&oacute; yếu tố thời gian, bất cứ người chơi n&agrave;o cũng c&oacute; thể giải được c&acirc;u đố ấy.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Valve lu&ocirc;n biết c&aacute;ch gợi &yacute; ngầm người chơi, bằng c&aacute;ch g&acirc;y sự ch&uacute; &yacute; của người chơi tới điểm gợi &yacute; với c&aacute;c hiệu ứng như &acirc;m thanh, những chuyển động v&agrave; th&uacute; vị nhất l&agrave; &aacute;nh s&aacute;ng. Một gợi &yacute; cho những ai đang chơi&nbsp;Portal 2: h&atilde;y ch&uacute; &yacute; quan s&aacute;t tất cả mọi thứ trong m&agrave;n chơi trước khi bắt tay v&agrave;o giải đố. V&agrave; đừng nản l&ograve;ng với bất cứ m&agrave;n chơi n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Valve lu&ocirc;n được nhiều người v&iacute; von như l&agrave; Pixar của thế giới game. H&agrave;m &yacute; n&agrave;y c&oacute; thể hiểu như &aacute;m chỉ sự bất bại của Valve khi chưa c&oacute; sản phẩm n&agrave;o l&agrave; kh&ocirc;ng chinh phục đựơc game thủ, cũng như chưa c&oacute; bộ phim Pixar n&agrave;o l&agrave; kh&ocirc;ng l&agrave;m rung động tr&aacute;i tim của bất cứ người xem n&agrave;o. Nhưng c&aacute; nh&acirc;n người viết th&iacute;ch hiểu h&agrave;m &yacute; n&agrave;y theo một c&aacute;ch kh&aacute;c: Valve, cũng như Pixar, biết c&aacute;ch thổi hồn v&agrave;o sản phẩm của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cũng như phần đầu, xuy&ecirc;n suốt&nbsp;Portal 2&nbsp;bạn sẽ kh&ocirc;ng bắt gặp một b&oacute;ng người n&agrave;o trong game (ngoại trừ bạn &ndash; c&ocirc; g&aacute;i Chell với tung t&iacute;ch b&iacute; ẩn v&agrave; lu&ocirc;n c&acirc;m như hến).&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Những g&igrave; c&ograve;n lại của con người chỉ l&agrave; những nh&agrave; m&aacute;y, những văn ph&ograve;ng v&agrave; đồ d&ugrave;ng. Tuy nhi&ecirc;n, d&ugrave; thiếu vắng b&oacute;ng người trong những căn ph&ograve;ng ấy, nhưng hầu như kh&ocirc;ng c&oacute; căn ph&ograve;ng n&agrave;o l&agrave; thực sự lạnh lẽo cả. Để &yacute; những đồ nội thất, người chơi cũng c&oacute; thể đo&aacute;n được ph&ograve;ng n&agrave;y trước kia l&agrave; g&igrave;, đọc những tờ b&aacute;o c&ograve;n s&oacute;t lại tr&ecirc;n b&agrave;n để biết th&ecirc;m về c&ocirc;ng ty Aperture, cũng như cảm nhận được văn ph&ograve;ng n&agrave;y được x&acirc;y dựng thời k&igrave; n&agrave;o, những năm 50, 70 hay 80-90 chỉ bằng c&aacute;ch nh&igrave;n v&agrave;o đồ nội thất. Bạn kh&ocirc;ng tin ư? Cứ thử &nbsp;chơi đến tầm chương 6 trở đi!</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&oacute; l&agrave; những c&aacute;i hồn của vật tĩnh, c&ograve;n c&aacute;i hồn của vật động th&igrave; sao? V&igrave; bạn sẽ kh&ocirc;ng gặp ai trong game cả, nhưng thay v&agrave;o đ&oacute;, bạn sẽ gặp những robot v&agrave; m&aacute;y t&iacute;nh của ph&ograve;ng th&iacute; nghiệm Aperture. V&agrave; phải th&aacute;n phục với t&agrave;i năng của Valve, v&igrave; cho d&ugrave; những robot ấy c&oacute; h&igrave;nh d&aacute;ng như thế n&agrave;o, bạn vẫn cảm nhận được sức sống trong chuyển động của ch&uacute;ng, thậm ch&iacute; đo&aacute;n được cảm x&uacute;c qua những cử chỉ.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/02/28/portal11.jpg\" style=\"width:500px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhưng nếu đề cập tới phần hồn của game m&agrave; kh&ocirc;ng đề cập tới sự h&agrave;i hước của game th&igrave; thật thiếu s&oacute;t. Đ&atilde; từ l&acirc;u lắm rồi, c&oacute; lẽ từ sau bản remake Monkey Island 2: LeChuck&rsquo;s Revenge, người chơi mới được cười nhiều với một tựa game như vậy. Lời thoại của&nbsp;Portal 2&nbsp;thật sự xuất sắc, cảm tưởng như cứ tầm 3 c&acirc;u thoại, bạn sẽ phải bật cười một lần.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Những c&acirc;u n&oacute;i ngờ nghệch v&agrave; thật sự c&oacute; hồn của Wheatley khiến ta c&oacute; cảm tưởng m&igrave;nh đang song h&agrave;nh c&ugrave;ng một anh ch&agrave;ng người Anh ngố ngố hơn l&agrave; một chiếc l&otilde;i AI. Đặc biệt l&agrave; những lời thoại ng&agrave;n v&agrave;ng của GLaDOS, kh&ocirc;ng ngớ ngẩn như Wheatley nhưng lại mang sắc th&aacute;i mỉa mai sẽ khiến bạn vừa r&ugrave;ng m&igrave;nh, vừa bật cười.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Xin tiện kh&ocirc;ng tr&iacute;ch dẫn một v&agrave;i v&iacute; dụ, bởi khả năng sẽ l&agrave;m bạn mất bất ngờ. Đỉnh cao của những cuộc đối đ&aacute;p, theo người viết, c&oacute; lẽ l&agrave; Chapter 9 của game: This is the part where he kills you.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave; xin bật m&iacute;,&nbsp;Portal 2&nbsp;sẽ c&oacute; m&agrave;n chơi đấu tr&ugrave;m hay v&agrave; độc đ&aacute;o nhất với tất cả mọi người. Chỉ trong dưới 5 ph&uacute;t, bạn sẽ rơi v&agrave;o trạng th&aacute;i cảm x&uacute;c kh&aacute; phức tạp: vừa gấp r&uacute;t suy nghĩ, vừa cười s&aacute;i miệng, v&agrave; vừa h&aacute; hốc miệng ngạc nhi&ecirc;n. Tin t&ocirc;i đi.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều người n&oacute;i với độ tuổi 7-8 năm, c&oacute; lẽ Source Engine đ&atilde; qu&aacute; gi&agrave; cỗi. Nhưng với&nbsp;Portal 2, điều đ&oacute; khẳng định ngược lại. Với nhiều hiệu ứng &aacute;nh s&aacute;ng &ndash; b&oacute;ng tối ấn tượng hơn, c&oacute; thể thấy Source engine đ&atilde; được n&acirc;ng cấp để bắt kịp thời đại. Chưa kể những hiệu ứng vật l&iacute; kh&aacute; phức tạp, với quy m&ocirc; l&ecirc;n tới cả m&agrave;n chơi rộng lớn.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần &acirc;m thanh của&nbsp;Portal 2&nbsp;cũng l&agrave; một điểm ng&agrave;n vang của game. C&aacute;c bản nhạc v&agrave; hiệu ứng &acirc;m thanh họat động rất ổn với nhau, tới mức trong một số đoạn, những h&agrave;nh động của Chell như nhảy, cũng tạo th&agrave;nh một nốt nhạc l&agrave;m điểm nhấn cho bản nhạc đang s&ocirc;i động, tăng sự phấn kh&iacute;ch của người chơi l&ecirc;n cực độ. V&agrave; một điểm thưởng tới d&agrave;n nhạc cuối game, cũng như b&agrave;i h&aacute;t Want you gone do Ellen McLane / GLaDOS tr&igrave;nh b&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/02/28/portal2ps32.jpg\" style=\"width:500px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần chơi co-op của&nbsp;Portal 2&nbsp;cũng l&agrave; một phần chơi th&uacute; vị kh&ocirc;ng k&eacute;m phần chơi đơn, d&ugrave; kh&ocirc;ng d&agrave;i bằng. Sự th&uacute; vị của phần chơi co-op n&agrave;y nằm ở độ kh&oacute; hơn hẳn so với chơi đơn, v&agrave; ở ch&iacute;nh sự tương t&aacute;c giữa người chơi với người chơi. Phần chơi co-op y&ecirc;u cầu cả hai người chơi phải l&agrave;m việc nh&oacute;m lẫn l&agrave;m việc độc lập thật tốt để qua được những m&agrave;n chơi. Phần thưởng sau khi cả hai người chơi giải đố đ&ocirc;i khi l&agrave; những cử động của hai ch&uacute; robot, từ cười, nhảy cho tới đập tay v&agrave;&hellip; oẳn t&ugrave; t&igrave; ngộ nghĩnh.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&atilde; l&acirc;u lắm rồi, kể từ khi sau Red Dead Redemption, người viết mới cảm thấy thực sự thỏa m&atilde;n, tới độ hơi &ldquo;ph&ecirc; ph&ecirc;&rdquo; khi kết th&uacute;c game&nbsp;Portal 2. Ho&agrave;n hảo trong mọi mặt,&nbsp;Portal 2&nbsp;sẽ c&ograve;n được game thủ nhắc đến kh&ocirc;ng chỉ năm 2011, m&agrave; c&ograve;n 2012 v&agrave; l&acirc;u hơn thế nữa.&nbsp;</span></p>', '<p><strong>OS: Windows 7 / Vista / XP<br />\r\nProcessor: 3.0 GHz P4, Dual Core 2.0 (or higher) or AMD64X2 (or higher)<br />\r\nMemory: 1GB XP / 2GB Vista<br />\r\nHard Disk Space: At least 7.6 GB of Space<br />\r\nVideo: Video card must be 128 MB or more and should be a DirectX 9-compatible with support for Pixel Shader 2.0b (ATI Radeon X800 or higher / NVIDIA GeForce 7600 or higher / Intel HD Graphics 2000 or higher).<br />\r\nAudio: DirectX 9.0c compatible</strong></p>', '<p><strong>OS: Windows 7 / Vista / XP<br />\r\nProcessor: 3.0 GHz P4, Dual Core 2.0 (or higher) or AMD64X2 (or higher)<br />\r\nMemory: 1GB XP / 2GB Vista<br />\r\nHard Disk Space: At least 7.6 GB of Space<br />\r\nVideo: Video card must be 128 MB or more and should be a DirectX 9-compatible with support for Pixel Shader 2.0b (ATI Radeon X800 or higher / NVIDIA GeForce 7600 or higher / Intel HD Graphics 2000 or higher).<br />\r\nAudio: DirectX 9.0c compatible</strong></p>', 'Cài đặt và chơi', '1521044119Portal 2 d5s.jpg', 'https://www.youtube.com/embed/tax4e4hBBZc', 2011, '2018-03-14'),
(13, 6, 'Devil May Cry', 'devil-may-cry', '<h3 style=\"text-align:justify\">Khoảng thời gian đợi chờ đ&atilde; gần như kết th&uacute;c, v&agrave; cộng đồng game thủ sắp sửa được chạm tay v&agrave;o phi&ecirc;n bản reboot của d&ograve;ng game chặt ch&eacute;m kh&eacute;t tiếng&nbsp;Devil May Cry. Được thai ngh&eacute;n bởi Ninja Theory, studio đ&atilde; từng tạo n&ecirc;n ấn tượng với cộng đồng qua c&aacute;c t&aacute;c phẩm như&nbsp;Heavenly Sword&nbsp;v&agrave;o năm 2007 hay&nbsp;Enslaved: Odyssey to the West&nbsp;v&agrave;o năm 2010. Đ&atilde; c&oacute; kh&ocirc;ng &iacute;t những fan của d&ograve;ng game của Capcom đ&atilde; tỏ vẻ ho&agrave;i nghi, liệu rằng số phận của Dante trong b&agrave;n tay nh&agrave;o nặn của studio đến từ Cambridge, Anh Quốc sẽ ra sao? V&igrave; thế, ngay trước khi chạm tay v&agrave;o tựa game, h&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i điểm qua những n&eacute;t ch&iacute;nh tạo n&ecirc;n sắc th&aacute;i kh&aacute;c biệt cũng như &ldquo;ăn điểm&rdquo; của&nbsp;DmC&nbsp;phi&ecirc;n bản 2013 ngay sau đ&acirc;y.</h3>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><strong>Cốt truyện</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Cốt truyện của game tương đối đơn giản, Dante, một tay thợ săn quỷ trẻ tuổi vướng v&agrave;o cuộc chiến với đội qu&acirc;n b&oacute;ng tối m&agrave; kẻ cầm đầu kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; Mundus, ch&uacute;a tể lo&agrave;i quỷ dữ v&agrave; cũng từng l&agrave; người anh em v&agrave;o sinh ra tử với cha của anh, chiến binh huyền thoại Sparda. Th&ocirc;ng qua tổ chức the Order, m&agrave; cầm đầu lại l&agrave; Vergil, người anh song sinh của Dante, ch&agrave;ng trai trẻ bắt đầu cuộc chiến với những thế lực to lớn một mặt kiểm so&aacute;t thế giới lo&agrave;i người đang u m&ecirc; v&agrave; lạc lối, mặt kh&aacute;c cố gắng x&oacute;a sổ những thợ săn quỷ c&ograve;n s&oacute;t lại như Dante.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><strong>Ấn tượng đồ họa</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Nếu như những phi&ecirc;n bản&nbsp;<em>Devil May Cry<strong>&nbsp;</strong></em>trước đ&acirc;y g&acirc;y ấn tượng với người chơi th&ocirc;ng qua phong c&aacute;ch đồ họa đậm chất cổ điển, với những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c Gothic ho&agrave;nh tr&aacute;ng, th&igrave;&nbsp;<em>DmC</em>&nbsp;qua b&agrave;n tay của Ninja Theory lại l&agrave; một th&aacute;i cực kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 2\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/DMC_screenshot4-72937.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 2\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em>&quot;L&ecirc;n TV rồi k&igrave;a&quot;.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">N&eacute;t cổ điển vẫn c&ograve;n đ&oacute;, nhưng bao tr&ugrave;m l&ecirc;n tr&ecirc;n tất cả l&agrave; phong c&aacute;ch u &aacute;m của một thế giới bị kiểm so&aacute;t bởi quỷ dữ, v&agrave; đối nghịch lại với sự u &aacute;m, l&agrave; những mảng m&agrave;u tương phản kh&ocirc;ng hề dịu mắt một ch&uacute;t n&agrave;o, l&agrave; những bức vẽ graffiti thể hiện sự nổi loạn v&agrave; bất cần của nh&acirc;n vật ch&iacute;nh. C&oacute; thể nhiều người sẽ kh&ocirc;ng th&iacute;ch h&igrave;nh tượng Dante cũng như thế giới m&agrave; Ninja Theory tạo ra v&igrave; n&oacute; rời xa phong c&aacute;ch truyền thống của series, nhưng nếu đứng tr&ecirc;n phương diện một tr&ograve; chơi độc lập, mặt h&igrave;nh ảnh của game thật sự kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng để ph&agrave;n n&agrave;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><strong>Gameplay lạ m&agrave; quen</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Qua nhiều b&agrave;i viết trước đ&acirc;y, chắc hẳn c&aacute;c bạn cũng đ&atilde; được chi&ecirc;m ngưỡng qua một v&agrave;i clip gameplay hay combo của nh&acirc;n vật ch&iacute;nh. V&agrave; sau khi chơi thử, GameK buộc l&ograve;ng phải tặng th&ecirc;m một điểm cộng cho đội ngũ thiết kế của Ninja Theory. Từng combo, từng đ&ograve;n kết liễu với c&acirc;y kiếm Rebellion hay cặp s&uacute;ng Ebony n&rsquo; Ivory đều kh&ocirc;ng kh&aacute;c qu&aacute; nhiều so với những Dante gi&agrave; dặn trong c&aacute;c phi&ecirc;n bản trước. V&agrave; như một lẽ dĩ nhi&ecirc;n, Dante trẻ tuổi dường như vẫn chưa thể quen với c&acirc;y kiếm nặng nề, những thao t&aacute;c vẫn c&ograve;n ch&uacute;t l&oacute;ng ng&oacute;ng v&agrave; hay mất đ&agrave; sau một lượt combo.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 3\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/DmC-Devil-May-Cry-Screenshot-011-72937.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 3\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em>Khả năng của lưỡi h&aacute;i&nbsp;Osiris.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Thay đổi tất cả kh&ocirc;ng phải l&uacute;c n&agrave;o cũng tốt, nhưng giữ lại mọi thứ của qu&aacute; khứ lại c&agrave;ng l&agrave; một sai lầm chết người<em>.</em>&nbsp;Hiểu được điều n&agrave;y, Ninja Theory đ&atilde; &ldquo;tặng&rdquo; cho Dante một v&agrave;i m&oacute;n vũ kh&iacute; mới. Đ&oacute; l&agrave; c&acirc;y r&igrave;u Arbiter, tượng trưng cho sức mạnh quỷ dữ v&agrave; Osiris, chiếc lưỡi h&aacute;i tượng trưng cho sức mạnh thi&ecirc;n thần, sức mạnh của mẹ anh, Eva. Trong mỗi m&agrave;n chơi, bạn sẽ phải ứng dụng linh hoạt cả 3 m&oacute;n vũ kh&iacute; cận chiến kể tr&ecirc;n để tạo ra những đ&ograve;n thế đi&ecirc;n cuồng nhất, g&oacute;p phần tăng điểm số sau mỗi m&agrave;n chơi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">B&ecirc;n cạnh những m&oacute;n vũ kh&iacute; kể tr&ecirc;n, trong qu&aacute; tr&igrave;nh &ldquo;dẹp loạn&rdquo;, hay cụ thể hơn l&agrave; những m&agrave;n đấu boss, Dante sẽ được &ldquo;l&ecirc;n đời&rdquo; th&ecirc;m v&agrave;i m&oacute;n vũ kh&iacute;, v&iacute; dụ như nắm đấm th&eacute;p Erix, cặp phi ti&ecirc;u Aquila hay khẩu shotgun Revenant...</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 4\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/DmC-Devil-May-Cry-Screenshot-021-72937.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 4\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Nếu như trong phần 4, người chơi cần sử dụng một đơn vị &ldquo;tiền tệ&rdquo; mang t&ecirc;n Proud Souls để mở kh&oacute;a những chi&ecirc;u thức mới của vũ kh&iacute; cũng như khả năng cho nh&acirc;n vật, th&igrave; ở&nbsp;<em>DmC</em>, c&aacute;ch chơi của bạn sẽ vẫn ảnh hưởng trực tiếp đến số điểm n&acirc;ng cấp. N&oacute;i c&aacute;ch kh&aacute;c, combo c&agrave;ng &quot;đi&ecirc;n&quot;, số điểm sẽ c&agrave;ng cao, v&agrave; bạn sẽ c&oacute; cơ hội mở nhiều combo mới cho mỗi m&oacute;n vũ kh&iacute; hơn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Dante ng&agrave;y n&agrave;o vốn &ldquo;kh&eacute;t tiếng&rdquo; nhờ những phong c&aacute;ch chiến đấu ri&ecirc;ng biệt (Style) như Swordmaster hay Gunslinger. Thế nhưng trong hệ thống chiến đấu mới t&iacute;nh năng n&agrave;y đ&atilde; bị loại bỏ. Thay v&agrave;o đ&oacute; tr&ecirc;n phi&ecirc;n bản Xbox 360, hai c&ograve; tr&aacute;i v&agrave; phải sẽ đảm nhận việc chuyển đổi vũ kh&iacute; theo hướng &ldquo;angel&rdquo; v&agrave; &ldquo;demon&rdquo;. Trong khi đ&oacute; ph&iacute;m D-pad cũng được thiết lập để thay đổi vũ kh&iacute; một c&aacute;ch nhanh nhất, v&igrave; thế việc game thủ đạt được những chuỗi combo cỡ SS hay SSS cũng vẫn phụ thuộc v&agrave;o việc ghi nhớ combo v&agrave; thao t&aacute;c nhanh m&agrave; th&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Một điều mới được Ninja Theory đưa v&agrave;o&nbsp;<em>DmC</em>, đ&oacute; l&agrave; thi thoảng sẽ c&oacute; một v&agrave;i loại qu&aacute;i vật chỉ nhận s&aacute;t thương bởi một trong hai loại vũ kh&iacute; dạng Angel hoặc Demon với 2 m&agrave;u xanh/đỏ tương ứng. Thử th&aacute;ch thực sự sẽ xuất hiện khi cả&nbsp;2 loại n&agrave;y c&ugrave;ng c&oacute; mặt tấn c&ocirc;ng người chơi, đ&ograve;i hỏi bạn kh&eacute;o l&eacute;o &quot;xử&quot; từng con với m&oacute;n vũ kh&iacute; th&iacute;ch hợp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">C&oacute; mới, cũng phải c&oacute; cũ, khả năng &quot;h&oacute;a quỷ&quot; của Dante vẫn c&ograve;n đ&oacute; v&agrave; được k&iacute;ch hoạt bởi hai n&uacute;t analog tr&ecirc;n gamepad. Tuy nhi&ecirc;n việc biến h&igrave;nh của Dante trong phi&ecirc;n bản mới tỏ ra &quot;b&aacute; đạo&quot; hơn rất nhiều, khi k&iacute;ch hoạt, tất cả qu&aacute;i vật sẽ đồng loạt bay l&ecirc;n trời v&agrave; nằm im cho Dante... l&agrave;m cỏ (ngoại trừ tr&ugrave;m). C&ograve;n lại khi trong trạng th&aacute;i Trigger, cả tốc&nbsp;độ,&nbsp;sức s&aacute;t thương của Dante lẫn điểm thưởng sau mỗi chuỗi combo&nbsp;đều sẽ tăng l&ecirc;n đ&aacute;ng kể giống như trước.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 5\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/Screen_002_bmp_jpgcopy-72937.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 5\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Arbiter, m&oacute;n vũ kh&iacute; chậm nhưng cực kỳ lợi hại.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Như đ&atilde; n&oacute;i ở tr&ecirc;n, c&aacute;ch điều khiển mới n&agrave;y gi&uacute;p cho người chơi c&oacute; thể &ldquo;k&eacute;o&rdquo; combo dễ hơn trước rất nhiều. Thế nhưng sự dễ d&agrave;ng n&agrave;y chỉ hiện diện ở những cấp độ kh&oacute; ban đầu như Demon Hunter hay Nephilim. Thử th&aacute;ch thật sự đến khi bạn bắt đầu th&acirc;m nhập v&agrave;o thế giới bị ruồng bỏ Limbo với độ kh&oacute; cỡ Dante Must Die hay Heaven&nbsp;or Hell. Khi đ&oacute; mọi đ&ograve;n đ&aacute;nh sẽ thực sự phải nhuần nhuyễn, kết hợp với khả năng sử dụng x&iacute;ch để k&eacute;o qu&aacute;i một c&aacute;ch ho&agrave;n hảo, nếu như bạn kh&ocirc;ng muốn ngậm ng&ugrave;i nh&igrave;n ch&agrave;ng thợ săn quỷ &ldquo;về th&agrave;nh dưỡng sức&rdquo;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><strong>Gi&aacute; trị chơi lại</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Nếu như trước đ&acirc;y, kh&ocirc;ng &iacute;t người đ&atilde; bỏ ra cả tuần lễ chỉ để đi t&igrave;m những mảnh blue orb tăng m&aacute;u hay ho&agrave;n th&agrave;nh những secret mission th&igrave;&nbsp;<em>DmC</em>&nbsp;sẽ vẫn l&agrave;m cho c&aacute;c game thủ cầu to&agrave;n cảm thấy thỏa m&atilde;n nhờ v&agrave;o hệ thống 25 nhiệm vụ b&iacute; mật đặt rải r&aacute;c trong c&aacute;c m&agrave;n chơi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 6\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/DMC-72937.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 6\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Điều hấp dẫn lại nằm ở chỗ, c&aacute;c m&agrave;n chơi ẩn được đặt sau những c&aacute;nh cửa m&agrave; muốn mở ch&uacute;ng ra, người chơi sẽ phải đi t&igrave;m ch&igrave;a kh&oacute;a. C&oacute; cả thảy 4 loại ch&igrave;a kh&oacute;a mở &ldquo;&ocirc; cửa b&iacute; mật&rdquo; tương ứng, v&agrave; việc đi t&igrave;m những chiếc ch&igrave;a kh&oacute;a n&agrave;y chắc chắn sẽ g&acirc;y ti&ecirc;u tốn kh&ocirc;ng &iacute;t thời gian c&agrave;y k&eacute;o của người chơi. Gi&aacute; trị chơi lại của game c&ograve;n nằm ở hệ thống Lost Souls, những linh hồn v&ocirc; t&igrave;nh lạc v&agrave;o thế giới Limbo. Mỗi m&agrave;n chơi sẽ c&oacute; một số lượng Lost Souls nhất định, v&agrave; việc t&igrave;m ra hết ch&uacute;ng cũng kh&ocirc;ng dễ hơn việc đi t&igrave;m ch&igrave;a kh&oacute;a ở tr&ecirc;n l&agrave; bao.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute; kh&ocirc;ng thể kh&ocirc;ng nhắc tới Bloody Palace - chế độ chơi vốn đ&atilde; gắn liền với series từ những ng&agrave;y đầu ti&ecirc;n (<em>DMC 2</em>).&nbsp;Đ&aacute;ng tiếc l&agrave; n&oacute; kh&ocirc;ng&nbsp;c&oacute; mặt ngay&nbsp;ở trong game gốc m&agrave;&nbsp;sẽ xuất hiện dưới dạng DLC sau khi game ph&aacute;t&nbsp;h&agrave;nh, v&agrave;&nbsp;đến l&uacute;c đ&oacute; chắc chắn&nbsp;c&aacute;c gamer sẽ c&oacute; th&ecirc;m một l&yacute; do&nbsp;để tiếp tục tung ho&agrave;nh trong thế giới của&nbsp;<em>DmC</em>.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><strong>&ldquo;Ch&uacute;ng ta đ&atilde; thức giấc&rdquo;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">V&acirc;ng, đ&oacute; ch&iacute;nh l&agrave; những lời lo&agrave;i người đ&atilde; thốt l&ecirc;n v&agrave;o cuối game, khi Mundus đ&atilde; bị hạ gục. Con người đ&atilde; thức giấc khỏi cơn m&ecirc; đầy u &aacute;m tạo ra bởi lo&agrave;i quỷ dữ. Ở một chừng mực n&agrave;o đ&oacute;, tựa game cũng tạo cho người chơi cảm gi&aacute;c tương tự.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">B&ecirc;n cạnh đồ họa khắc họa sự nổi loạn, th&igrave; &acirc;m nhạc cũng l&agrave; một t&aacute;c nh&acirc;n đem lại &ldquo;lửa&rdquo; cho mỗi m&agrave;n chơi. Nếu như người Nhật trước đ&acirc;y sử dụng nhạc rock metal nặng nề, u &aacute;m v&agrave; đầy sức mạnh l&agrave;m nền cho mỗi trận đấu, th&igrave; người Anh lại khai th&aacute;c một thể loại kh&aacute;c ho&agrave;n to&agrave;n, nhưng cũng kh&ocirc;ng k&eacute;m phần &ldquo;long trọng&rdquo;: Dubstep. Bộ ba DJ đến từ H&agrave; Lan Noisia c&ugrave;ng nh&oacute;m nhạc Na Uy Combichrist đ&atilde; tạo ra một bộ mặt ho&agrave;n to&agrave;n mới cho&nbsp;<em>DmC</em>, nhưng song h&agrave;nh với đ&oacute; l&agrave; việc giữ lại chất &ldquo;đi&ecirc;n cuồng&rdquo; cần c&oacute; cho một tựa game&nbsp;<em>Devil May Cry</em>.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 7\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/dmc-gamescom-screenshot2-600x337-bc404-fe2ac.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 7\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Thế nhưng mặc d&ugrave; thay đổi, người chơi vẫn nhận thấy một điều g&igrave; đ&oacute; kh&ocirc;ng hề lay chuyển b&ecirc;n trong c&aacute;c nh&acirc;n vật của phi&ecirc;n bản mới. Đ&oacute; l&agrave; t&iacute;nh c&aacute;ch bất cần của Dante tr&aacute;i ngược với người anh trai Vergil đầy tham vọng, hay bản chất tham lam v&agrave; t&agrave;n bạo to&aacute;t ra từ con người Mundus. Tất cả vẫn như vậy, chỉ c&oacute; điều ch&uacute;ng được đặt trong một thế giới kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Hai&nbsp;điểm trừ duy nhất của&nbsp;<em>DmC</em>, đ&oacute; l&agrave; trong khi qu&aacute; ch&uacute; trọng v&agrave;o việc chăm ch&uacute;t hệ thống chiến đấu, đội&nbsp;ngũ tại Ninja Theory dường như đ&atilde; bỏ qu&ecirc;n vấn đề thiết kế m&agrave;n chơi. 20 nhiệm vụ của&nbsp;<em>DmC</em>&nbsp;c&oacute; thể n&oacute;i l&agrave; &quot;đều đều&quot; như nhau, v&agrave; hệ quả tất yếu l&agrave; người chơi chỉ c&oacute; một việc, đi một mạch từ đầu ch&iacute; cuối, thi thoảng rẽ ngang để t&igrave;m ch&igrave;a kh&oacute;a mở m&agrave;n chơi ẩn hoặc cứu Lost Souls, v&agrave; quay trở lại với nhiệm vụ ch&iacute;nh. Tương tự như vậy, hệ thống c&acirc;u đố của game cũng cực kỳ sơ s&agrave;i. Sẽ chẳng c&oacute; những c&acirc;u đố &quot;đốt thời gian&quot; của người chơi, hoặc nếu c&oacute; th&igrave; cơ chế của game cũng sẽ hiển thị mọi thứ ra cho bạn nh&igrave;n chứ chẳng cần ch&uacute;t động n&atilde;o như trước.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Mặc d&ugrave; chạy ở tốc độ cố định 30 fps tr&ecirc;n c&aacute;c hệ m&aacute;y console, thế nhưng gameplay của&nbsp;<em>DmC</em>&nbsp;trong mắt người chơi vẫn hết sức trơn tru nhờ v&agrave;o kỹ thuật xử l&yacute; h&igrave;nh ảnh đặc biệt đ&atilde; từng xuất hiện trong một số tựa game trước đ&acirc;y của Capcom. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; nhờ v&agrave;o t&ocirc;ng m&agrave;u của game, người chơi sẽ vẫn thấy chuyển động của Dante cũng như cả thế giới mượt m&agrave; như phi&ecirc;n bản&nbsp;<em>Devil May Cry 4</em>&nbsp;trước đ&acirc;y, vốn chạy ở tốc độ 60 FPS. Tuy nhi&ecirc;n những bạn đọc đang mong chờ phi&ecirc;n bản PC của tr&ograve; chơi n&agrave;y ho&agrave;n to&agrave;n kh&ocirc;ng phải lo lắng g&igrave; v&igrave;&nbsp;<em>DmC</em>&nbsp;sẽ&nbsp;kh&ocirc;ng bị kh&oacute;a FPS như console.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><img alt=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 8\" src=\"http://genk2.vcmedia.vn/DlBlzccccccccccccE5CT3hqq3xN9o/Image/2013/01/dmc_devil_may_cry_screenshot_01-6ff28.jpg\" style=\"width:745px\" title=\"DmC Devil May Cry: &quot;Món ngon&quot; đầu năm 8\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Tựu chung lại,&nbsp;<em>DmC</em>&nbsp;ho&agrave;n to&agrave;n l&agrave; một tựa game đ&aacute;ng chơi chứ<em>&nbsp;</em>kh&ocirc;ng phải l&agrave;<em>&nbsp;</em>thứ &quot;đồ bỏ&quot; như nhiều người vẫn nghĩ.&nbsp;</span></span></p>', '<p>Hệ điều h&agrave;nh: Windows Vista/XP, Windows 7, Windows 8<br />\r\nCPU: AMD Athlon X2 2.8 GHz, Intel Core 2 Duo 2.4 Ghz.</p>\r\n\r\n<p>Bộ nhớ: 2 GB RAM<br />\r\nHDD: 8 GB chỗ trống.</p>\r\n\r\n<p>Card đồ họa: Hỗ trợ DirectX 9.0c trở l&ecirc;n (ATI Radeon HD 3850, NVIDIA GeForce 8800GTS).</p>', '<p>Hệ điều h&agrave;nh: Windows Vista/XP, Windows 7, Windows 8<br />\r\nCPU: AMD Athlon X2 2.8 GHz, Intel Core 2 Duo 2.4 Ghz.</p>\r\n\r\n<p>Bộ nhớ: 2 GB RAM<br />\r\nHDD: 8 GB chỗ trống.</p>\r\n\r\n<p>Card đồ họa: Hỗ trợ DirectX 9.0c trở l&ecirc;n (ATI Radeon HD 3850, NVIDIA GeForce 8800GTS).</p>', 'Cài đặt và chơi', '1521044979image.jpg', 'https://www.youtube.com/embed/jzQPZUMqd_0', 2012, '2018-03-14');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(14, 5, 'Age of Empires: Definitive Edition', 'age-of-empires:-definitive-edition', '<h3 style=\"text-align:justify\">20 năm sau khi Age of Empires, tựa game RTS kinh điển chinh phục v&ocirc; số game thủ v&agrave; mở đường cho chiến thắng &ldquo;ngời ngời tuyệt vời&rdquo; của Age of Empires 2, Microsoft quyết định trao cho Forgotten Empires trọng tr&aacute;ch l&agrave;m lại AOE với t&ecirc;n gọi mới Age of Empires: Definitive Edition (AOEDE).</h3>\r\n\r\n<h2>&nbsp;</h2>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">D&ugrave; c&oacute; một ch&uacute;t vấp v&aacute;p khi tr&ograve; chơi phải ho&atilde;n ng&agrave;y ra mắt sang năm 2018 thay v&igrave; được tung ra trong năm 2017, đ&uacute;ng dịp kỷ niệm 20 năm AOE, n&oacute; vẫn khiến game thủ y&ecirc;u th&iacute;ch RTS tr&ecirc;n khắp thế giới mỏi mắt tr&ocirc;ng chờ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Bộ quần &aacute;o mới&hellip;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Với c&aacute;i gi&aacute; 20 USD tr&ecirc;n Microsoft Store, Microsoft cho thấy r&otilde; rằng đ&acirc;y l&agrave; một phi&ecirc;n bản l&agrave;m lại, chủ yếu l&agrave; n&acirc;ng cấp về mặt h&igrave;nh ảnh để đ&aacute;p ứng nhu cầu của những nh&agrave; cầm qu&acirc;n tr&ecirc;n m&agrave;n h&igrave;nh 4K, với số pixel to gấp v&agrave;i chục lần những m&agrave;n h&igrave;nh của năm 1997. V&agrave; phải n&oacute;i rằng nh&agrave; ph&aacute;t triển FE đ&atilde; l&agrave;m rất tốt phần việc của m&igrave;nh, khi n&oacute; c&oacute; thể khiến nhiều người nghĩ rằng tr&ograve; chơi đ&atilde; lột x&aacute;c l&ecirc;n 100% 3D, chỉ trừ địa h&igrave;nh của game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Với những cải tiến vượt trội trong lối chơi thế giới mở v&agrave; chế tạo, My Time at Portia xứng đ&aacute;ng được điểm cao mặc d&ugrave; chỉ mới Early Access.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Họ đ&atilde; l&agrave;m điều đ&oacute; như thế n&agrave;o? Bằng hai &ldquo;độc chi&ecirc;u&rdquo; m&agrave; phải rất tinh &yacute;, bạn mới nhận ra được. Đầu ti&ecirc;n, c&aacute;c đơn vị qu&acirc;n trong AOE gốc l&agrave; những vật thể 2D được dựng h&igrave;nh ở 8 g&oacute;c độ kh&aacute;c nhau, v&agrave; tr&ograve; chơi sẽ chuyển đổi giữa c&aacute;c g&oacute;c độ n&agrave;y khi đơn vị đ&oacute; di chuyển. Trong Age of Empires: Definitive Edition, mỗi đơn vị qu&acirc;n được&nbsp; dựng h&igrave;nh ở&hellip; 32 g&oacute;c kh&aacute;c nhau, gi&uacute;p ch&uacute;ng di chuyển mượt m&agrave; hơn hẳn, x&oacute;a bỏ hiện tượng &ldquo;nh&igrave;n một đằng, đi một nẻo&rdquo; m&agrave; bạn c&oacute; thể dễ d&agrave;ng nhận thấy trong game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Age of Empires: Definitive Edition\" src=\"https://media.motgame.vn/2018/02/monster_vnsf/aoe-definitive-edition-2.jpg\" style=\"height:413px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>C&aacute;c đơn vị qu&acirc;n được dựng h&igrave;nh từ nhiều g&oacute;c độ hơn.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ecirc;m v&agrave;o đ&oacute;, n&oacute; khiến t&aacute;c giả c&oacute; cảm tưởng như đang nh&igrave;n một đơn vị qu&acirc;n 3D thực thụ tr&ecirc;n m&agrave;n h&igrave;nh. Với sự c&ocirc;ng phu n&agrave;y, Forgotten Empires tiết lộ rằng chỉ ri&ecirc;ng file cho con t&agrave;u Trireme trong game cũng đ&atilde; to hơn cả tr&ograve; chơi gốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chi&ecirc;u thứ hai m&agrave; nh&agrave; ph&aacute;t triển sử dụng l&agrave; những chuyển động (animation) sụp đổ của c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng. Thay v&igrave; lập tức x&oacute;a c&ocirc;ng tr&igrave;nh đ&oacute; v&agrave; thay thế bằng một đống đổ n&aacute;t, b&acirc;y giờ c&aacute;c t&ograve;a nh&agrave; sẽ sụp đổ trong một đ&aacute;m kh&oacute;i bụi mịt m&ugrave;:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img src=\"https://media.giphy.com/media/YFDlA9DkNx8GdN0txS/giphy.gif\" style=\"height:466px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Xem m&atilde;i kh&ocirc;ng ch&aacute;n.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Quyết định &ldquo;n&acirc;ng cấp&rdquo; c&aacute;c vụ&hellip; sập nh&agrave; của Forgotten Empires c&oacute; lẽ l&agrave; ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c. Ch&uacute;ng ta kh&ocirc;ng phải c&aacute;c gosu, khi thắng bại được ph&acirc;n định chỉ bằng c&aacute;i chết của v&agrave;i đơn vị l&iacute;nh, n&ecirc;n đ&acirc;y l&agrave; điều bạn sẽ nh&igrave;n thấy h&agrave;ng chục, hoặc h&agrave;ng trăm lần khi chơi. N&oacute; c&oacute; thể th&ecirc;m một ch&uacute;t &ldquo;kho&aacute;i cảm&rdquo; khi bạn đ&egrave; bẹp kẻ th&ugrave;, v&agrave; c&oacute; thể l&agrave; một ch&uacute;t an ủi khi kỳ quan bạn bỏ c&ocirc;ng x&acirc;y dựng đổ ập (t&aacute;c giả rất muốn d&ugrave;ng meme &ldquo;khối đ&aacute; 90 kg được n&eacute;m từ khoảng c&aacute;ch 300m&rdquo; ở đ&acirc;y, nhưng thật đ&aacute;ng tiếc&hellip;).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ecirc;n cạnh sự h&agrave;o nho&aacute;ng của độ ph&acirc;n giải 4K v&agrave; những đơn vị qu&acirc;n được vẽ lại hết sức c&ocirc;ng phu, phần &acirc;m thanh của Age of Empires: Definitive Edition ho&agrave;n to&agrave;n xứng đ&aacute;ng để bạn bỏ thời gian thưởng thức. Những b&agrave;i nhạc giao hưởng tr&agrave;o d&acirc;ng được sử dụng tạo ra cảm gi&aacute;c về những đế chế huy ho&agrave;ng m&agrave; bạn đang gầy dựng trong game. Lời n&oacute;i kh&ocirc;ng thể n&agrave;o m&ocirc; tả được &acirc;m thanh, nhưng nếu đ&atilde; click v&agrave;o n&uacute;t Play như Mọt game đề nghị, những giai điệu bạn đang nghe ch&iacute;nh l&agrave; một phần của những g&igrave; Forgotten Empires đưa v&agrave;o &ldquo;bộ quần &aacute;o mới&rdquo; của Age of Empires.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bị thuyết phục bởi những nốt nhạc n&agrave;y, bạn c&oacute; thể mua to&agrave;n bộ 22 soundtrack của game <a href=\"https://www.amazon.com/Age-Empires-Definitive-Original-Soundtrack/dp/B074MZ564K\" target=\"_blank\"><strong>tại đ&acirc;y</strong></a>.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Nh&agrave; vua</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ecirc;n dưới lớp vỏ ngo&agrave;i h&agrave;o nho&aacute;ng v&agrave; l&oacute;ng l&aacute;nh, Age of Empires: Definitive Edition gần như kh&ocirc;ng c&oacute; g&igrave; thay đổi. Bạn sẽ vẫn t&igrave;m thấy sự h&agrave;o hứng của những cuộc đua với thời gian d&ugrave; chơi một m&igrave;nh, qua LAN hoặc online. Khởi đầu với một TC, ba d&acirc;n l&agrave;ng v&agrave; trở th&agrave;nh một đế quốc trải rộng to&agrave;n cầu (OK, kh&ocirc;ng phải to&agrave;n cầu, chỉ l&agrave; một bản đồ b&eacute; tẹo), hay bị nghiền n&aacute;t dưới g&oacute;t sắt của những đế chế h&ugrave;ng mạnh hơn, tiến bộ hơn ho&agrave;n to&agrave;n phụ thuộc v&agrave;o &oacute;c chiến thuật, khả năng do th&aacute;m v&agrave; số APM của bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Với một game thủ b&igrave;nh thường chỉ muốn c&oacute; những trận chiến ho&agrave;nh tr&aacute;ng giữa h&agrave;ng trăm binh sĩ c&ugrave;ng một l&uacute;c, AOEDE đem lại một giới hạn qu&acirc;n mới cao hơn hẳn trong c&aacute;c trận đ&aacute;nh Custom Map. Trong khi đ&oacute;, unit cap trong phần chơi chiến dịch dừng lại ở mức 50, n&ecirc;n bạn sẽ kh&ocirc;ng c&oacute; những qu&acirc;n đo&agrave;n khổng lồ trong mục chơi n&agrave;y. C&aacute;c đơn vị qu&acirc;n vẫn kh&ocirc;ng thực sự đa dạng, v&agrave; đ&ocirc;i khi tạo ra những chuyện lạ l&ugrave;ng như Ai Cập c&oacute;&hellip; Phalanx của La M&atilde;, v&acirc;n v&acirc;n v&agrave; m&acirc;y m&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Age of Empires: Definitive Edition\" src=\"https://media.motgame.vn/2018/02/monster_vnsf/aoe-definitive-edition-4.jpg\" style=\"height:371px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Một cảnh trong phần hướng dẫn Sự trỗi dậy của Ai Cập.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ugrave; lại, bạn sẽ thưởng thức những chiến dịch mới, cực d&agrave;i v&agrave; được thiết kế theo c&aacute;c sự kiện lịch sử. Đập ph&aacute; v&agrave;i th&aacute;p canh v&agrave; chiếm một h&ograve;n đảo? Đ&oacute; l&agrave; bạn, pharaoh Senusret III đang x&acirc;y dựng th&agrave;nh tr&igrave; để chinh phục Hạ Nubia. &ldquo;Đẻ&rdquo; v&agrave;i n&ocirc;ng d&acirc;n v&agrave; ra lệnh cho họ l&agrave;m ruộng? Pharaoh Narmer vĩ đại đang t&igrave;m c&aacute;ch thống nhất Thượng v&agrave; Hạ Ai Cập bằng sự dồi d&agrave;o từ nền n&ocirc;ng nghiệp ở đ&ocirc;i bờ s&ocirc;ng Nile.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một điểm cộng của Age of Empires: Definitive Edition l&agrave; ở việc tốc độ của game đ&atilde; được điều chỉnh để ph&ugrave; hợp hơn với 2018. D&ugrave; chưa hề chạm tay v&agrave;o AOE trong 18 năm qua, Mọt game vẫn cảm thấy tốc độ l&agrave;m việc, di chuyển của c&aacute;c đơn vị trong game nhanh hơn hẳn, gi&uacute;p tăng nhịp độ của tr&ograve; chơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&Agrave;, tất cả c&aacute;c cheatcode cũ cũng vẫn hoạt động b&igrave;nh thường nữa đấy!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Age of Empires: Definitive Edition\" src=\"https://media.motgame.vn/2018/02/monster_vnsf/aoe-definitive-edition-5.jpg\" style=\"height:449px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Kh&ocirc;ng c&oacute; g&igrave; lạ ở đ&acirc;y cả.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; một c&acirc;u n&oacute;i rất hay: c&aacute;i g&igrave; kh&ocirc;ng hư th&igrave; đừng sửa. Forgotten Empires đ&atilde; theo đ&uacute;ng lời khuy&ecirc;n n&agrave;y khi l&agrave;m n&ecirc;n AOEDE, v&agrave; v&igrave; thế ch&uacute;ng ta được gặp lại tất cả những điều l&agrave;m n&ecirc;n sức hấp dẫn của n&oacute; m&agrave; t&aacute;c giả vừa nhắc đến. Nhưng thật đ&aacute;ng tiếc l&agrave; họ lại bỏ qu&ecirc;n c&ocirc;ng đoạn &ldquo;lọc sạn&rdquo; những g&igrave; vốn c&oacute; trong game. V&igrave; thế, Age of Empires: Definitive Edition giữ nguy&ecirc;n cả những khuyết điểm của tựa game gốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầu ti&ecirc;n, những ai từng &ldquo;chiến&rdquo; AOE chắc chắn kh&ocirc;ng dưới chục lần&hellip; s&ocirc;i m&aacute;u v&igrave; mức độ ngu xuẩn của những đơn vị l&iacute;nh trong game ng&agrave;y xưa, v&agrave; phi&ecirc;n bản mới lại tiếp tục thử độ bền mạch m&aacute;u của bạn. Ngay cả với hệ thống t&igrave;m đường được quảng c&aacute;o l&agrave; đ&atilde; n&acirc;ng cấp, c&aacute;c đơn vị qu&acirc;n vẫn c&oacute; những h&agrave;nh vi kh&ocirc;ng thể chấp nhận được. G&atilde; d&acirc;n l&agrave;ng ốm đ&oacute;i đ&ocirc;i khi sẽ tưởng m&igrave;nh l&agrave; The Hulk v&agrave; cố gắng t&ocirc;ng ng&atilde; một c&aacute;i c&acirc;y, thay v&igrave; đi v&ograve;ng quanh n&oacute;. Khi sau lưng hắn ta l&agrave; một con sư tử đang rất muốn b&aacute;o &ldquo;nhất tiễn chi cừu&rdquo;, điều n&agrave;y chắc chắn kh&ocirc;ng tốt cho huyết &aacute;p của ch&uacute;ng ta.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Age of Empires: Definitive Edition\" src=\"https://media.motgame.vn/2018/02/monster_vnsf/aoe-definitive-edition-6.jpg\" style=\"height:498px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Ở mức zoom cận cảnh, bạn c&oacute; thể nh&igrave;n r&otilde; chuyển động của c&aacute;c đơn vị.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chưa hết. Hitbox của game thỉnh thoảng tỏ ra lệch lạc, khiến việc click v&agrave;o đơn vị qu&acirc;n hay c&aacute;i c&acirc;y bạn muốn trở n&ecirc;n kh&oacute; khăn &ndash; bạn c&oacute; thể muốn dọn một c&aacute;i c&acirc;y cản đường, nhưng click nhầm v&agrave;o mỏ sẽ khiến cả nh&oacute;m c&ocirc;ng nh&acirc;n vứt đống gỗ trong người để chuyển sang đ&agrave;o mỏ &ndash; một điều cực kỳ đ&aacute;ng tiếc bởi bạn cần từng điểm t&agrave;i nguy&ecirc;n một trong giai đoạn đầu game. Việc điều khiển bản đồ cũng kh&aacute; hỗn loạn: n&uacute;t chuột phải được d&ugrave;ng để k&eacute;o bản đồ, nhưng nếu bạn đang lựa chọn một đơn vị l&iacute;nh, chuột phải sẽ ra lệnh cho đơn vị đ&oacute; đi đến vị tr&iacute; m&agrave; bạn click thay v&igrave; di chuyển bản đồ của game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh năng zoom của game cũng cần phải được cải thiện. Khi sử dụng n&uacute;t cuộn để zoom, game kh&ocirc;ng thay đổi giữa c&aacute;c mức zoom (1x, 2x, 4x) m&agrave; chỉ tăng k&iacute;ch thước của h&igrave;nh ảnh tr&ecirc;n m&agrave;n h&igrave;nh, khiến mọi thứ tr&ocirc;ng mờ đi cho đến khi c&aacute;c chi tiết h&igrave;nh ảnh thực sự được load xong. Bạn c&oacute; thể so s&aacute;nh b&ecirc;n dưới:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://media.motgame.vn/2018/02/Bookgrinder/zoom.jpg\" style=\"height:466px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Độ chi tiết trước khi load (tr&aacute;i), sau khi load (phải), v&agrave; phản hồi của nh&agrave; ph&aacute;t triển.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; ph&aacute;t triển cho biết tốc độ hiển thị của chi tiết sau mỗi lần chuyển đổi mức zoom l&agrave; t&ugrave;y thuộc v&agrave;o tốc độ của ổ cứng v&agrave; dung lượng RAM. Điều n&agrave;y kh&ocirc;ng qu&aacute; quan trọng về mặt lối chơi, nhưng kh&aacute; &ldquo;ngứa mắt&rdquo; nếu bạn l&agrave; một game thủ muốn tận hưởng chất lượng đồ họa tối đa. V&igrave; thế, nh&agrave; ph&aacute;t triển n&oacute;i rằng họ cũng đang t&igrave;m c&aacute;ch để khắc phục vấn đề n&agrave;y trong tương lai.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave; cũng như mọi tr&ograve; chơi đ&ograve;i hỏi đường truyền internet kh&aacute;c, AOEDE bị ch&igrave;m ngập trong đủ thứ lỗi khi bạn cần đến bất kỳ th&agrave;nh phần online n&agrave;o của n&oacute;. Do ng&agrave;y xưa chỉ được chơi AOE phi&ecirc;n bản lậu, t&aacute;c giả muốn được thưởng thức AOE &ldquo;xịn, bản quyền&rdquo; khi bỏ ra 20 USD (hoặc 115.000 đồng nếu l&agrave; store Việt Nam) cho tựa game n&agrave;y, nhưng h&oacute;a ra n&oacute; chẳng kh&aacute;c g&igrave; với phi&ecirc;n bản lậu ng&agrave;y xưa bởi những rắc rối của server. Lỗi kết nối xảy ra như cơm bữa khi chơi multiplayer, v&agrave; t&aacute;c giả chưa thể ho&agrave;n tất một trận đấu online n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Age of Empires: Definitive Edition\" src=\"https://media.motgame.vn/2018/02/monster_vnsf/aoe-definitive-edition-8.jpg\" style=\"height:307px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>LAN, vị cứu tinh nếu bạn muốn chơi c&ugrave;ng bạn b&egrave;.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể n&oacute;i rằng ch&iacute;nh Age of Empires: Definitive Edition đ&atilde; nhắc nhở tại sao Mọt game kh&ocirc;ng th&iacute;ch c&aacute;c cửa h&agrave;ng ứng dụng v&agrave; ngại ngần khi n&acirc;ng cấp l&ecirc;n Windows 10. Tất cả những vấn đề kết nối n&agrave;y đ&atilde; tồn tại từ c&aacute;c bản beta trước đ&acirc;y, v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng được Forgotten Empires khắc phục d&ugrave; game đ&atilde; ch&iacute;nh thức ra mắt. Thật ra, nếu Age of Empires: Definitive Edition kh&ocirc;ng bị tr&oacute;i buộc v&agrave;o khu&ocirc;n khổ Windows 10 v&agrave; được ph&aacute;t h&agrave;nh tr&ecirc;n Steam, game thủ sẽ tho&aacute;t khỏi những vấn đề n&agrave;y ngay lập tức.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Đấng minh qu&acirc;n?</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn muốn chơi Đế Chế, lời khuy&ecirc;n của Mọt game l&agrave; h&atilde;y bỏ tiền cho <strong>Age of Empires II HD</strong>. Đ&oacute; l&agrave; một tựa game vượt trội hơn hẳn, l&agrave; đỉnh cao của cả series &ndash; kh&ocirc;ng phải ngẫu nhi&ecirc;n m&agrave; AOE II được l&agrave;m lại trước AOE I. AOE II HD c&oacute; tất cả những g&igrave; bạn muốn ở Đế Chế, từ h&igrave;nh ảnh đẹp mắt, t&iacute;nh chiến thuật s&acirc;u sắc, c&aacute;c đơn vị qu&acirc;n đa dạng, phần chơi mạng hoạt động 100% v&agrave; quan trọng nhất l&agrave; n&oacute; cho ph&eacute;p bạn&hellip; x&acirc;y cổng trong game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Age of Empires: Definitive Edition\" src=\"https://media.motgame.vn/2018/02/monster_vnsf/aoe-definitive-edition-9.jpg\" style=\"height:581px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Age of Empires II HD &gt; Age of Empires: Definitive Edition, nuff said.</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhưng nếu bạn nhất quyết phải chơi AOE I, v&igrave; ho&agrave;i cổ, v&igrave; đồ họa xịn 4K, v&igrave; c&aacute;c chiến dịch chơi đơn hay v&igrave; bạn chưa chơi qua phi&ecirc;n bản gốc? T&aacute;c giả cũng xin x&aacute;c nhận rằng Definitive Edition thực sự l&agrave; một phi&ecirc;n bản đ&aacute;ng đồng tiền.</span></p>', '<p>Hệ điều h&agrave;nh: Windows 10</p>\r\n\r\n<p>CPU:&nbsp;1.8 Ghz+ Dual Core hoặc Core I5 trở l&ecirc;n</p>\r\n\r\n<p>VGA: Intel HD 4000 trở l&ecirc;n</p>\r\n\r\n<p>Bộ nhớ: 4GB RAM</p>\r\n\r\n<p>DirectX: Version 11</p>', '<p>Hệ điều h&agrave;nh: Windows 10</p>\r\n\r\n<p>CPU:&nbsp;1.8 Ghz+ Dual Core hoặc Core I5 trở l&ecirc;n</p>\r\n\r\n<p>VGA: Intel HD 4000 trở l&ecirc;n</p>\r\n\r\n<p>Bộ nhớ: 4GB RAM</p>\r\n\r\n<p>DirectX: Version 11</p>', 'Cài đặt và chơi', '1521045603aoe-hero-hero-1.jpg', 'https://www.youtube.com/embed/JOEMc0pJHY8', 2018, '2018-03-14'),
(15, 6, 'Final Fantasy XV', 'final-fantasy-xv', '<h3 style=\"text-align:justify\">Việc x&acirc;y dựng theo thế giới mở, cho ph&eacute;p người chơi thoải m&aacute;i di chuyển, kh&aacute;m ph&aacute; bản đồ khiến cho Final Fantasy XV được đ&aacute;nh gi&aacute; cực cao so với c&aacute;c tựa game trước đ&oacute;, v&agrave; đ&acirc;y cũng ch&iacute;nh l&agrave; điều tuyệt vời nhất trong Final Fantasy XV.</h3>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&agrave; một fan h&acirc;m mộ d&ograve;ng game Final Fantasy, chắc chắn, c&aacute;c bạn sẽ cảm thấy tuyệt đối h&agrave;i l&ograve;ng với&nbsp;<strong>Final Fantasy XV</strong>&nbsp;- Một trong những tựa game hay nhất từ trước đến nay trong cả series.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá chi tiết Final Fantasy XV dưới góc nhìn một fan hâm mộ Việt Nam\" id=\"img_31aff4c0-bd05-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/31-1481174061606.jpg\" style=\"height:423px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầu ti&ecirc;n, cần phải khẳng định rằng t&ocirc;i đ&atilde; sau khi chơi hết nội dung cốt truyện Final Fantasy XV, t&ocirc;i đ&atilde; c&oacute; 30 tiếng trải nghiệm thực sự tuyệt vời, v&agrave; chắc chắn sẽ kh&ocirc;ng nối tiếc bất cứ một ph&uacute;t n&agrave;o m&agrave; m&igrave;nh đ&atilde; bỏ ra trong game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc x&acirc;y dựng theo thế giới mở, cho ph&eacute;p người chơi thoải m&aacute;i di chuyển, kh&aacute;m ph&aacute; bản đồ khiến cho Final Fantasy XV được đ&aacute;nh gi&aacute; cực cao so với c&aacute;c tựa game trước đ&oacute;, v&agrave; đ&acirc;y cũng ch&iacute;nh l&agrave; điều tuyệt vời nhất trong Final Fantasy XV.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thật vậy, Final Fatantasy XV giới thiệu đến người chơi một thế giới tuyệt đẹp, đẹp đến mức m&agrave; người chơi c&oacute; thể cảm thấy như ch&iacute;nh m&igrave;nh đang được đi du lịch vậy, chứ kh&ocirc;ng chỉ l&agrave; đơn giản như trước chơi game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Không chỉ là chơi game, Final Fantasy XV còn khiến bạn cảm thấy như mình đang được di du lịch\" id=\"img_302fcf50-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/22-1481173206299.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng chỉ l&agrave; chơi game, Final Fantasy XV c&ograve;n khiến bạn cảm thấy như m&igrave;nh đang được di du lịch</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thế giới tuyệt đẹp trong Final Fantasy XV khiến cho bạn cảm thấy &quot;phải l&ograve;ng&quot; tựa game n&agrave;y ngay trong lần đầu ti&ecirc;n v&agrave;o chơi, n&oacute; kh&ocirc;ng giống như một tựa game, n&oacute; l&agrave;m bạn cảm thấy như m&igrave;nh đang xem phim th&igrave; đ&uacute;ng hơn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể tự do chạy tr&ecirc;n c&aacute;c hoang mạc rộng lớn, trải d&agrave;i kh&ocirc;ng c&oacute; điểm cuối, hay ngắm nh&igrave;n cảnh biển tuyệt đẹp từ tr&ecirc;n cao ở tr&ecirc;n c&aacute;c con đ&egrave;o, hay những th&agrave;nh phố cổ được thiết kế y hệt như c&aacute;c th&agrave;nh phố Ch&acirc;u &Acirc;u ngo&agrave;i đời thật.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Một thành phố giống hệt như Vienne của Áo trong Final Fantasy XV\" id=\"img_b21e4aa0-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/28-1481173423384.png\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một th&agrave;nh phố giống hệt như Vienne của &Aacute;o trong Final Fantasy XV</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Yếu tố kh&aacute;m ph&aacute; được ưu ti&ecirc;n khiến người chơi cảm thấy h&agrave;o hứng v&agrave; th&uacute; vị hơn rất nhiều so với c&aacute;c trải nghiệm ban đầu. Bản đồ trong game rộng v&agrave; kh&oacute; c&oacute; thể t&igrave;m thấy điểm cuối, v&agrave; khi di chuyển tại đ&acirc;y, bạn c&oacute; thể bắt gặp những con qu&aacute;i vật khổng lồ, cực mạnh v&agrave; c&oacute; t&iacute;nh thử th&aacute;ch rất cao. Kh&ocirc;ng chỉ c&oacute; vậy, h&agrave;ng loạt nhiệm vụ ẩn v&agrave; c&aacute;c phụ bản trong game nằm rải r&aacute;c khắp tr&ecirc;n bản đồ v&agrave; bạn sẽ phải tự m&igrave;nh kh&aacute;m ph&aacute; ra ch&uacute;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng chỉ c&oacute; vậy, d&ugrave; đ&acirc;y l&agrave; lần thứ 2 yếu tố thời gian được đưa v&agrave;o trong một tựa game Final Fantasy (trước Final Fantasy XV l&agrave; Lightning Returns: Final Fantasy XIII), nhưng điều n&agrave;y lại khiến cho trải nghiệm Final Fantasy XV trở n&ecirc;n th&uacute; vị hơn rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Vào ban đêm, bạn có thể sẽ phải chạm trán quái vật mạnh ngoài bản đồ\" id=\"img_e7210030-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/29-1481173513230.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave;o ban đ&ecirc;m, bạn c&oacute; thể sẽ phải chạm tr&aacute;n qu&aacute;i vật mạnh ngo&agrave;i bản đồ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn sẽ cần phải cố gắng ho&agrave;n th&agrave;nh c&aacute;c nhiệm vụ v&agrave;o buổi s&aacute;ng để tiết kiệm thời gian, trong khi buổi tối việc di chuyển ngo&agrave;i bản đồ sẽ trở n&ecirc;n kh&oacute; khăn v&agrave; nguy hiểm hơn rất nhiều. Điều n&agrave;y kh&ocirc;ng chỉ đến từ việc trời tối rất kh&oacute; nh&igrave;n đường, m&agrave; c&ograve;n ở cả việc người chơi sẽ phải tự m&igrave;nh điều khiển chiếc xe hơi m&agrave; kh&ocirc;ng thể tự đặt chế độ Auto, ngo&agrave;i ra, c&aacute;c loại qu&aacute;i vật mạnh cũng sẽ xuất hiện nhiều hơn v&agrave;o buổi tối. V&agrave; nếu bị đ&aacute;nh bại, c&aacute;c</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hệ thống chiến đấu c&oacute; thể xem l&agrave; một trong những điểm kh&aacute;c biệt v&agrave; khiến fan h&acirc;m mộ cảm thấy &quot;ngỡ ng&agrave;ng&quot; nhất trong Final Fantasy XV, khi đ&atilde; ph&aacute; bỏ lối đ&aacute;nh theo lượt truyền thống sang cơ chế chiến đấu h&agrave;nh động.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Cơ chế chiến đấu trong Final Fantasy XV mang đậm chất hành động, nhưng lại quá dễ\" id=\"img_66f3e3f0-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/24-1481173296525.png\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cơ chế chiến đấu trong Final Fantasy XV mang đậm chất h&agrave;nh động, nhưng lại qu&aacute; dễ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&ecirc;n thực tế, mặc d&ugrave; cảm gi&aacute;c chơi c&oacute; phần s&ocirc;i động với tiết tấu nhanh hơn, nhưng do hướng đến nội dung &quot;to&agrave;n cầu h&oacute;a&quot;, hướng đến những đối tượng người chơi mới, n&ecirc;n trận đấu trong Final Fantasy XV trở n&ecirc;n dễ d&agrave;ng hơn rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngay từ ban đầu hay thậm ch&iacute; trong nửa cốt truyện đầu, việc đ&aacute;nh qu&aacute;i, hay thậm ch&iacute; đ&aacute;nh Boss chẳng tạo n&ecirc;n bất cứ cảm gi&aacute;c kh&oacute; khăn n&agrave;o cho người chơi. Người chơi khi điều khiển nh&acirc;n vật đa phần chỉ cần ấn n&uacute;t tấn c&ocirc;ng, đồng thời chuyển đổi sang c&aacute;c loại vũ kh&iacute; kh&aacute;c nhau để g&acirc;y th&ecirc;m nhiều s&aacute;t thương hơn m&agrave; th&ocirc;i, chứ chẳng cần c&oacute; bất cứ nỗ lực hay t&iacute;nh to&aacute;n g&igrave; cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá chi tiết Final Fantasy XV dưới góc nhìn một fan hâm mộ Việt Nam\" id=\"img_63810ee0-bd04-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/30-1481173722169.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hệ thống tự định hướng đ&ograve;n đ&aacute;nh cũng khiến cho cơ chế chiến đấu trong Final Fantasy XV trở n&ecirc;n đơn giản hơn rất nhiều. Đối với c&aacute;c fan h&acirc;m mộ, c&aacute;c đ&ograve;n đ&aacute;nh theo dạng Blitz (đ&aacute;nh nhiều mục ti&ecirc;u) hay việc sử dụng Magic để tấn c&ocirc;ng đối thủ vốn đ&atilde; l&agrave; một điều quen thuộc trong c&aacute;c tựa game Final Fantasy trước đ&oacute;, th&igrave; gần như bị bỏ qu&ecirc;n trong Final Fantasy XV lần n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Điều đ&aacute;ng n&oacute;i hơn rằng lối chơi trong Final Fantasy XV qu&aacute; dễ, trong trận đấu bạn gần như chỉ cần ấn n&uacute;t attack m&agrave; th&ocirc;i. Điều n&agrave;y c&oacute; thể khiến cho c&aacute;c fan h&acirc;m mộ Final Fantasy trước kia cảm thấy đ&ocirc;i ch&uacute;t thất vọng, khi lối chơi trong game trở n&ecirc;n qu&aacute; mức đơn giản v&agrave; c&oacute; phần casual.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá chi tiết Final Fantasy XV dưới góc nhìn một fan hâm mộ Việt Nam\" id=\"img_67148b50-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/26-1481173296526.png\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Như ch&uacute;ng ta đều đ&atilde; biết, cốt truyện của Final Fantasy XV xoay quanh nh&acirc;n vật ch&iacute;nh Noctis, ch&agrave;ng ho&agrave;ng tử của vương quốc Lucis. Cuộc h&agrave;nh tr&igrave;nh của Lucis trong thế giới Final Fantasy XV diễn ra k&eacute;o d&agrave;i tới 10 năm, trước khi anh ch&agrave;ng n&agrave;y c&oacute; đủ sức mạnh để quay về gi&agrave;nh lại ngai v&agrave;ng của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&igrave; x&acirc;y dựng trong thế giới mở, n&ecirc;n c&aacute;ch dẫn truyện trong Final Fantasy XV c&ugrave;ng ho&agrave;n to&agrave;n mở, khi cho ph&eacute;p người chơi tự lựa chọn mục ti&ecirc;u của m&igrave;nh, chứ kh&ocirc;ng bắt &eacute;p người chơi đi theo một hướng nhất định như trong c&aacute;c tựa game Final Fantasy kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng chỉ được tự quyết định mạch truyện, người chơi cũng c&oacute; thể lựa chọn c&aacute;c quyết định kh&aacute;c nhau trong từng t&igrave;nh huống nhất định, như lựa chọn c&oacute; gi&uacute;p đỡ c&aacute;c NPC giải quyết vấn đề c&aacute; nh&acirc;n của họ hay kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá chi tiết Final Fantasy XV dưới góc nhìn một fan hâm mộ Việt Nam\" id=\"img_b1e7aa40-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/thumb_w/690/2016/27-1481173423383.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều lựa chọn v&agrave; nội dung chơi mới được đưa v&agrave;o Final Fantasy XV gi&uacute;p cho người chơi c&oacute; được cảm gi&aacute;c trải nghiệm Final Fantasy XV th&uacute; vị hơn, v&agrave; khiến ch&uacute;ng ta tạm thời qu&ecirc;n đi cơ chế chiến đấu c&oacute; phần hơi nh&agrave;m ch&aacute;n trong game.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Để chơi hết cốt truyện ch&iacute;nh, người chơi sẽ chỉ cần ti&ecirc;u tốn hết khoảng 30 tiếng m&agrave; th&ocirc;i. Tuy nhi&ecirc;n, sau khi ho&agrave;n th&agrave;nh cốt truyện th&igrave; vẫn sẽ c&ograve;n rất nhiều nội dung kh&aacute;c để người chơi kh&aacute;m ph&aacute;. Hay thậm ch&iacute;, bắt đầu một New Game + kh&aacute;c cũng khiến cho cảm gi&aacute;c trải nghiệm Final Fantasy XV trở n&ecirc;n hấp dẫn v&agrave; th&uacute; vị hơn rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Đánh giá chi tiết Final Fantasy XV dưới góc nhìn một fan hâm mộ Việt Nam\" id=\"img_30814ab0-bd03-11e6-8745-47b7c39f5a4b\" src=\"https://genknews.genkcdn.vn/2016/23-1481173206300.png\" style=\"height:421px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Theo ước t&iacute;nh, Final Fantasy XV c&oacute; thể được xem l&agrave; tựa game &quot;g&acirc;y nghiện&quot; nhất trong cả series Final Fantasy, bạn sẽ kh&ocirc;ng chỉ chơi để rồi &quot;ph&aacute; đảo&quot;, m&agrave; sẽ gần như cảm thấy &quot;nghiện&quot; tựa game n&agrave;y, một cảm gi&aacute;c giống như kiểu chỉ cần được rảnh rỗi, bạn sẽ ngay lập tức bật m&aacute;y l&ecirc;n để v&agrave;o chơi Final Fantasy XV vậy.</span></p>', '<p>Hệ điều h&agrave;nh: Windows 10</p>\r\n\r\n<p>CPU: Intel Core i5 2400 hoặc AMD FX 6100</p>\r\n\r\n<p>Ram: 8 GB</p>\r\n\r\n<p>Card đồ họa: GeForce GTX 760</p>', '<p>Hệ điều h&agrave;nh: Windows 10</p>\r\n\r\n<p>CPU: Intel Core i7 3770 hoặc AMD FX 8350</p>\r\n\r\n<p>Ram: 16 GB</p>\r\n\r\n<p>Card đồ họa: GeForce GTX 1060</p>', 'Cài đặt và chơi', '1521046325thumb-1920-834171.jpg', 'https://www.youtube.com/embed/IiI7SMQA59Q', 2017, '2018-03-14'),
(16, 6, 'Fallout 4', 'fallout-4', '<h3><strong>Tựa game n&agrave;y đang g&acirc;y cơn sốt khắp thế giới - những ai chơi được trước ng&agrave;y ph&aacute;t h&agrave;nh hả h&ecirc;, c&ograve;n những người phải chờ đợi do ch&ecirc;nh lệch m&uacute;i giờ ức ph&aacute;t kh&oacute;c.</strong></h3>', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau hơn 7 năm chờ đợi kể từ khi Fallout 3 ra mắt (5 năm nếu t&iacute;nh từ thời điểm của bản mở rộng New Vegas), cuối c&ugrave;ng th&igrave; Fallout 4 cũng đến với game thủ khắp thế giới v&agrave;o ng&agrave;y h&ocirc;m nay 10/11/2015. Vẫn l&agrave; một game nhập vai lấy bối cảnh hậu tận thế quen thuộc, Fallout 4 đưa game thủ v&agrave;o vai một người sống s&oacute;t sau chiến tranh hạt nh&acirc;n, nhưng một điều hết sức bất ngờ l&agrave; khi bạn tỉnh dậy, đ&atilde;&hellip; 200 năm tr&ocirc;i qua kể từ khi cuộc chiến kết th&uacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Fallout 4 - không chơi phí cả một đời!\" src=\"http://media.game8.vn/media2/game8/2015/11/10/fo_1.jpg.600.333.cache\" style=\"height:413px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave; khi người sống s&oacute;t duy nhất của Vault 111 trở lại với mặt đất trong Fallout 4, anh ch&agrave;ng / c&ocirc; n&agrave;ng n&agrave;y đ&atilde; nhận được sự khen ngợi kh&ocirc;ng tiếc lời của cả game thủ v&agrave; c&aacute;c trang tin game danh tiếng. Với những điểm số cao tận m&acirc;y xanh, Fallout 4 đ&atilde; trở th&agrave;nh một trong những tựa game hay nhất năm nay khi c&oacute; điểm số 89 tr&ecirc;n trang tổng hợp đ&aacute;nh gi&aacute; game Metacritic, chỉ thua 4 điểm so với một bom tấn kh&aacute;c l&agrave; The Witcher 3: Wild Hunt ra mắt hồi th&aacute;ng 5 năm nay.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Fallout 4 - không chơi phí cả một đời!\" src=\"http://media.game8.vn/media2/game8/2015/11/10/fo_2.jpg.600.332.cache\" style=\"height:412px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Với game thủ khắp thế giới, Fallout 4 thực sự đ&atilde; vượt qua được Fallout 3 khi n&oacute; đem đến cho người chơi một thế giới mở rộng lớn v&agrave; sinh động, với những nh&acirc;n vật ch&acirc;n thực m&agrave; Bethesda kh&ocirc;ng tiếc c&ocirc;ng x&acirc;y dựng n&ecirc;n một c&acirc;u chuyện ri&ecirc;ng cho mỗi người. Bạn sẽ lu&ocirc;n c&oacute; rất nhiều việc để l&agrave;m trong FO4 bởi hai hệ thống mới l&agrave; chế tạo vũ kh&iacute; v&agrave; x&acirc;y dựng căn cứ được đưa v&agrave;o game, nhưng ngay cả khi hai yếu tố n&agrave;y kh&ocirc;ng hiện diện, thế giới của Fallout 4 vẫn c&oacute; v&ocirc; số điều để thực hiện &ndash; bạn kh&ocirc;ng thể đi từ điểm A đến điểm B của một nhiệm vụ m&agrave; kh&ocirc;ng ph&aacute;t hiện ra &iacute;t nhất&hellip; 3 nhiệm vụ kh&aacute;c để ho&agrave;n th&agrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Fallout 4 - không chơi phí cả một đời!\" src=\"http://media.game8.vn/media2/game8/2015/11/10/fo_4.jpg.600.333.cache\" style=\"height:413px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một đặc trưng kh&aacute;c của c&aacute;c game RPG do Bethesda thực hiện cũng được giữ lại trong Fallout 4: sự mập mờ của những lựa chọn m&agrave; game thủ phải đưa ra trong tr&ograve; chơi. Những lựa chọn của game thủ khi đối mặt với c&aacute;c t&igrave;nh huống kh&oacute; khăn kh&ocirc;ng bao giờ ph&acirc;n định đ&uacute;ng &ndash; sai r&otilde; r&agrave;ng, m&agrave; lu&ocirc;n dẫn đến những c&aacute;i kết bất ngờ v&agrave; hậu quả của ch&uacute;ng đ&ocirc;i khi xuất hiện rất, rất l&acirc;u sau đ&oacute;. Điều n&agrave;y khiến người chơi kh&ocirc;ng bao giờ biết được đ&acirc;u l&agrave; lựa chọn tối ưu, v&agrave; phải đối xử với những nh&acirc;n vật trong game đ&uacute;ng với t&iacute;nh c&aacute;ch của m&igrave;nh, đem lại cho game cảm gi&aacute;c &ldquo;nhập vai&rdquo; cần thiết.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Fallout 4 - không chơi phí cả một đời!\" src=\"http://media.game8.vn/media2/game8/2015/11/10/fo_3.jpg.600.315.cache\" style=\"height:391px; width:745px\" /></span></p>', '<p><strong>HĐH:</strong>&nbsp;Windows 7/8/10 (bắt buộc 64-bit)</p>\r\n\r\n<p><strong>CPU:</strong>&nbsp;Intel Core i5-2300 (2.8 GHz) hoặc AMD Phenom II X4 945 (3.0 GHz) trở l&ecirc;n</p>\r\n\r\n<p><strong>RAM:</strong>&nbsp;8GB</p>\r\n\r\n<p><strong>HDD:</strong>&nbsp;C&ograve;n trống tối thiểu 30GB</p>\r\n\r\n<p><strong>GPU:</strong>&nbsp;nVidia GeForce GTX 550 Ti hoặc ATI Radeon HD 7870 (dung lượng VRAM 2GB trở l&ecirc;n)</p>\r\n\r\n<p><strong>DirectX:</strong>&nbsp;11</p>', '<p><strong>HĐH:</strong>&nbsp;Windows 7/8/10 (bắt buộc 64-bit)</p>\r\n\r\n<p><strong>CPU:</strong>&nbsp;Intel Core i5-2300 (2.8 GHz) hoặc AMD Phenom II X4 945 (3.0 GHz) trở l&ecirc;n</p>\r\n\r\n<p><strong>RAM:</strong>&nbsp;8GB</p>\r\n\r\n<p><strong>HDD:</strong>&nbsp;C&ograve;n trống tối thiểu 30GB</p>\r\n\r\n<p><strong>GPU:</strong>&nbsp;nVidia GeForce GTX 550 Ti hoặc ATI Radeon HD 7870 (dung lượng VRAM 2GB trở l&ecirc;n)</p>\r\n\r\n<p><strong>DirectX:</strong>&nbsp;11</p>', 'Cài đặt và chơi', '15210472491461768167_fallout-4.jpg', 'https://www.youtube.com/embed/GE2BkLqMef4', 2014, '2018-03-14');
INSERT INTO `games` (`id`, `genre_id`, `title`, `url`, `description`, `content`, `minimum`, `recommended`, `guide`, `image`, `trailer`, `year`, `update_at`) VALUES
(17, 7, 'Resident Evil', 'resident-evil', '<h3 style=\"text-align:justify\">Resident Evil 7 l&agrave; một game kinh dị xuất sắc. Bạn sẽ sợ v&atilde;i linh hồn nếu phải chơi một m&igrave;nh, m&agrave; tốt nhất bạn kh&ocirc;ng n&ecirc;n chơi một m&igrave;nh. Resident Evil 7 l&agrave; một trong những game thử th&aacute;ch cả tim cả thận của bạn, n&oacute; khiến con đường đến nh&agrave; vệ sinh ban đ&ecirc;m thử th&aacute;ch hơn bao giờ hết. Nếu Silent Hill 2 khiến bạn sợ ch&iacute;nh con ch&oacute; của m&igrave;nh th&igrave; Resident Evil 7 l&agrave;m bạn phải băn khoăn mỗi khi mở một c&aacute;nh cửa của căn nh&agrave; n&agrave;o đ&oacute;. N&eacute;t kinh dị mờ ảo ở g&oacute;c nh&igrave;n thứ nhất nay đ&atilde; trở lại với sự trợ gi&uacute;p của đồ họa v&agrave; c&ocirc;ng nghệ VR, bạn n&ecirc;n c&acirc;n nhắc vừa chơi vừa pause l&ecirc;n haivl đọc truyện cười để tr&aacute;nh những trường hợp đ&aacute;ng tiếc c&oacute; thể xảy ra.</h3>', '<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://i.ytimg.com/vi/5iszUCmLyW4/maxresdefault.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Resident Evil 7, một bản RE k&igrave; lạ</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể thẳng thắn rằng RE 7 c&oacute; thể được đặt một c&aacute;i t&ecirc;n mới ho&agrave;n to&agrave;n m&agrave; vẫn b&aacute;n chạy như thường. Với những ai l&agrave; fan của d&ograve;ng game Resident Evil sẽ thấy một ch&uacute;t giận dữ với những g&igrave; Capcom l&agrave;m với RE 7. Kh&ocirc;ng c&oacute; sự li&ecirc;n kết đến với c&aacute;c phần trước của RE đ&atilde; đ&agrave;nh (RE 5,6 c&oacute; thể tệ nhưng vẫn l&agrave;m được điều n&agrave;y), Capcom ở đ&acirc;y c&ograve;n thẳng tay vứt đi một đặc sản của d&ograve;ng RE: những con qu&aacute;i vật nh&igrave;n-ph&aacute;t-nhớ-t&ecirc;n. Thay v&agrave;o đ&oacute; RE 7 mang lại một ch&uacute;t yếu tố l&eacute;n l&uacute;t c&ugrave;ng kh&ocirc;ng gian mờ ảo đặc trưng của &nbsp;một tựa game kinh dị t&acirc;m linh : sợ ch&iacute;nh những c&aacute;i tưởng như gần gũi với m&igrave;nh. Bạn nh&igrave;n thấy một con chuột? Okay c&oacute; vẻ kh&ocirc;ng nguy hiểm, bạn tạm cất s&uacute;ng đi v&agrave; r&uacute;t c&aacute;i r&igrave;u bất tử ra. Bạn đi qua con chuột v&agrave; đột nhi&ecirc;n c&oacute; đoạn nhạc r&eacute;o rắt vang l&ecirc;n. Bạn biết c&oacute; biến v&agrave; thầm mong chỉ l&agrave; một con qu&aacute;i vật &ldquo;nhẹ nh&agrave;ng&rdquo;. Bạn liền quay một v&ograve;ng nhưng chẳng thấy g&igrave;. Nh&igrave;n kĩ hơn th&igrave; bạn thấy con chuột l&uacute;c n&atilde;y c&oacute; g&igrave; đ&oacute; &hellip; kh&ocirc;ng b&igrave;nh thường, v&agrave; khi đang &ldquo;ti&ecirc;u h&oacute;a&rdquo; c&aacute;i kh&ocirc;ng-b&igrave;nh-thường ấy th&igrave; tim đ&atilde; kịp ngừng đập mấy nhịp. M&agrave; ngừng đập mấy nhịp l&agrave; may, đ&ocirc;i khi bạn sẽ giật như động kinh nếu chơi RE 7 một m&igrave;nh buổi tối. Nhớ kiểm tra c&aacute;i b&agrave;n để PC xem đ&atilde; vững v&agrave;ng chưa, l&uacute;c co giật người ta đạp ch&acirc;n mạnh lắm!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://static.gamespot.com/uploads/original/1365/13658182/3079148-beginning_hour_012_1465868664.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;i hay của RE 7 ở chỗ, n&oacute; h&ograve;a hợp tất cả yếu tốt kinh dị t&acirc;m linh vốn l&agrave; thế mạnh của game kinh dị phương Đ&ocirc;ng v&agrave; đặt trong c&aacute;i vỏ của d&ograve;ng game Resident Evil 7 lấy bối cảnh phương T&acirc;y. Sự thiếu thốn về trang bị, những c&acirc;u hỏi nhỏ đầy &aacute;m ảnh, kh&ocirc;ng gian th&acirc;n thuộc nhưng ma qu&aacute;i, một cốt truyện chi tiết nhưng &ldquo;k&igrave; k&igrave;&rdquo;&hellip; Tất cả đặt l&ecirc;n trong c&acirc;u chuyện đi t&igrave;m người th&acirc;n của anh ch&agrave;ng nh&acirc;n vật ch&iacute;nh Ethan Winters. Bạn sẽ gi&uacute;p anh ch&agrave;ng n&agrave;y sống s&oacute;t v&agrave; kh&aacute;m ph&aacute; ra những b&iacute; mật ẩn s&acirc;u b&ecirc;n trong. Giờ t&aacute;c giả mới hiểu tại sao c&aacute;c game kinh dị lại hay lấy bối cảnh nh&acirc;n vật ch&iacute;nh đi t&igrave;m người th&acirc;n. Đơn giản bởi v&igrave; những nơi như biệt thự Baker trong RE 7 &nbsp;d&ugrave; c&oacute; mọc th&ecirc;m c&aacute;nh hoặc c&oacute; th&ecirc;m si&ecirc;u năng lực h&oacute;a đi&ecirc;n th&igrave; m&igrave;nh cũng đ** d&aacute;m v&agrave;o. Thế đ** n&agrave;o những gi&acirc;y ph&uacute;t đầu ti&ecirc;n được tự do kh&aacute;m ph&aacute; lại l&agrave; l&uacute;c sợ h&atilde;i nhất. Bạn gấu m&agrave; hỏi kiểu&nbsp;<em>&ldquo;Em m&agrave; bị lạc v&agrave;o đ&acirc;y th&igrave; anh c&oacute; cứu em kh&ocirc;ng&rdquo;</em>&nbsp;th&igrave; m&igrave;nh cũng thật th&agrave; trả lời rằng&nbsp;<em>&ldquo;Để anh từ biệt mọi người rồi v&agrave;o đ&oacute; c&ugrave;ng em&rdquo;</em>. M&aacute;u anh h&ugrave;ng thật kh&ocirc;ng c&oacute; đất sống trong mấy game kinh dị kiểu n&agrave;y, nhất l&agrave; những gi&acirc;y ph&uacute;t đầu ti&ecirc;n của RE 7.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://cdn.gamer-network.net/2017/usgamer/Resident-Evil-7-Shot-05.jpg\" style=\"height:391px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Resident Evil 7, một sự khởi đầu mới</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Capcom đ&atilde; giới thiệu RE 7 với c&aacute;i ti&ecirc;u đề ngắn gọn : mừng trở lại, Resident Evil. Vốn l&agrave; fan của Leon v&agrave; Wesker cũng như kh&ocirc;ng kh&iacute; RE đậm đặc ở mấy bản đầu n&ecirc;n người viết b&agrave;i hơi bất ngờ với phong c&aacute;ch của RE 7. Một c&acirc;u chuyện chi tiết về một gia đ&igrave;nh bệnh hoạn tuy kh&ocirc;ng mới nhưng vẫn đủ t&ograve; m&ograve;. Một ch&uacute;t lắt l&eacute;o trong giải đố, một v&agrave;i c&aacute;i bẫy đ&aacute;ng ngạc nhi&ecirc;n, cũng kh&ocirc;ng mới nhưng cũng l&agrave; th&ecirc;m thắt đ&aacute;ng gi&aacute;. Capcom hẳn đ&atilde; phải đầu tư kĩ c&agrave;ng cho gameplay của RE 7, từ cốt truyện đến c&aacute;c tương t&aacute;c, mọi thứ đều chi tiết v&agrave; gợi mở đến nhau. Nếu c&oacute; điểm trừ g&igrave; cho Capcom ở đ&acirc;y ch&iacute;nh l&agrave; việc bọn qu&aacute;i vật ngo&agrave;i &ldquo;gh&ecirc;&rdquo; th&igrave; hơi hiền, nếu bạn kh&ocirc;ng chết v&igrave; khiếp rồi đơ n&atilde;o để n&oacute; b&ograve; đến ch&acirc;n th&igrave; coi như bạn thắng 90%. Chỉ việc chạy ra xa rồi bắn v&agrave;o đầu n&oacute;, rất đơn giản. Kh&ocirc;ng c&oacute; những con qu&aacute;i vật l&agrave;m bạn phải t&acirc;m tư về c&aacute;ch ti&ecirc;u diệt cũng như nguồn gốc của n&oacute;. C&aacute; nh&acirc;n người viết b&agrave;i đ&aacute;nh gi&aacute; rất cao điểm n&agrave;y, mỗi con qu&aacute;i vật l&agrave; một thứ đ&aacute;ng để t&ograve; m&ograve;. Một thế giới qu&aacute;i vật l&agrave; một thế giới th&uacute; vị! Cớ sao Capcom kh&ocirc;ng đầu tư th&ecirc;m một ch&uacute;t để trải nghiệm của game thủ trở n&ecirc;n to&agrave;n vẹn hơn. Về sau khi đ&atilde; quen với kh&ocirc;ng kh&iacute; của tr&ograve; chơi (hoặc l&agrave; đ&atilde; mua bỉm kĩ c&agrave;ng) th&igrave; bạn sẽ thấy tr&ograve; chơi vơi bớt phần hấp dẫn. C&ugrave;ng với một c&aacute;i kết kh&aacute; ngắn th&igrave; đ&acirc;y coi như một ch&uacute;t điểm trừ tiếc nuối cho RE 7.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://gpstatic.com/acache/30/58/1/uk/s3-4b69cc6b4fa4e956209df11571132c6b.jpg\" style=\"height:419px; width:745px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Tạm kết</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn mới bắt đầu với d&ograve;ng game kinh dị t&acirc;m linh g&oacute;c nh&igrave;n thứ nhất th&igrave; h&atilde;y mua ngay RE 7. Nếu bạn l&agrave; d&acirc;n hardcore hoặc đơn giản đ&atilde; gắn b&oacute; với Umbrella Corp v&agrave; những con qu&aacute;i vật đ&aacute;ng nhớ của RE 2 v&agrave; RE 4 th&igrave; n&ecirc;n chờ đến dịp giảm gi&aacute;. Một lời khuy&ecirc;n ch&acirc;n th&agrave;nh kh&aacute;c l&agrave; kh&ocirc;ng chơi game kinh dị t&acirc;m linh một m&igrave;nh, nếu c&oacute; chơi th&igrave; cũng n&ecirc;n chơi c&ugrave;ng hoặc t&acirc;m sự với ai đ&oacute; về những thứ đ&atilde; xảy ra trong game. &Aacute;m ảnh l&agrave; kh&ocirc;ng tốt, nỗi sợ kh&ocirc;ng phải l&agrave; thứ d&ugrave;ng được cả đời.</span></p>', '<p>OS:&nbsp;WINDOWS 7,8,8.1,10 64-BIT</p>\r\n\r\n<p>Processor:&nbsp;Intel Core i5-4460, 2.70GHz or AMD FX-6300 or better</p>\r\n\r\n<p>Memory:&nbsp;8 GB RAM</p>\r\n\r\n<p>Graphics:&nbsp;NVIDIA GeForce GTX 760 or AMD Radeon R7 260x</p>\r\n\r\n<p>DirectX:&nbsp;Version 11</p>', '<p>OS:&nbsp;WINDOWS 7,8,8.1,10 64-BIT</p>\r\n\r\n<p>Processor:&nbsp;Intel Core i5-4460, 2.70GHz or AMD FX-6300 or better</p>\r\n\r\n<p>Memory:&nbsp;8 GB RAM</p>\r\n\r\n<p>Graphics:&nbsp;NVIDIA GeForce GTX 760 or AMD Radeon R7 260x</p>\r\n\r\n<p>DirectX:&nbsp;Version 11</p>', 'Cài đặt và chơi', '1521047706maxresdefault.jpg', 'https://www.youtube.com/embed/W1OUs3HwIuo', 2017, '2018-03-14');

